html body [class*="menu"]:NOT(SELECT),
html body [class*="tooltip"],
html body [class*="popup"], 
html body [id*="popup"],

/* Notes, details, etc.  Maybe useful */

html body [class*="note"],
html body [class*="detail"],
html body [class*="description"]

{background-color: #232323 !important}

/* Also common */
[class*="content"],
[class*="container"] {
background-color: #1c1c1c !important;
}

/* Headers, Logos */

[id*="masthead"] a,
[id*="header"] a,
[id*="logo"] a,
[class*="logo"] a {
text-indent: 0 !important;
}

/* Instead of increasing specificity rating by using :not, set rules separately */

html:root body [class*="layer"],
html:root body #lightbox-nav,
html:root body #imageContainer {
background-color: transparent !important;
}

/*::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::*/

/* Universal - sets color of text, border */

* {
color: #CCC !important; 
border-color: #444 !important;
outline-color: #444 !important;
text-shadow: none !important;
}

html *:after,
html *:before {
color: #CCC !important;
border-color: #666 !important;
background: none transparent !important;
}


/* Link */

a:link,
a:link *:not(img){ 

color: #B1CBF7 !important;
background-color: transparent !important;
border-color: #666 !important;}

a:hover,
a:hover *:not(img){

background-color: #363037 !important;
border-color: #999 !important;
}

html [href*="#"]:hover {
background-color: transparent !important;
}


/* Event Handlers/Attributes */

[onclick],
[ondblclick],
[onmousedown] {
text-indent: 0 !important;
}

/* Make images transparent */

img {
opacity: .75 !important;
}

img:hover {
opacity: 1 !important;
background-color: #888 !important;
}

svg {
background: none #666 !important;
}

/* Highlight */
::-moz-selection {
background-color: #626F61 !important;
color: #F6F7B9 !important;
}

/* :::::::: Specific Fixes :::::::: */

/* google search link fix */
.g .r {
background-color: transparent !important;
}

/* google result hover highlight*/
div.vsc:hover > .vspi, div.vso > .vspi {
background: none transparent !important;
border: none !important;
}

/* Blbe biele ciarky */
.windowbg2 span.topslice span, .windowbg span.topslice span, .windowbg2 span.botslice span, .windowbg span.botslice span {
  background: unset;
}


li.replies > img {
  height: 20px !important;;
  width: 20px !important;;
}

li.views > img {
  height: 20px !important;;
  width: 20px !important;;
}

li[class="replies"] {
  font-size: 10px !important;;
}

li[class="views"] {
  font-size: 10px !important;
}

div.display-info > h1 {
  font-size: 20px !important;
}

div.body_message--ad {
  display: none !important;
}

blockquote {
  font-size: 11px !important;
}

div[class*="inner"] {
  font-size: 12.5px !important;
}

div[class*="body_message"] {
  padding-top: 5px !important;
  padding-bottom: 5px !important;
}

div.smalltext.reportlinks {
  font-size: 9px !important;
}

div.body_content {
  margin-left: unset !important;
}

span.arrow-left {
  display: none !important;
}
  
