css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("chomikuj.pl"), url-prefix("http://opis-chomikuj.pl") {

body {
    background: #283243;
    color: #e6e6e6;
}
#mainContainer {
    background: #283243 !important;
    background-attachment: fixed !important;
    background-size: 100% 100% !important;
    margin-left: 0px;
    margin-right: 0px;
    
    
}

#mainPage{padding-top: 55px;}
#mainContainer{padding-top: 55px;}

#content {
    background-color: #1d2430;
    height: auto;
    padding: 15px 30px 10px;
    border-radius: 3px;
    box-shadow: 0px 0px 0px 0px #899926;
    border: 0px solid #889925;
    min-height: 250px;
    position: relative;
}

.sliderBg, .sliderBg:before, .sliderBg:after{
    background: #1d2430 ;   
}


/* ------------Górny panel------------ */

#topbar {
    background: #141C27 !important;
    border-bottom: 2px solid #3498DB;
    width: 100%;
    height: 47px;
    padding-top: 6px;
    position: fixed;
    z-index: 100;
    
}
ul.topbarList a,
ul.topbarList input[type="image"] {
    background: #3498DB none repeat scroll 0% 0%;
    border: none #2C8484;
    color: #FFF;
    font-size: 14px;
    text-decoration: none;
    display: block;
    height: 31px;
    line-height: 31px;
    border-radius: 3px !important;
    box-shadow: none !important;
}

ul.topbarList a:hover,ul.topbarList input[type="image"]:hover{background: #175782 }

#topbarMessage {background: #3498DB url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 2px 7px}
#topbarTransfer {background: #3498DB url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 5px -28px}
#topbarPoints {background: #3498DB url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 8px -66px}
#topbarOptions {background: #3498DB url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 8px -101px}

#topbarMessage:hover {background: #175782 url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 2px 7px}
#topbarTransfer:hover {background: #175782 url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 5px -28px}
#topbarPoints:hover {background: #175782 url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 8px -66px}
#topbarOptions:hover {background: #175782 url("http://x2.static-chomikuj.pl/res/46bceb8cff.png") no-repeat scroll 8px -101px}


/* ------------Buttony----------- */

a.button,.sendButton, .greenButtonCSS, .greenButtonCSS.disabled, .captcha-container .captcha-button, .fileActionsButtons .greenButtonCSS, .disabled .greenButtonCSS:hover
{
    border: 1px solid #175782!important;
    border-radius: 5px;
    background: #2691d9;
    box-shadow: none;
    color: #e6e6e6;
}


a.button:hover,.sendButton:hover, .greenButtonCSS:hover, .greenButtonCSS.disabled:hover, .captcha-container .captcha-button:hover, .fileActionsButtons .greenButtonCSS:hover, .disabled .greenButtonCSS:hover
{
    border: 1px solid #3498db !important;
    border-radius: 5px;
    background: #2691d9;
    box-shadow: none;
    color: #e6e6e6;
    
}

a.button:active,.sendButton:active, .greenButtonCSS:active, .greenButtonCSS.disabled:active, .captcha-container .captcha-button:active, .fileActionsButtons .greenButtonCSS:active, .disabled .greenButtonCSS:active
{
	transform: translate(0px,2px);
  -webkit-transform: translate(0px,2px);
	
}




.greenButton, ul.borderRadius, #sortTab{
    background: #2383c3;
    box-shadow: none;
    border: 0px solid;
    color: #e6e6e6;
}

a.selected, li.selected {
    font-weight: bold;
    color: #fff;
    background-color: #263040;
}




.button, .fileActionsButtons a, .galeryActionButtons a, .scFileActionButtons li
{
	border: 1px solid #263040;
    box-shadow: none;
    background: #2691d9 !important;
    background-image: none !important;
}

/* ------------Boxy----------- */

.frameHeader {
    height: 30px;
    line-height: 30px;
    text-align: left;
    color: #FFF;
    font-size: 18px;
    font-weight: bold;
    background: #3498DB;
    border: none;
    position: absolute;
    left: -1px;
    top: -1px;
    padding-left: 5px;
    right: 0px;
    overflow: hidden;
}

.frame {
    background: #1d2430;
    border: 1px solid #13496c;
    box-shadow: none;
    position: relative;
    padding: 40px 10px 15px;
    margin-bottom: 20px;
    font-size: 1rem;
}

.ChomikTable {
border: none;
}

.ChomikTable th,.ChomikTable-light th{
    background-image: none;
    background-color:#1d2430;
    border: none;
}

.aR TD, .ChomikTable-light .aR td{
    background-image: none;
    background-color:#1d2a34 !important;
    border: none;
}

.R TD, .Rs TD, .F TD,.aF TD, .aR td{
    border: none;
}

.standardTable td {
    border-bottom: 1px solid #13496c;
    border-left: none;
    border-right: none;
    padding: 0 5px;
}

.standardTable th{
    border-bottom: 1px solid #13496c !important;
    border-left: none !important;
    border-right: none !important;
    border-top: none !important;
    background: none;
}

.adminMessageInfo,.spamMessageInfo {
	background-color:#263040;
    border:1px solid #1e74ae;
    
}

.fileInfoSmallFrame, .fileInfoFrame, .greenButtonCSS{ 
    background-color: #263040 !important;
    border: 0px;
}

.borderRadius,
.borderTopRadius {
    border-radius: 3px;
}
.alt {
    background-color: #1d2a34 !important;
}
.filerow,.messageRow  {
    clear: both;
    border-bottom: 1px solid #13496c;
    padding: 10px 5px;
    margin-top: 1px;
    overflow: hidden;
}
.highslide {
    color: #fff
}

.borderBottom{border-bottom: 1px solid #13496c;}

#folderOptions{
    background: #263040 !important;
    border: 1px solid #13496c;   
}
.borderPointer{
	background: #263040 !important;
    border-left: 1px solid #13496c;
    border-top: 1px solid #13496c;
}


/* ------------Okienka----------- */

.dialogWindow, .DW {
    background: #3498DB!important;
    border: 1px solid #13496c!important;
    box-shadow: none!important;
    -moz-box-shadow: none!important;
    -webkit-box-shadow: none!important;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px
}

.dialogWindow .ui-tooltip-content, .DWcontent {
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    background-color: #283243;
    margin: 0 3px 3px;
    border: 1px solid #13496c;
    padding: 15px;
    
}

#PointsDetailsWindowContent tr:nth-child(odd) {
    background-color: #1d2a34 
}

.dialogWindow .ui-state-default .ui-icon {
    background-image: url("http://x3.static-chomikuj.pl/res/81d695cc0f.jpg");
    background-position: left top!important;
    border: 1px solid #829923!important;
    box-shadow: none;
    display: block;
    width: 24px;
    height: 24px;
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    margin-top: -4px
}

.DW {
    padding: 2px 2px 2px 2px;
    background-color: #fff;
    border: 1px solid #8CA824;
    border-bottom-color: #5F7A1D;
    border-left-color: #719122;
    box-shadow: #898989 0 0 5px;
    -moz-box-shadow: #898989 0 0 5px;
    -webkit-box-shadow: #898989 0 0 5px;
    border-radius: 7px;
    -moz-border-radius: 7px;
    -webkit-border-radius: 7px;
}




/* ------------Input------------ */


#quickSearch .searchForm {
    background-color: #fff;   
    border: 0px solid #7b9220;   
    border-radius: 5px;
    box-shadow: none;  
}


/* ------------Loga----------- */




/* ------------Czcionka----------- */


.T_table a,
#foldersList a {
    color: #F1F3F6;
}
.T_table a:hover .hidden {
    color: #F1F3F6
} 

h1, h2, h3, .marked, a , .noFile, .subpageHeaderText{ color: #2383c3 }



.sliderContent, .footer {
color: #F1F3F6 ;
}

/* ------------Scroll----------- */

body::-webkit-scrollbar {
    width: 1em;
}
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
} 
body::-webkit-scrollbar-thumb {
  background: #3498DB;
  outline: 1px solid slategrey;
}




/* ------------Fixy----------- */

#folderOptionsTitle {
    background: #3498DB
}

#registrationForm, .captcha-container{
    background-color: inherit;
    border: none;
}

#topbarLoginForm input[type="text"], #topbarLoginForm input[type="password"], #registrationForm input[type="text"], #registrationForm input[type="password"], #accountBlocked input[type="text"], #accountBlocked input[type="password"], #blockedChomikLoginForm input[type="text"], #blockedChomikLoginForm input[type="password"], .loginWindowFrame input[type="text"], .loginWindowFrame input[type="password"], .registrationCaptcha #recaptcha_response_field, .loginWindowFrame textarea{
    border: none !important;
    background-color: #fff;
}

#selectedFilesActionButtons{
    background-color: inherit;
    border-bottom: none;
}

input:-webkit-autofill{background-color:#fff}

.footerLinks a, .footerLinks a:visited{color: #1f74ad}

.tabs a{background: #3498DB; border: none;}
.tabs .selected {background: #67b2e4; border: none; color: #fff}
.tabs a:hover, .tabs .selected:hover {background: #175782}


.thumbnail a{border:none}
span.T_selected{background: #175782; border:none;}

#favoriteTab li.active a, #favoriteTab li.FavTab a:hover,#favoriteTab li.FavTab a, #favoriteTab .edit{ background:#263040; border: none;}

.standardTable th {
    border: 1px solid #d2cbab;
}


.sub_slider .ui-slider-handle{
		background: #3498DB;
        border: 1px solid #263040;
}

.readMessageHeader, .reply div, #SelectedMessageContainer .replyForm {
    background-color: #1d2a34;
    border-top: 1px solid #13496c;
    border-bottom: 1px solid #13496c;
    padding: 5px;
}


.sub_frame_message {
    background: #1d2a34;
    padding: 10px;
    font-size: 13px;
    border: 1px solid #13496c;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    
}


.thumbnailexpander .socialPanel {
    background-color: #1d2a34;
    border-top: 1px solid #13496c;
    border-bottom: 1px solid #13496c;
    padding: 10px;
    clear: both;
    margin-top: 10px;
    height: 22px;
}


/* ------------Wywalone----------- */

div [id="aPlaceHolder"], .publishmentRightAd, .NewPointsTransferLink {
    display: none !important
}


}
