@-moz-document domain("www.messenger.com") {
/**
 * IRC Style for messenger.com
 * @description Makes messenger.com feel more like an IRC client
 * @version     1.0.4
 * @url         https://github.com/PeteDevoy/irc-style-for-messenger-com
 */

h1, a, span, p, strong, div, input {
    font-family: 'Lucida Console', 'Courier New', 'Consolas' !important;
}

/*hide chat bubble background*/
.__i_ div, ._nd_ ._hh7._6ybn {
    background-color: #fff ;
    color: #000 !important;
    font-family: 'Lucida Console', 'Courier New', 'Consolas';
}

/*prevents chat bubble color override from hiding images*/
#js_1 > div > div > div > div.clearfix._o46._3erg div {
    background-color: transparent;    
}

/*left align sender text*/
._o46._3i_m ._3058 {
    clear: both;
    float: left !important;
}

/*make messages fill width*/
._52mr,
._52mr._s1- {
    max-width: 100% !important;
}

/*make display picture smaller*/
._4ld- {
    width: 16px !important;
    height: 16px !important;
    margin: 8px;
}

/*show names*/
._-ne {
    height: auto;
    opacity: 1;
}
h5._ih3 {
    margin: 0;
    padding: 0;
    height: auto !important;
    width: auto !important;
    position: relative !important;
}
a {
    color: black !important;
}

/*hide pictures and conversation previews in sidebar*/
._4ldz,
._1htf,
._5bli._2_a2.img {
