}

/*remove 32px width*/
._fl2 li a {
    width: auto;
    height: auto;
}

/*inject text replacements*/
._fl2 li:nth-child(0n+2) a::after {
    content: "[voice]";
}
._fl2 li:nth-child(0n+3) a::after {
    content: "[video]";
}
._fl2 li:nth-child(0n+4) a::after {
