css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("head-fi.org") {

   /*-----Format searchbar-----*/ 

     .search-bar-outer {
  position: absolute !important;
  top: 17px !important;
  height: 0px !important;
  border: transparent !important;
  display: inline-block !important;
  width:212px !important;
  right: 0px !important;
  left: 810px !important
}
    
.fixed-scroll-breakpoint .search-bar-outer {
  top: -2px !important;
}
    
.ui-header-fixed #advanced-search-submit, .ui-header-fixed #advanced-search-submit:hover {
  top:40px !important;
  color:#FFFFFF !important;
}
    
.ui-header-fixed ul#main-nav .search > a {
  display: none !important;
} 
    
input#search {
  margin-top: 15px !important;
  width:200px !important;
  height: 22px !important;
  font-size: 11px !important;
  text-align: left !important;
}


#search-form .btn {
  width:47px !important;
  margin-top: 16px !important;
  height: 20px !important;
  left:163px !important;
  text-indent: -6px !important;
  background-color: #6E7393 !important;
  line-height: 11px !important;
  text-align: center !important;
}
    
#search-form .btn:focus::-moz-focus-inner {
	border: 0px transparent !important;
}
    
#search-form .btn:hover {
  background-color: #5b6985 !important;
}

#search-bar {
  position: absolute !important;
  display: inline-block !important;
  height: 40px !important;
  }

.advanced-search-link {
  line-height: 8px;
}
    /*-----CODE TO FORMAT AND STYLE THE NAVBAR LINKS TO "Private Messages" AND "Subscriptions".-----*/   
    .jlink {

   font-size: 12px;
   display: block !important;
   height: 18px !important;
   color: #FFFFFF !important;
   text-align: left;

}

.jncontainer {
    position: absolute;
    right: 48px;
    display: inline-block;
    top: 27px;
    width: 124px;    

}

.jcounter {
    background-color: #B84300;
    color: #FFFFFF;
    font-weight: bold;
    display: inline-block;
    height: 15px;
    min-width: 18px;
    text-align: center;
    font-size: 10px;
    line-height: 14px;
    border-radius: 2px;
    margin-right: 5px;
    font-family: Verdana;
}

.jpadder {
    display: inline-block;
    height: 15px;
    min-width: 18px;
    text-align: center;
    font-size: 10px;
    line-height: 14px;
    border-radius: 2px;
    margin-right: 5px;
    background-color: transparent;
    font-family: Verdana;
}

.jpadder,
.jcounter {
    float: right !important;
    margin-top: 0px;
    margin-left: 3px;
    text-indent: -1px;
}

.fixed-scroll-breakpoint .jncontainer {
    top: 8px;
} 
.ui-header-fixed li.profile .user-avatar .notification-counter,
.ui-header-fixed ul#main-nav li.messages,
.ui-header-fixed ul#main-nav li.subscriptions {
    display:none;
}

.search-bar-outer, .ui-header-fixed ul#main-nav .search > a {
  right:165px !important;

}

    
    
    
    
     /*-----Prevent the Profile menu from disappearing immediately when cursor moves out-----*/
.fixed-scroll-breakpoint .ui-header-fixed ul.menu {
    top: 51px!important;
}  

.ui-header-fixed li.profile .user-avatar{
display: block;
opacity:1;
transition:0s 0.55s;

}
.ui-header-fixed li.profile .user-avatar:hover{
opacity:1;
transition:0s;
visibility: visible;
} 
    
    
.ui-header-fixed li.profile ul.menu{
display: block;
opacity:0;
transition:0.2s 0.55s;
pointer-events: visiblefill;
visibility: hidden; 
top: 100%;
}
.ui-header-fixed li.profile:hover ul.menu{
opacity:1;
transition:0s;
visibility: visible;
}
    
.ui-header-fixed {
    background: rgba(91, 105, 133, .99803915619850145999)!important;
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
}

html {
    background-image: none !important;
}
    
    .section:target {
    padding-top: 40px;
}


    /*-----THE COLOR SURROUNDING THE Headfi LOGO ON THE NAVBAR-----*/

.ui-header-fixed ul#main-nav .mainNav-logoLink,
.ui-header-fixed ul#main-nav .mainNav-logoLink:focus,
.ui-header-fixed ul#main-nav .mainNav-logoLink:hover {
    background-color: #5b6985 !important;

}
    
.ui-header-fixed ul#main-nav .mainNav-rootLink,
.ui-header-fixed ul#main-nav .mainNav-rootLink :focus,
.ui-header-fixed ul#main-nav .mainNav-rootLink :hover {
    background-color: #5b6985 !important;

}
    
   /*-----HOVER COLOR FOR NAVBAR BUTTONS-----*/

.ui-header-fixed ul#main-nav li.forums a.form-select-link-expanded,
.ui-header-fixed ul#main-nav li.forums a:hover,
.ui-header-fixed ul#main-nav .mainNav-rootLink:hover,
.ui-header-fixed ul#main-nav.forums-tab li.forums a:hover,
.ui-header-fixed li.profile:hover .user-avatar,
.ui-header-fixed ul#main-nav .search:hover>a::before {
background-color: #515e77 !important;

}

/*-----PREVENT MAIN NAVBAR LINKS FROM MOVING UP WHEN YOU SCROLL DOWN.-----*/

.fixed-scroll-breakpoint .ui-header-fixed ul#main-nav .mainNav-rootLink {
line-height: 60px !important;
}

.fixed-scroll-breakpoint .ui-header-fixed ul#main-nav li.forums a {
background-position: 75px 25px !important;
}

.fixed-scroll-breakpoint .ui-header-fixed ul#main-nav li.forums a.form-select-link-expanded,
.fixed-scroll-breakpoint .ui-header-fixed ul#main-nav li.forums a.form-select-link-expanded a:hover,
.fixed-scroll-breakpoint .ui-header-fixed ul#main-nav li.forums a:hover {
background-position: 75px 24px !important;
}


   /*-----THE COLOR OF THE TOP OF THE FORUMS MENU (the background color for "New Posts", "Unanswered" and "I've Posted")-----*/
#forum-select-menu .forum-select-options {
    background: #5b6985 !important;

    

}

    

   /*-----THE TINY VERTICAL BORDER YOU SEE ON MOUSE HOVER THAT'S LOCATED ON THE LEFT SIDE OF EACH MENU ITEM IN THE FORUMS MENU-----*/
#forum-select-menu ul li.current-forum ul ul li.current-forum a, #forum-select-menu ul li.current-forum a {
    border-left-color: #5b6985;

}


/*-----THIS CODE PREVENTS THE PROFILE MENU BUTTON FROM MOVING UP A FEW PIXELS WHEN YOU SCROLL DOWN (WHEN THE NAVBAR TRANSITIONS INTO THE STICKY NAVBAR)-----*/

.fixed-scroll-breakpoint .ui-header-fixed ul#main-nav li.profile .user-avatar {
    margin-top: -2px !important;

}

   /*-----HIDES THE NAVBAR GUEST ICON WHEN YOU'RE NOT LOGGED IN BECAUSE IT'S NOT NEEDED WITH One-Click Log-In-----*/    
.ui-header-fixed ul#main-nav li.profile > a {
    display: none
}

   /*-----PUTS THE DIVIDERS BACK IN THE TOP OF THE FORUMS MENU THAT GO BETWEEN "New Posts", "Unanswered", AND "I've Posted"-----*/
#forum-select-menu .divider {

    margin-left: 5px;
    display: -webkit-inline-box;
    display: -moz-box;

}

.divider {

    margin: -4px;

}




   /*-----COLOR OF THE TOP OF THE PROFILE MENU-----*/

.ui-header-fixed ul.menu li:first-child {

    background-color: #5b6985;

}

   /*-----BETTER VERTICAL ALIGNMENT OF YOUR USERNAME ON YOUR PROFILE MENU-----*/

.ui-header-fixed ul.menu .username a,
.ui-header-fixed ul.menu .username a:hover {
    line-height: 15px;

}

#quick-reply .form_actions .btn {

    line-height: 12px !important;

}

   /*-----SEARCH BUTTON LOCATION AND MENU DIRECTION-----*/

.ui-header-fixed ul#main-nav .search > a:before {
    margin-left: 0px;

}  

.search-bar-outer, .ui-header-fixed ul#main-nav .search > a {
    left: 679px !important;
    width: 52px;

}

   /*-----PAGINATION BUTTON COLORS AND HOVER COLOR-----*/

.pn li a:hover,
.pn .curr {

    background-color: #6E7E93 !important;

    border: 1px solid #6E7E93 !important;

}

.pn li a:focus,
.pn .curr {
    background-color: #46566B !important;
    border: 1px solid #46566B !important;
    color: #FFFFFF
}

    
   /*-----PAGINATION BUTTON BORDER COLOR AND TEXT COLOR-----*/

.pn li a {
    border: 1px solid #46566B !important; 
    color: #46566B;
    background-color: transparent;

}

       /*-----THIS REMOVES UNNECESSARY FOCUS BORDERS ON TEXT LINKS-----*/

a:focus {
    text-decoration: none;

}

   /*-----FIXES DISAPPEARING TEXT LINK UNDERLINING WHEN YOU CLICK THE LINK-----*/

a:hover {
    text-decoration: underline;

}

.btn-cont a:hover,
body a.priact:hover,
body .form_actions .accented_primary_cta:hover,
body .form_actions .primary_cta:first-child:hover,
.form_actions .buttons-right .btn:hover,
.forum-nav-control .btn:hover,
#quick-reply-editor .reply-btn:hover,
.advanced-search .btn:hover,
#search-form .btn:hover,
#pm-container .btn:hover,
#edit-acct-cont .btn:hover,
.yui3-panel .yui3-widget-buttons .yui3-button:hover,
.yui3-panel .yui3-widget-buttons .yui3-button.button-submit:hover,
.component .btn:hover {
    background-color: #6E7E93 !important;
    border-color: #6E7E93 !important;

}

   /*----HORIZONTALLY-CENTERED TEXT ON THE MULTI BUTTON----*/
.post-multiquote {
    text-indent: -4px !important;

}

    
   /*----CLASSIC LOG-IN BUTTON FOR THE ONE-CLICK LOG-IN-----*/

button[name="Login"] {

    background: transparent url('data:image/png;base64,');

    color: transparent;
    border: none;
    width: 25px;
    height: 26px;
    padding-top: 10px;
    background-position: 0 0;
    cursor:pointer;

}  

button[name="Login"]:hover {
    background-position: 25px 0;

}

button[name="Login"]:focus::-moz-focus-inner {
    border: 0px transparent !important;

}



ul#main-nav li a {
  height: 10px;

}
div:target {
  padding-top: 60px; 
  margin-top: -55px;
}

}
