css:
@-moz-document domain("bunker.lunavod.ru") {
body {
    color: #0B0000;
    background-color: #FFF290;
    background:url(http://static.lunavod.ru/img/05446cc348fc70cfc5ba6cab5f38fd8b34cca56a.png)
}

h1 {
    padding: 0.5em;
}
.topic, .comment, .block, .block-content, .blog .blog-header, .blog-more, .blog .blog-content, .blog .blog-footer {
    color: #0B0000;
    background-color: rgba(231, 215, 255, 0.9);
}
a {
color: #241400
}
#wrapper, #footer {
    background:
                                            url(http://static.lunavod.ru/img/f8e7d0ad03b5232487a910309962f84e40c25a98.png)
}
.nav.nav-main, .nav.nav-userbar {
background: #763F71
}
userbar li a {
   color: #DC00C9;
}
.spoiler-gray {
    background: #0F001C;
    color: #0F001C;
}
.vote .vote-up,.vote.vote-count-positive .vote-count, .favourite-icon, div.block.Topicinfo div.MoreInfo div.OneDescription p b.r, .profile .vote .vote-up, .profile .vote.vote-count-positive .vote-count, .blog .blog-header .vote.vote-count-positive .vote-count{
    color: #FF7800;
}
.vote .vote-down,.vote.vote-count-negative .vote-count, .profile .vote .vote-down, .blog .blog-header .vote.vote-count-negative .vote-count, .vote .vote-down, .vote.vote-count-negative .vote-count, .profile .vote .vote-down, .profile .vote.vote-count-negative .vote-count, .blog .blog-header .vote.vote-count-negative .vote-count {
    color: #4A0072;
}
.comment.comment-self, .button.button-primary.active {
    background: rgba(202, 253, 127, 0.9);
}
.nav.nav-main li.active a {
    background: #916099;
    color: white;
}
.nav.nav-userbar li a {
    color: #FFFFFF;
}
.toolbar section {
    background: #481544;
}

.block .block-header {
    padding: 13px 20px;
    background: #763F71;
    border-top: 1px solid #FFB700;
    position: relative;
}
.nav.nav-pills li.active a {
    background: #763F71;
    color: #fff;
}
.profile-info-about {
    background: rgba(255, 255, 255, 0.5);
}
.infobox {
    background: #763F71;
}
.toolbar section a:hover, .nav.nav-userbar li a:hover, .nav.nav-main li a:hover, .nav.nav-main li:last-child a:hover {
    background: #D9ADDE;
}
.toolbar section a {
    background: #763F71;
}
a:hover {
    color: #FFCC41;
}
.text a:visited, .topic .topic-header .topic-title a:visited, .item-list li a:visited {
    color: #80005D;
}
.topic .topic-footer .topic-info {
    overflow: hidden;
    background: #F7F7F7;
    border-radius: 3px;
}
.block .block-header h3 a, .block .block-header h3 {
    color: #D6ACE0;
}
.block.block-type-profile .status.status-online{
    background: #FF7800;
}
.block.block-type-profile .status.status-offline {
    background: #4A0072;
}
.button.button-primary, .comment.comment-current {
    background: rgba(206, 129, 255, 0.9);
}
#content {
    background: rgba(255, 255, 255, 0);
}
.comment {
    border: 1px solid #DCBDD9;
}
.toolbar .toolbar-update .new-comments {
    border-top: 1px solid #241400;
    padding: 10px 0;
    color: #000000;
    text-align: center;
    font-size: 11px;
}
.spoiler-title {
    border: 1px solid #9E00A4;
    background-color: #FFFFFF;
    color: #000000;
}
.topic {
    border: 1px solid #9E00A4;}
.button {
    border: 1px solid #9E00A4;
    color: #000000;
}
.text .ls-user:hover {
    color: #FFCC41;
}
.text .ls-user {
    color: #000000;
}
.cut a {
    background-color: #CCBFE7;
}
.item-list li .author {
    color: #550045;
}
.actions li a {
    color: #80005D;
}
.nav.nav-pills li a {
    background: #DDB5E8;
    color: #3D024E;
}
.nav.nav-pills li a:hover {
    background: #9251A4;
    color: #FFCC41;
}
.blog-more:hover {
    background: #9251A4;
}
.blog .blog-header {
    border-bottom: 1px solid #80005D;
}
.user, .ls-user {
    color: #80005D;
    font-weight: bold;
}
.input-text {
    background: #FFFFFF;
    border: 1px solid #80005D;
    color: #000000;
}
h2.page-header {
    color: #763F71;
}
.table td, .table th {
    border-bottom: 1px solid #763F71;
}
#footer {
    color: #763F71;
}
.table th {
    background: #DDB5E8;
}
.pagination ul li span, .pagination ul li a {
    color: rgb(0, 0, 0);
    background: #DDB5E8;
}
.pagination ul li.active span {
    color: #FFB700;
    background: #763F71;
}
.pagination ul li a:hover {
    background: #9251A4;
}
.button:hover {
    color: #FFCC41;
    background: #9251A4;
}
.button {
    color: #000000;
    background: #DDB5E8;
}
.comment.comment-new {
    background: rgba(255, 226, 141, 0.9);
}
.comment .comment-info li.comment-date a {
    color: #2E2E2E;
    border-color: #2E2E2E;
}
h2.header-table {
    border-bottom: 1px solid #000;
}
.nav.nav-main li.quote a:hover {
    background: #DDB5E8;
    cursor: default;
}
.nav.nav-userbar li a.widemode:hover {
    background: #DDB5E8 url(http://bunker.lunavod.ru/templates/skin/reboot/images/wide_mode.png);
}
}
