@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bubb.la") {
  body.not-logged-in, .not-logged-in header.navbar, .not-logged-in header.navbar a.active, body, .region-navigation, first leaf active, .nav.navbar-nav .first.leaf a 
 {
    background-color: #151515 !important;
    color: #773377 !important;
  }
  
  a:focus, .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus  {
    outline: none;
    outline-offset: -0px;
    text-decoration: none;
  }
  
  a:hover, a:focus, .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus  {
    color: #BBB;
    text-decoration: none;
  }
  
  a {
    color: #777;
  }
  
  .odd, .even {
    background-color: #222 !important;
  }
  
  h1 a{
   color: #773377 !important;
  }
  
  h1 a:hover{
   color: #BBB !important;
  }

  label, h2 a{
   color: #AAA !important;
  }
  
  h2 a:hover{
   color: #BBB !important;
  }
  
  
  body, body h1, body h2, body h3 {
    font-family: "Liberation Sans" !important;
    font-style: normal;
    font-weight: bold;
  }

.views-field-value, .views-field-value-1 {
    float: left;
    width: 6em;
  background-color: #222 !important;
  }
  
  .form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857;
    color: #BBB;
    vertical-align: middle;
    background-color: #333;
    background-image: none;
    border: 1px solid #111;
    border-radius: 4px;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
    transition: border-color 0.15s ease-in-out 0s, box-shadow 0.15s ease-in-out 0s;
  }
  
  .form-control:focus {
    border-color: #773377;
    outline: 0px none;
    box-shadow: 0px 1px 1px rgba(0, .5, .1, .5) inset, 0px 0px 8px rgba(111, 33, 111, 0.6);
  }
  
  .btn:focus {
    outline: none;
    outline-offset: -0px;
  }
  
    .btn-info, .btn-primary{
    color: #BBB;
    background-color: #333;
    border-color: #111;
  }
  
  
  .btn-primary:hover, .btn-primary:focus, .btn-primary:active, .btn-primary.active, .open .dropdown-toggle.btn-primary, .btn-info:hover, .btn-info:focus, .btn-info:active, .btn-info.active, .open .dropdown-toggle.btn-info{
    color: #BBB;
    background-color: #444;
    border-color: #111;
  }
  
  
}
