css:
/* Chat bubbles */

#messages .me .text {
	border-radius: 10px;
	border: 1px solid #000;
	background-image: none;
	background-color: #0066cc;
	color: #eee;
}
#messages .text {
	border-radius: 10px;
	border: 1px solid #000;
	background-color: #4D6A88;
	color: #eee;
}
#messages .media img {
	border-radius: 10px;
	border: 1px solid #000;
}
/* ------------ */

body {
	background-color: #444;
	border-color: #000;
}
#conversations {
	background-color: #535365;
	border-color: #000;
}
#strip,
#compose,
.modal,
#compose #emoji-pane {
	background-color: #333;
	border-color: #000;
}
#partners,
#status p,
#settings-pane label,
.modal .link,
#settings-pane ul li,
.modal h3 {
	color: #eee;
}
#compose .tools .button,
.modal .link {
	background-color: #444;
	margin-right: 3px;
}
#compose .tools .tool:hover {
	background-color: #555;
	margin-right: 3px;
}
#compose .tools .imessage:hover,
#settings-pane ul li:hover,
#conversations span:hover {
	background-color: #555;
}
#settings-pane ul li.selected,
#conversations span.selected,
#conversations span.selected:hover {
	background-color: #555;
}
#conversations span,
#compose #editor,
#settings-pane .groups,
#settings-pane ul li,
#settings-pane ul,
#search-results .result,
#search-results .result:hover,
#media-pane #media-detail {
	border-color: #000;
}
#compose #editor {
	background-color: #999;
}
#settings-pane p.copy:before {
	content: "Dark theme by Freddie Chessell | "
}
#conversations span i,
.modal .close {
	background-color: #C70000;
}
#conversations span i:hover,
.modal .close:hover {
	background-color: #C70000;
}
#compose #emoji-pane .scroll {
	height: 325px;
	background-color: #444;
	border-color: #000;
}
#compose #emoji-pane {
	width: 70%;
}
#compose #emoji-pane img {
	height: 25px;
	width: 25px;
}
.scrollbar .grab {
	-webkit-border-radius: 0px;
	border: none;
	background-color: #DDDDDD;
}
#search {
	background-color: #333;
}
#search-query {
	background-color: #999;
	color: #fff;
}
#search-results {
	background-color: #333;
}
#search-results .result:hover {
	background-color: #444;
}
#search-results .result {
	color: #ccc;
}
#search-results .result .search-result-date,
#search-results .result .search-result-name {
	color: #fff;
}
#search-submit {
	right: 3px;
	top: -1px;
}
#compose #filepicker,
#compose #filepicker_surrogate {
	background-color: #999999;
	border-color: #000;
}
#compose .filestyle button {
	background-color: #444;
	border-color: #000;
}
#media-pane #media-list {
	background-color: #444;
}
#media-pagination li.media-prev,
#media-pagination li.media-next {
	background-color: #444;
	border-color: #000;
}
#media-pagination li.media-prev:hover,
#media-pagination li.media-next:hover {
	background-color: #555;
}
#compose #emoji-button:hover {
	opacity: 1;
}
#compose #emoji-button {
	margin-top: 1px;
	opacity: 0.7;
	background-image: url(data:image/png;base64,)
}
/* i really want this to be global */
