css:
/* i really want this to be global */
body {
	min-width: 700px;
}
body, .page-main, .page-sidebar,.navbar-nav>li>a,.page-sidebar .nav-link,.page-sidebar .nav-link,.heading-1-sub, .heading-2-sub, .heading-3-sub, .heading-4-sub, .heading-5-sub,.heading-1, .heading-2, .heading-3, .heading-4, .heading-5,input,#page_profile .profile-header .user-background .user-background-position .user-header .user-header-info .user-ligne .user-name,.btn .label {
	font-family: -apple-sysem, "Helvetica Neue", "Helvetica", sans-serif !important;
}


.page-sidebar, .page-main {
	margin-top: 90px;
}

.page-notifications>.notification {
	border-radius: 0px;
	text-align: center;
	position: fixed;
	top: 0px;
	left: 0;
	right: 0;
	z-index: 999;
}

.page-sidebar .player-controls {
	position: fixed;
	top: 0;
	z-index: 900;
	background-color: transparent !important;
	height: 80px;
	width: 300px;
	margin: 0 auto;
}
div#player.player {
	position: fixed;
	top: 0;
	height: 90px;
	background-color: #1E1E27;
}
.page-sidebar .player-cover {
	width: 90px;
	height:90px;
	z-index: 90;
}

.page-sidebar .player-progress {
	position: fixed;
	top: 85px;
	height: 5px !important;
}

.page-sidebar .player-track {
	position: fixed;
	left: 90px;
	top: 20px;
	display: inline-block;
	right: auto;
	max-width: 250px;
	white-space: nowrap;
	overflow: hidden;
	-ms-text-overflow: ellipsis;
	-o-text-overflow: ellipsis;
	text-overflow: ellipsis;
}

.page-sidebar .player-actions {
	position: fixed;
	top: 30px;
	right:30px;
height: 30px;
width: 120px;
	left: auto;
	display: inline-block;
}

.page-sidebar .player-progress .progress-time, .page-sidebar .player-progress .progress-length {
	bottom: 5px;
}

.page-sidebar .player-actions>.action-hide {
	opacity: 1;
}

.page-sidebar .player-options {
	right: 35px;
	top: 30px;
}

.page-sidebar .player-progress .progress,.page-sidebar .player-progress .progress-handler {
	transition: width 1s, left 1s;
}

#page_profile .user-profil .catalog-content {
	padding: 5px 20px 24px;
}

#page_profile .user-profil .user-section-title {
	margin-bottom: 0px;
}

.page-sidebar .player-overlay {
	opacity: .3;
}

.page-sidebar .player-controls>.controls.controls-options .volume-progress-bar {
	height: 6px;
	border-radius: 3px;
}

.page-sidebar .player-controls>.controls.controls-options .volume-handler {
	height: 12px;
	width: 12px;
	margin: -6px 0 0 -6px;
	border-radius: 6px;
}

.page-sidebar .player-controls>.controls.controls-options .volume-bar {
	border-radius: 3px;
}

.page-panels .panel-container.opened .panel-wrapper,.page-livebar {
	top: 90px;
}

.page-sidebar .player-progress .progress-time {
	left: auto;
	right: 0px;
	border-radius: 0;
	padding-right:3px;
	background-color: transparent  !important;
	opacity: 1 !important;
	top: -88px;
	height: 12px;
	padding: 34px 10px 1px 10px;
}

.page-sidebar .player-progress .progress-time:after {
    content: "";
}
.page-sidebar .player-progress .progress-length {
	border-radius: 0;
	padding-left: 0;
	background-color: transparent !important;
	opacity: 1 !important;
	top: -42px;
	height: 12px;
	padding: 1px 10px 28px 10px;
}
.page-sidebar .navigation {
	bottom: 0;
}
.page-sidebar .player-track-title {
	max-width: 250px;
	white-space: nowrap;
	overflow: hidden;
	-ms-text-overflow: ellipsis;
	-o-text-overflow: ellipsis;
	text-overflow: ellipsis;
}
