css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dev-point.com") {
    .button_yellow {
        padding: 2px 10px !important;
        color: #fff !important;
        border-radius: 0px !important;
        /*border: none !important;*/
        box-shadow: none !important;
        padding: 3px 10px !important;
        white-space: nowrap !important;
        font: bold 12px 'Droid Arabic Kufi' !important;
        background-repeat: no-repeat !important;
        background-size: auto !important;
        background-color: #5b74a8 !important;
        border-color: #2f477a #29447e #1a356e !important;
    }
    .blogparmlink a {
        font-family: Arial !important;
    }
    .blogparmlink {
        box-shadow: none !important;
        font-family: Arial !important;
        text-shadow: none !important;
    }
    span.fa { font-family: 'Droid Arabic Kufi' !important; }
    table.forum > tbody > tr > td > a:nth-child(1) {font-size: 12px !important;}
    div .forum.forumhead {background-color: #0064b2 !important; }
    #searchv4 { font-size: 11px !important;border-bottom: none !important;}
    #createaccountlogin { border-radius: 0px !important;}
    .button_yellow:hover {
        background-repeat: no-repeat !important;
        background-size: auto !important;
        background-color: #5b74a8 !important;
        border-color: #2f477a #29447e #1a356e !important;
    }
    .button_yellow:active {
        border-bottom-color: #2f477a !important;
        background-image: linear-gradient(to bottom, #ffba00 0%, #d79d00 100%);
        box-shadow: inset 0 0px 2px 0 rgba(0, 0, 0, 0.5) !important;
    }
    .forum_counter_replys small, .forum_counter_threads small, .forum_counter_visitors small { font-size: 10.5px !important; }
    #postsidebar small { font-size: 11px !important; }
    div .forum.forumhead > a { color:#fafafa !important; }
    div.forum > table > tbody > tr > td > small > a {font-size: 10px !important; }
    div.forum > table > tbody > tr > td > a > strong { font-size: 12px !important; }
    .button_yellow .fa, .tborder .alt1 small .icov4 {display: none !important;}
    .barv4 {
        background-color: #0064b2 !important;
    }
    .tborder .alt1, .tborder .alt2 {
        background: #FAFAFA !important;
        font: bold 12px 'Droid Arabic Kufi' !important;
    }
    .button { font-size: 11px !important; padding: 2px 10px !important;}
    .tborder .alt1 .smallfont { font-family: 'Droid Arabic Kufi', Arial, Tahoma !important; }
    .tborder .thead { background: #FAFAFA !important; font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;}
    .vbmenu_control {font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;font-size: 10px !important;}
    td, th, p, li, .navbar, .page, .button, .wysiwyg, .alt2.vbseo_liked {font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;}
    td, th, p, li {font-size: 11px !important;}
    .button-nav-group .button { border: 0px solid #d4d4d4 !important; background-image: none !important; background-color: #ececec !important;}
    .button-nav-group .button:hover { text-shadow: none !important; color: #fafafa !important; background-color: #3072b3 !important;}
    .alt2.vbseo_liked {font-size: 11px !important;  }
    .postbitcontent, .page, .smallfont, input[type="text"], textarea, input[type="password"], input[type="email"], input[type="number"], input[type="url"], select {font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;}
    .postbitcontent {font-size: 12px !important;}
    .button.addreplyv4 {border-radius: 0.0em !important; }
    .navbar_notice {font-size: 12px !important;}
    .tcat {
        background: #0064b2 !important;
        font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;
        font-size: 12px !important;
    }
    .thead { border-bottom: 1px #eee solid !important; }
    .forum {
        background-color: #FBFBFB !important;
        border: 1px #eee solid !important;
        box-shadow: none !important;
    }
    .logo {font-size: 15px !important;}
    .vbseo_liked {
        border-color: #D5D5D5 !important;
        -moz-border-radius: 0px !important;
        -webkit-border-radius: 0px !important;
        border-radius: 0px !important;
        background-color: #FBFBFB !important;
        background-image: none !important;
    }
    .button_red, .buttonclosed {
        background-image: none !important;
        font-size: 11px !important;
        font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;
        padding: 2px 5px !important;
        color: #FFF !important;
        border-radius: 0px !important;
        border: none !important;
        box-shadow: none !important;
        text-shadow: none !important;
    }
    #vbseo-likes {
        border: 1px solid #eee !important;
        -moz-border-radius: 5px !important;
        border-radius: 0px !important;
        -webkit-radius-border: 5px !important;
        font-size: 11px !important;
        font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;
    }
    .vbseo-likes-count em {font-family: 'Droid Arabic Kufi', Arial, Tahoma !important;font-size: 10px !important;}
    .button, button, input[type="submit"] {
        text-shadow: none !important;
        background-image: none !important;
        background-clip: none !important;
        border-radius: 0.0em !important;
        border: 1px solid #eee !important;
        margin: 1px 2px !important;
    }
    input[type="submit"]:hover, .button:hover, .button:focus, .button:active, .button.active {
        border: 1px solid #3c8dde !important;
        text-shadow: none !important;
        background-image: none !important;
    }
    table > tbody tr td > div div > div > center table > tbody > tr > td > small {
        font-size: 10px !important;
    }
}
