* {
    border-radius: 0;
}

.btn-group {
    margin-left: 10px;
}

.btn-group .btn {
    background-color: transparent;
    border: 0;
    color: #888;
    text-shadow: 1px 1px 0px #fff;
}

.btn-group .btn.active {
    box-shadow: none;
    color: inherit;
    text-shadow: initial;
}

.btn-group .btn.active::before {
    border: solid #3B7FAE;
    border-width: 0 2px 2px 0;
    content: '';
    display: inline-block;
    height: 8px;
    left: 2px;
    position: absolute;
    top: 11px;
    transform: rotate(45deg);
    width: 5px;
}

.btn-group .btn:active {
    box-shadow: none;
}

.btn-group .btn:not(:first-child) {
    margin-left: 2px;
}

.input-group * {
    border-radius: 0;
}

.legend-item[style]:nth-child(1), div.progress [style*='green'] {
    background-color: #5DD257 !important;
}

.legend-item[style]:nth-child(2), div.progress [style*='orange'] {
    background-color: #848484 !important;
}

.legend-item[style]:nth-child(3), div.progress [style*='red'] {
    background-color: #D34848 !important;
}

.row.examples .btn {
    -webkit-font-smoothing: antialiased;
    background-color: rgb(62, 164, 224);
    border: 0;
    border-radius: 1px;
    color: #FFF;
    margin: 2px inherit;
    margin-bottom: 4px;
    padding: 2px 8px;
    font-weight: bold;
}


body > div.container > div:nth-child(1) > div > table > tbody > tr > td:nth-child(1) > div.h1.text-center {
	font-family: 'Playfair Display';
	color: #EE4456;
}

body > div.container > div:nth-child(1) > div > table > tbody > tr > td:nth-child(2) {
	display: block;
	position: absolute;
	top: 27px;
	left: 0;
	right: 0;
	margin: auto;
}

body > div.container > div:nth-child(1) > div {
	margin-top: 130px;
}

body > div.container > div:nth-child(1) > div > table {
	display: block;
	margin: auto;
}

.fb-like.fb_iframe_widget {
	position: absolute;
	left: 0;
	right: 0;
	top: 533px;
}

.jumbotron:first-of-type {
	background-color: transparent;
}

.row.search-options {
	text-align: center;
}

body {
	font-family: 'Exo 2';
}

.row.examples {
	text-align: center;
}

.jumbotron:not(:first-of-type) h1 {
	font-size: 50px;
	font-weight: bold;
	margin-top: 0;
}

#message {
	font-weight: normal;
}

div#reddit-widget {
	margin-left: 260px;
}

.legend-item {
	margin-left: 10px;
}

.legend {
	margin-top: 30px;
	width: 100%;
	text-align: center;
}


div#sharing-button-text {
	display: inline-block;
}

.sharing-buttons[style] {
	/* display: inline-block !important; */
	width: 360px;
	margin: auto;
	position: absolute;
	/* left: 0; */
	right: 30px;
	bottom: 26px;
}

.jumbotron.jumbo-results {
	position: relative;
}

.jumbotron > .row > table > tbody {
	display: block;
	width: 100%;
	text-align: center;
}

.jumbotron > .row > table > tbody tr {
	display: inline-block;
}

