css:
@-moz-document domain("twitter.com") {
.ProfileAvatar-image, .ProfileAvatar-placeholderImage {
    -moz-force-broken-image-icon: 1;
    border-radius: 120px;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
}
.ProfileAvatar {
    background: #fff;
    border: 5px solid #fff;
    border-radius: 120px;
    height: 200px;
    position: relative;
    width: 200px;
}
.profile-card.profile-header .avatar {
float: none;
-webkit-border-radius: 73px;
-moz-border-radius: 73px;
border-radius: 73px;
}
.profile-card.profile-header .profile-picture {
position: relative;
z-index: 5;
margin: 20px auto 6px;
display: block;
width: 73px;
height: 73px;
border: 4px solid white;
background-color: white;
-webkit-border-radius: 73px;
-moz-border-radius: 73px;
border-radius: 73px;
}
.avatar {
width: 48px;
height: 48px;
-webkit-border-radius: 48px;
border-radius: 48px;
}
.size32 {
width: 32px;
height: 32px;
-webkit-border-radius: 32px;
-moz-border-radius: 32px;
border-radius: 32px;
}
.size73 {
width: 73px;
height: 73px;
-webkit-border-radius: 73px;
-moz-border-radius: 73px;
border-radius: 73px;
}
.global-nav .people .count {
    background-color: #fff;
    border-radius: 120px;
    color: white;
    display: none;
    font-size: 11px;
    height: 20px;
    left: 24px;
    line-height: 17px;
    margin: 0;
    opacity: 1;
    -ms-filter: "alpha(opacity=100)";
    position: absolute;
    text-align: center;
    text-shadow: none;
    top: 3px;
    z-index: 100;
}
.global-nav .people .count .count-inner {
    border: 2px solid #fff;
    border-radius: 50px;
    display: block;
    opacity: .6;
    -ms-filter: "alpha(opacity=60)";
    background-color: #66757f;
    line-height: 16px;
    width: 16px;
    height: 16px;
}
.DashboardProfileCard {
    border: 1px solid #e1e8ed;
    background: #FFF;
    border-radius: 0px;
}
.DashboardProfileCard-bg {
    background-size: 100%;
    border-bottom: 1px solid #e1e8ed;
    height: 95px;
    border-radius: 0px 0px 0 0;
    background-position: 0 50%;
    width: 100%;
    padding: 0 1px;
    margin-left: -1px;
    margin-top: -1px;
}
.Trends {
    background-color: #fff;
    border: 1px solid #e1e8ed;
    border-radius: 0px;
    line-height: inherit;
    padding: 15px;
}
.module {
    background-clip: padding-box;
    border: 0;
    border-radius: 0px;
    line-height: 16px;
    position: relative;
    margin-bottom: 10px;
}
body {
    font-family: "Segoe UI";
}
.wtf-module.has-content {
    display: block;
    visibility: hidden;
}
.DashboardProfileCard-avatarImage {
    border-radius: 120px;
    height: 72px;
    width: 72px;
    border: 2px solid #fff;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: #fff;
}
.DashboardProfileCard-avatarLink {
    background-color: #fff;
    border-radius: 120px;
    margin: -30px 0 0 8px;
    padding: 1px;
    vertical-align: bottom;
}
.module .list-link {
    position: relative;
    display: block;
    padding: 8px 12px;
    border-radius: 0px;
}
.module .list-link {
    background-color: #fff;
    color: #66757f;
    border: 1px solid #e1e8ed;
    border-top: 0;
    border-radius: 0px;
}
.module {
    background-clip: padding-box;
    border: 0;
    border-radius: 0px;
    line-height: 16px;
    position: relative;
    margin-bottom: 10px;
}
.ProfileCard {
    background-color: #fff;
    border: 1px solid #e1e8ed;
    border-radius: 0px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    text-align: left;
}
.ProfileCard {
    background-color: #fff;
    border: 1px solid #e1e8ed;
    border-radius: 0px;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    text-align: left;
}
.ProfileCard-avatarImage {
    border-radius: 120px;
    display: block;
    height: 70px;
    width: 70px;
}
.ProfileCard-avatarLink {
    background-color: #fff;
    border-radius: 120px;
    display: inline-block;
    margin: -45px 2px 0 -3px;
    padding: 3px;
    position: relative;
    vertical-align: bottom;
    z-index: 1;
}
.hovercard.profile-card .ProfileCard-content .ProfileNameTruncated {
    margin-top: -25px;
    text-align: center;
}
.ProfileCard-screenname {
    height: 14px;
    line-height: 1;
    margin-bottom: 10px;
    text-align: center;
}
.ProfileCard .ProfileCard-screennameLink, .ProfileCard .ProfileCard-screennameLink:hover, .ProfileCard .ProfileCard-screennameLink:focus {
    color: #66757f;
    font-size: 12px;
    font-weight: 400;
    text-align: center;
}
.DashboardProfileCard-name {
    font-weight: 700;
    font-size: 23px;
    line-height: 21px;
    text-align: center;
}
div.DashboardProfileCard.module {
height: 310px;
}
div.DashboardProfileCard-content {
height: 100px;
}
}
