css:
.autocomplete-history-dropmarker.urlbar-history-dropmarker {display: none !important;}

#urlbar {
  background: black !important;
  color: gray !important;}





@-moz-document url(about:newtab), url("about:privatebrowsing"), url("about:Privatebrowsing"), url("about:PrivateBrowsing"), url("about:privateBrowsing"), url("about:blank") {

html {
  background: black !important;}

#newtab-customize-button, #newtab-vertical-margin, body, #bar, #trackingProtectionSection, #list-area, a#learnMore, #privateBrowsingSection
  {display: none !important;}
}






@-moz-document url-prefix(about:neterror), url-prefix("about:blank") {

html, body {background: black !important;}

h1, p, li, ul {color: #999 !important;}

button {
  -moz-appearance: none;
  background: black !important;
  color: #d2d2d2 !important;
  background: -moz-linear-gradient(center top , #333333, #000000) repeat scroll 0 0 #000000 !important;
  border: 1px #444 solid !important;}

button:hover {background: #222 !important;}

}





@-moz-document url(about:config) {

#configDeck {
  background: black !important;
  color: #d2d2d2 !important;}

scrollbar  {
  -moz-appearance: none !important;
  background: white !important;}

thumb {
  -moz-appearance: scalethumb-vertical !important;}

#textbox, tree#configTree {filter: invert(100%) hue-rotate(180deg);}

}





@-moz-document url-prefix(about:preferences) {

page {
  filter: invert(100%) hue-rotate(180deg);
  background-color: black !important;}

#categories {background-color: white !important;}

.category[selected], .category:hover {
  background-color: #E9E9E9 !important;
  color: #2D2D2D !important;
  -moz-border-start: unset !important;}

#tabsElement tab[selected] {-moz-border-bottom-colors: #D2D2D2 !important;}

.help-button {display: none !important;}
}





@-moz-document url(about:addons) {

page#addons-page, page.greasemonkey #greasemonkey-sort-bar, #list-view[type="userstyle"] #addon-list-empty[hidden] ~ #userstyle-sorting, #addons-page .global-info-container, .alert, #nav-header, #categories, #header-utils-btn, textbox#header-search {
  background: black !important;
  color: #d2d2d2 !important;}

richlistitem.category:not([selected]), #detail-contrib-description, page.greasemonkey #greasemonkey-sort-bar .text-link
  {color: #d2d2d2 !important;}

.name-container
  {color: #d2d2d2 !important;
  font-weight: unset !important;}

.addon[selected], .category[selected] {
  background: #565656 !important;
  -moz-border-start: 4px solid #A9A9A9 !important;}

#header-utils-btn:hover {
  color: white !important;
  background: #222 !important;}

.addon
  {color: gray !important;}

richlistitem.category[selected], #main #promos.js, #featured-addons, #addons {
  background: black !important;
  color: #d2d2d2 !important;}

richlistitem.category:hover:not([selected]){
  text-decoration: underline !important;}

#view-port-container #detail-view, #view-port-container #list-view {
  filter: unset !important;
  background-color: black !important;
  color: #d2d2d2 !important;}

.icon, #view-port-container #detail-view image, #view-port-container #list-view image, deck.view-pane, #detail-contributions
  {background-color: black !important;}

*|button, menulist {
  -moz-appearance: none !important;
  background: linear-gradient(#333, #000) !important;
  border: 1px #444 solid !important;
  color: #999 !important;
  box-shadow: unset !important;
  text-decoration: unset !important;}

*|button:hover {
  color: white !important;
  background: #222 !important;}

#category-discover
  {display: none !important;}

}





@-moz-document url(about:sessionrestore), url(about:home), url(about:robots) {

html, body, treechildren {
  background: black !important;
  color: #d2d2d2 !important;}

treecol {background: black !important; color: #d2d2d2 !important;}

treecol:hover {text-decoration: underline !important;}

button {
  -moz-appearance: none;
  background: black !important;
  background: -moz-linear-gradient(center top , #333333, #000000) repeat scroll 0 0 #000000 !important;
  border: 1px #444 solid !important;}

button:hover {background: #222 !important;}

#block-snippet-overlay, #launcher, #brandLogo, #searchIconAndTextContainer, #aboutMozilla, .title, #errorLongDesc, #errorPageContainer {display: none !important;}
}





@-moz-document url(about:about), url(about:crashes), url(about:rights), url(about:support), url(about:sync-log), url(about:telemetry), url(about:welcomeback) {

html, body {background: black !important;}

a:link {color: #d2d2d2 !important;}

button {
  -moz-appearance: none;
  background: black !important;
  background: -moz-linear-gradient(center top , #333333, #000000) repeat scroll 0 0 #000000 !important;
  border: 1px #444 solid !important;}

button:hover {background: #222 !important;}

}





@-moz-document url("chrome://browser/content/browser.xul") {#bamboo-stack, .bamboo-zone-view {background: black !important;}}





@-moz-document domain(youtube.com) {

.watch-sidebar-section, #footer-container, #watch-discussion, .yt-alert-actionable.yt-alert-info {display: none !important;}
}
/* i really want this to be global */
