css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nixers.net") {
  body {
    background-color: rgb(57, 81, 104);
    color: rgb(224, 224, 224);
  }
  table {
    color: rgb(224, 224, 224);
    border: 0px !important;
  }
  .trow2,.trow1 {
    background: rgb(67, 93, 120) !important;
    border: 0px !important;
  }
  td.tcat {
    background: rgb(67, 93, 120) !important;
    border: 0px !important;
    color: white !important;
  }
  .trow2 {
    border-top: 1px solid rgb(72, 101, 130) !important;
  }
  hr {
    border: 0;
    border-bottom: 1px solid rgb(72, 101, 130) !important;
    background: #212121;
    width: 100%;
  }
  a:link {
    color: rgb(224, 224, 224) !important;
  }
  a:visited {
    color: rgb(224, 224, 224) !important;
  }
  .trow1.post_buttons {
    background-color: rgb(67, 93, 120) !important;
  }
  .avatartable {
    background-color: rgb(67, 93, 120) !important;
    background: rgb(67, 93, 120) !important;
  }
  input[type="submit"], input[type="button"], input[type="checkbox"] {
    background-color: rgb(67, 93, 120) !important;
    color: white !important;
    border: 0 !important;
    outline: none !important;
  }
  .post_management_buttons > a > input {
    background-color: rgb(67, 93, 120) !important;
    color: white !important;
    margin-top: 3px !important;
  }
  .post_author a {
    color: rgb(19, 193, 95);
  }
  blockquote, .codeblock {
    background-color: rgb(57, 81, 104) !important;;
    border: 1px solid rgb(72, 101, 130) !important;
    outline: none !important;
    max-width: inherit !important;
  }
  blockquote > blockquote {
    background-color: rgb(67, 93, 120) !important;
    color: rgb(224, 224, 224) !important;
    border: 1px solid rgb(72, 101, 130) !important;
  }
  div.name_topic {
    background-color: rgb(67, 93, 120) !important;
    color: rgb(224, 224, 224) !important;
  }
  #infobar {
    background-color: rgb(67, 93, 120) !important;
    border: 0px !important;
  }
  #nixerlogo > a {
    color: #ffcc00 !important;
  }
  .largetext > a > span {
    color: #ffcc00 !important;
  }
  div.name_topic {
    border: 0px !important;
  }
  .author_buttons.float_left {
    color: rgb(224, 224, 224);
    font-size: 12px;
  }
  
}
