css:
@-moz-document url-prefix("http://www.jango.com/") {
/* Remove background image from the site */
body {
    background: #ccc;
}

/* Header adjustements */
#nc_header_widget {
    display: none;
}

/* "Welcome to service" banner */
#one_ad_to_rule {
    display: none;
}

/* Master header */
#masthead {
	margin: 0;
    padding-bottom: 0;
    border-radius: 0;
}


/* Audio player adjustments */

/* Audio player - small stations list */
#player_similar_stations,
#player_recent_stations {
	background-color: #ddd;
}


/* Lyrics panel */
.player2012_pane {
	background-color: #ddd;
}




/* "Browse Stations" section header */
#column-wrapper.three_col_user #c #playlist_module,
#playlist_module .h45 {
    border-radius: 0;
    background: none;
}
.h45 {
    border-radius: 0;
    border-bottom: 0;
}


/* "Browse Stations" list background */
#playlist_stations_module {
    background-color: #ddd;
}




/* Facebook box */
#user_profile_like_box {
    display: none;
}



/* Sidebar adjustments */

/* Similar artist list */
#similar_artists {
    background: #ccc;
    border-right: none;
}

/* Similar artist list divider hr bar */
#similar_artists .list_divider {
    display: none;
}

/* Similar artist item */
#similar_artists .the_sim {
    margin: 15px auto 0;
    padding-bottom: 0;
}
/* Artist image inside similar artist item */
#similar_artists .the_sim img {
    margin-bottom: 0;
}
/* Description text inside similar artist item */
#similar_artists .the_sim div {
    padding-bottom: 0;
}



/* Footer styles */
#footer.app {
    border-top: none;
	background: #ccc;
}
.footer_quotes_white {
    display: none;
}
}
