.username .style55:before {
    content: "\f040";
     padding-right: 3px;
}
.username .style9:before {
    content: "\f25b ";
     padding-right: 3px;
    font-family: "FontAwesome";
    font-size: 90%;
    display: inline-block;
    color: #F9A600;
    
}
.username .style2:before {
    content: "\f007 ";
     padding-right: 3px;
    font-family: "FontAwesome";
    font-size: 90%;
    display: inline-block;
    color: #417394;
    
}
.username .style10:before {
    content: "\f0ad ";
     padding-right: 3px;
    font-family: "FontAwesome";
    font-size: 90%;
    display: inline-block;
    color: #F9A600;
    color:#B9121B;
    text-shadow:0px 1px 1px rgba(240,240,240,0.3),0px 1px 1px rgba(0,0,0,0.3);
