css:
/* TRYB NOCNY DLA POCZTY o2.pl i prokonto.pl */
/* Autor - Maciej Kawa */
/* https://userstyles.org/styles/123749/tryb-nocny-dla-poczty-o2-pl-i-prokonto-pl */

@-moz-document regexp('(https?:\/\/)?([\w]+\.)?(poczta.o2|poczta10.o2|prokonto|mail.tlen)\.pl\/?.*')
{
    html
    {
        background: none;
    }

    body
    {
        color: #8f8f8f;
        background: #191919;
    }

    a,
    .link,
    .notification .link,
    #betatab .betatab #hide-tab,
    #folders li span.name,
    #mailbox #mailbox-hide-detailed-headers .link,
    #mailbox #mailbox-show-details,
    #mailbox #mailbox-view-detailed-full-headers a,
    #mailbox-headers .contact-button.is-in-abook
    {
        color: #afafaf;
    }

    .dropdown .more a
    {
        color: #fff;
    }

    a:hover,
    .link:hover,
    #betatab .betatab #hide-tab:hover,
    #folders li span.name:hover,
    #maillist tr.unread
    {
        text-decoration: underline;
    }

    table.sortable tbody tr:hover td,
    .dropdown .more li:hover,
    .roster-list li:hover,
    #folders li > a:hover,
    #options-folders-view li .folder:hover
    {
        transition: background-color .05s linear 0s;
        background-color: #363636;
    }

    .list__tabs,
    .notification,
    .top-info,
    #content #im #im-nav li.notification,
    #folders.active-view-inbox li.current > a,	
    #maillist #maillist-table .no-items,	
    #message .notice,
    #transparent-message-wrapper .notice
    {
        background-color: #363636;
    }

    .im-form-dialog h3,	
    .sidebar-wrap,

    #im-invite-gmail ul li.odd,
    #folders-wrap,	
    #footer,	
    #loading,	
    #sidebar-composer .left,
    #sidebar-composer .inner,
    #sidebar-composer .right,	
    #sidebar-links-bottom,	
    #sidebar-links-content,	
    #sidebar-links-top,
    #sidebar-roster-wrap,
    #user-inner,
    #widgetbarA .widget_container
    {
        background-color: #363636;
        background-image: none;
    }

    #folders
    {
        border-color: #363636;
        background-color: #0c0c0c;
    }

    #sidebar-composer .left
    {
        border-top-left-radius: 3px;
    }

    #sidebar-composer .right
    {
        border-top-right-radius: 3px;
    }

    #sidebar-links-bottom
    {
        border-bottom-right-radius: 3px;
        border-bottom-left-radius: 3px;
    }

    #content
    {
        border: 2px solid #363636;
    }

    input.text,
    option,
    select,
    textarea,	
    .content-inner,	
    .dropdown ul li,
    .im-form-dialog fieldset,
    #composer #composer-attachments-all,
    #im-welcome .features .inner
    {
        color: #fff;
        background: none;
        background-color: #080808;
    }

    #composer .attachments-tip
    {
        left: 10px;
        width: 0;
        height: 0;
        border-width: 0 13px 15px 13px;
        border-style: solid;
        border-color: transparent transparent #fff transparent;
        background: none;
    }

    #tinymce
    {
        background-color: #fff;
    }

    #betatab .betatab .alert-info h2,
    #betatab .betatab .alert-info p,
    #composer-abook-wrap,
    #content
    {
        color: #8f8f8f;
        background-color: #0c0c0c;
    }

    .content-tabs li .label,
    .content-tabs li:hover .label,
    .content-tabs li.selected .label,
    .options-form label,	
    #contextnav #contextnav-page-nav li.nav-count,
    #contextnav-page-nav-bottom li.nav-count,
    #editprofile .info-box h4,
    #editprofile .form-header h4,	
    #invite-tab-container .form-header h4,	
    #mail-search-advanced-submit-return,	
    #mail-search-advanced li label,
    #mailbox .detailed-header,	
    #mailbox #mailbox-view-short-rest,
    #mailbox #mailbox-attachment-list-thumbs li .name,	
    #mailbox-headers .link,
    #mailbox-view-full-label,	
    #maillist .from a,
    #maillist .subject a,
    #newagreement .info-box h4,
    #newagreement .form-header h4,
    #options .form-header h4,	
    #options-general-security-recover-link,
    #options-general-change-pass-link,
    #options label,	
    #options legend,
    #options-filtering-aliases-table .name,
    #sidebar-im-label-link,
    #sidebar-composer .label	
    {
        color: #8f8f8f;
    }

    .switcher .selected
    {
        color: #cfcfcf;
    }

    .image-button > .inner,
    .image-button > .left,
    .image-button > .right,
    .image-button .sep,
    .image-button > .inner > .arrow-wrap,
    .small-button.selected .left,
    .small-button.selected .inner,
    .small-button.selected .right
    {
        color: #8f8f8f;
        background-color: #363636;
        background-image: none;
    }

    .image-button
    {
        color: #8f8f8f;
        border: 1px solid #363636;
        border-radius: 3px;
    }

    .roster
    {
        border: none;
    }

    .content-tabs .close-tab,
    .content-tabs .label,	
    .content-tabs li .left,
    .content-tabs li .right,
    .content-tabs li.selected .close-tab,	
    .content-tabs li.selected:hover .close-tab,	
    .content-tabs li.selected:hover .label,	
    .content-tabs li.selected:hover .left,	
    .content-tabs li.selected:hover .right,	
    .content-tabs li.selected .label,		
    .content-tabs li.selected .left,	
    .content-tabs li.selected .right
    {
        background-image: none;
    }

    .content-tabs li .left
    {
        border-top-left-radius: 3px;
        background-color: #363636;
    }

    .content-tabs .close-tab,	
    .content-tabs .label,
    #mailbox #mailbox-attachment-list-thumbs li,
    #message .notice, 
    #transparent-message-wrapper .notice	
    {
        color: #8f8f8f;
        background-color: #363636;
    }

    .content-tabs li .right
    {
        border-top-right-radius: 3px;
        background-color: #363636;
    }

    .content-tabs li.selected .left
    {
        border-top: 1px solid #363636;
        border-left: 1px solid #363636;
        background-color: #0c0c0c;
    }

    .content-tabs li.selected .label
    {
        color: #7f94a7;
        border-top: 1px solid #363636;
        background-color: #0c0c0c;
    }

    .content-tabs li.selected .close-tab
    {
        border-top: 1px solid #363636;
        background-color: #0c0c0c;
    }

    .content-tabs li.selected .right
    {
        border-top: 1px solid #363636;
        border-right: 1px solid #363636;
        background-color: #0c0c0c;
    }

    #inbox
    {
        background-color: #0c0c0c;
    }

    .list__tab,
    #im-welcome,
    #gallery-menu,
    #mailbox-headers
    {
        background: none;
    }

    .list__tab.selected
    {
        border-bottom: none;
        background: #8f8f8f;
    }

    .list__tab:hover
    {
        color: #8f8f8f;
        background: #0c0c0c;
    }

    .x-binder-info-inbox,
    .x-binder-info-others
    {
        color: #363636;
        background-color: #8f8f8f;
    }

    .sort-order-asc,
    .sort-order-ascending,
    .sort-order-desc,	
    .sort-order-descending,
    #abook caption	
    {
        background-color: #555;
    }

    .sortable th
    {
        color: #000;
    }

    table.sortable tbody tr.current td,
    table.sortable tbody tr.current:hover td,
    table.sortable tbody tr.unread:hover td,	
    tr.current td,
    .dropdown .more li.current,
    .qr-form,
    #mailbox .search-hi,
    #maillist .search-hi,
    #options tbody tr.current td
    {
        color: #8f8f8f;
        background-color: #363636 !important;
    }

    table.sortable tbody tr.unread td
    {
        background-color: #220 !important;
    }

    li.odd > .folder,
    tr.odd,
    tr.odd td
    {
        background-color: #030303;
    }

    tr.even,
    tr.even td
    {
        background-color: #111;
    }

    .mailbox-info,	
    #mailbox-view-full
    {
        color: #8f8f8f;
        background-color: #363636;
    }

    #mailbox-headers .contact-button
    {
        border: 1px solid #515151;
    }

    #mailbox-headers .contact-button:hover
    {
        color: #363636;
        border-color: #8f8f8f;
        background-color: #8f8f8f;
    }

    div#widgetstab-list div.description,
    div#widgetstab-manage div.description,	
    .view .info-box,
    #editprofile .form-header,	
    #editprofile .info-box,
    #invite-tab-container .form-header,
    #newagreement .form-header,	
    #newagreement .info-box,
    #options .info-box,
    #options .form-header
    {
        color: #8f8f8f;
        border: none;
        background-color: #363636;
    }

    #options-identities-list .options-idt-row:hover
    {
        background-color: #131313;
    }

    #maillist .snippet-list
    {
        color: #4f4f4f;
    }

    #mail-filters-advanced-wrap,
    #mail-search-advanced	
    {
        color: #363636;
        background-color: #8f8f8f;
    }

    #abook .details-row,	
    #mail-filters-close,
    #mail-search-advanced li label,	
    #mail-search-advanced-submit-return,
    #maillist #maillist-table.view-mode-search .no-items,	
    #widgetstab-manage .widgetEven
    {
        color: #363636 !important;
    }

    .add-signature,
    #MB_caption,
    #MB_caption .extended-caption,
    #MB_content .wizard-footer-info,
    #MB_frame,	
    #options-folders tbody,
    #options-filtering-black-list tbody,	
    #options-filtering-white-list tbody
    {
        color: #363636;
        background-color: #8f8f8f;
        text-shadow: none;
    }

    #widgetstab-manage .widgetOdd
    {
        background-color: #000;
    }

    #adv-sidebar-iframe,
    #adv-sky-iframe
    {
                filter: grayscale(90%);
        -webkit-filter: grayscale(90%);
    }

    #html-iframe
    {
        background-color: #fff;

                filter: opacity(80%);
        -webkit-filter: opacity(80%);
    }
}
