css:
@-moz-document regexp("https?:\/\/s.{4,7}\.ikariam\.gameforge\.com\/.+") {
    #ikaez_popup_bg, #ikaez_popup, .ikaez_fb_link {
        display: none !important;
    }
    #js_tabIkaEasyOptions {
        background: transparent !important;
    }
    #js_tabIkaEasyOptions:hover, #js_tabIkaEasyOptions.selected {
        background-color: #FAF3D7 !important;
    }
    b.tabIkaEasyOptions, a.noViewParameters[href="/index.php?view=options"] {
        color: #542c0f !important;
    }
    #footer .copyright, #footer .copyright > * {
        font-size: 0px !important;
    }
    #footer .copyright::after {
        content: "Â© 2008 by Gameforge 4D GmbH. All rights reserved.";
        font-size: 10px;
    }
    #GF_toolbar ul {
        width: 1000px;
    }
    #header #advisors a:hover, #sidebar #buildingUpgrade .actions .action_btn:hover {
        -webkit-filter: brightness(1);
    }
    #header #advisors a:not(.pluslink), .hover {
        transition: all 0s ease 0s;
    }
    #container .tabmenu .selected, #container .tabmenu .tab:hover {
        height: 32px;
    }
    .allyLink > img[src*="http://ikariam-easy.com"] {
        display: none
    }
    .ikaeasy_watcher .ikaeasy_watcher_circle, .ikaeasy_resources_negative, .ikaeasy_resources_positive, #header #globalResources .gold a, #header #globalResources .transporters a, div#js_viewFriends, div#leftMenu, ul.resources .tooltip, .ikaeasy_watcher.build_default .ikaeasy_watcher_circle, div.ikaeasy_levelcity, button#ikaez_update_island_info, a.allyLink.ikaez_highlighted_ally {
        font-family: Arial, Helvetica, sans-serif;
    }
    a.allyLink.ikaez_highlighted_ally {
        font-weight: normal;
    }
}
