  /*------------------------------ SHOW/HIDE ITEMS ------------------------------ */
  
	/* Hide heart */
	  .glyphicon-heart { display: none !important; }
    .glyphicon-star { display: none !important; }
    .glyphicon-fire { display: none !important; }
    .glyphicon-signal { display: none !important; }
    
    /* hide footer */
    .footer { display: none !important; }
    
    /* hide tasks 'move to top' icon */
    .glyphicon-open { display: none !important; }
    
    /* hide tasks 'task notes' icon */
    .task-notes { display: none !important; }
  
    /* hide task filters */
    .task-filter { display: none !important; }
    
    /* hide tags */
    /*.filters, 
    .task-column:not(.rewards) .task-options > form > fieldset:nth-last-of-type(1), .task-column.rewards .rewards.main-list .task-options fieldset:nth-last-of-type(1) { display: none !important; }*/
    
    
  
  /*------------------------------ SITE HEADER (MENUS & AVATAR) ------------------------------ */
  
    /* toolbar fix for firefox users using the vertical scrolling bar on the left of the screen */
    .toolbar {
      right: unset !important;
      border-bottom: 1px solid #9ea9ab;
    }
    #main .col-md-12 {
      padding-left: 0;
    }
    
    /* we add the logo (original height="61" width="240") */
    .toolbar-container::before {
      float: left;
     content: ""; 
      background: url(https://habitica.com/presskit/Habitica%20Text%20Logo.png) no-repeat;
      background-size: auto 110%;
      background-position: 4px;
      margin-top: 8px;
      width: 150px;
      height: 26px;
      padding: 0 3em 0 0.3em !important;
    }
  
    /* we reduce the height of the toolbar */
    .toolbar-nav, .toolbar-bailey, .toolbar-options, .toolbar-subscribe, .toolbar-wallet {
      padding: 0;
    }
    
    .toolbar-currency {
      margin-bottom: 0;
    }
      
    /* we remove the buttons border */
    .toolbar-nav .toolbar-button:first-of-type > a { border-left: 1px solid #9ea9ab !important; }
    .toolbar-nav .toolbar-button > a, .toolbar-nav .toolbar-button-dropdown {
      border-width: 0 1px 0 0 !important;
    }
  
     .toolbar-options > li > a {
      border-width: 0 !important;
    }
  
    .toolbar-subscribe-button > button {
      border-width: 0 0 0 1px !important;
    }
    
    /* we remove the space between buttons */
    .toolbar-options > li:not(:first-of-type) {
      margin-left: 0;
    }
    .toolbar-nav > li {
      margin-right: 0;
    }
  
    /* the wrapper height has to reflect the toolbar height */
    .header-wrap {
      padding-top: 28px;
      border-bottom: 1px solid #C3D1D6;
    }

    
    /* we update the background color of the avatar on hover */
    .herobox.noBackgroundImage:hover {
      background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/IntroBackground.imageset/Intro%20Background%20iPad.png?raw=true);
    }
   
    /* what follows impacts only the submenus */
  
    /* we remove the light border before the sandwitch icon */
    .toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2) {
      border-left: transparent !important;
    }
    
    /* we hide the triangle on the top of each submenu */
    .toolbar-audio > div h4::before, .toolbar-audio > div > div:first-child::before, .toolbar-audio > div > ul:first-child::before, .toolbar-mobile > div h4::before, .toolbar-mobile > div > div:first-child::before,
    .toolbar-mobile > div > ul:first-child::before, .toolbar-nav .toolbar-button-dropdown > div h4::before, .toolbar-nav .toolbar-button-dropdown > div > div:first-child::before, .toolbar-nav .toolbar-button-dropdown > div > ul:first-child::before,
    .toolbar-notifs > div h4::before, .toolbar-notifs > div > div:first-child::before, .toolbar-notifs > div > ul:first-child::before, .toolbar-settings > div h4::before, .toolbar-settings > div > div:first-child::before,
    .toolbar-settings > div > ul:first-child::before {
      display: none !important;
    }
    
    /* the triangle being removed, we unset the top so the submenu appears just below its sandwitch button, without gaps in between. the margin trick allows a perfect alignment with the navbar borders */
    .toolbar-audio > div, .toolbar-mobile > div, .toolbar-nav .toolbar-button-dropdown > div, .toolbar-notifs > div, .toolbar-settings > div {
      top: unset !important;
      border-color: #9ea9ab;
      margin-left: -1px;
      margin-right: -1px;
    }
  
    /* we reduce the padding for a more condensed look */
    .toolbar-audio > div ul, .toolbar-mobile > div ul, .toolbar-nav .toolbar-button-dropdown > div ul, .toolbar-notifs > div ul, .toolbar-settings > div ul {
      padding: 0;
    }
     .toolbar-audio > div ul a, .toolbar-mobile > div ul a, .toolbar-nav .toolbar-button-dropdown > div ul a, .toolbar-notifs > div ul a, .toolbar-settings > div ul a {
      padding: 0.5em 1.2em 0.5em 0.5em; /* default: 0.618em 1em */
 
    } 
    .toolbar-nav .toolbar-button-dropdown.highlight > a.active {
      background-color: #C0D4F7 !important;
    }
    
    /* we make the different options of the submenu more visible */
    .toolbar-audio > div ul a:hover, .toolbar-mobile > div ul a:hover, .toolbar-nav .toolbar-button-dropdown > div ul a:hover, .toolbar-notifs > div ul a:hover, .toolbar-settings > div ul a:hover {
      background: #E6E6FA;
      text-decoration: none !important;
    }
    .toolbar-nav .toolbar-button-dropdown.highlight > div ul a:hover {
      background: #E6E6FA; /*#EDF2F2;*/
      text-decoration: none !important;
    }
    
    /* we hide the ugly big 'settings' h4 in the settings */
    /*.toolbar-settings > div > h4 { display: none !important; }*/
    
  
    /* see http://benkaminski.com/2014/08/30/how-to-customize-bootstrap-3-popovers-individually-using-only-css/ for more details about bootstrap popover styling */
    .popover {
      border-radius: 5;
      border-color: #9ea9ab;
      -webkit-box-shadow: none !important;
              box-shadow: none !important;
    }
    
    /* we remove the top arrow then remove the top margin so the popover appears like the other submenus */
    .popover.bottom > .arrow, .popover.bottom > .arrow:after { display: none !important; }

    .popover.bottom{
      margin-left: -30px !important; /* blob to display it completely now that the button is fully to the right */
      margin-top: 0; 
    }
      
  
  /*------------------------------ SITE HEADER (STAT BARS) ------------------------------ */
  /* we remove the gap between symbol & bar then tweak the colors & round the edges */
  /* we also replace the grey background by a light blue one */
  
    .header-wrap, header .hero-stats, .herobox.noBackgroundImage {
      background: #4B0082;
    }
    
    /* coupled with the margin-bottom: 0 of the last bar (mana), we have a nicely centered stat bars area (works only after level 10 though, when the mana bar appears) */
    .hero-stats {
      padding-top: 0;
      border-right: none !important;
    }

    .hero-stats .meter-label {
      margin-right: 0; /* default: 0.618em*/  
      text-shadow: unset !important;
      border-top-left-radius: 1.3em !important;
      border-bottom-left-radius: 1.3em !important;
      outline: 0;
      height: 2.31em;
      width: 3em;
      padding: 0.5em 0 0.5em 0.4em;
    }

    .hero-stats .meter {
      outline: 0;
      height: 1.85em; /* default: 1.618em; */
      margin-bottom: 0.6em;
      border-top-right-radius: 1.3em !important;
      border-bottom-right-radius: 1.3em !important;
    }
  
    .hero-stats .meter-text.value {
      right: 0.75em;
    }
    
    
