css:
    
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("last.fm"), 
domain("lastfm.es"),
domain("lastfm.de"),
domain("lastfm.fr"),
domain("lastfm.jp"),
domain("lastfm.it"),
domain("lastfm.pl"),
domain("lastfm.com.br"),
domain("lastfm.se"),
domain("lastfm.tr"),
domain("lastfm.ru"),
domain("cn.last.fm") {

.header-background {
  background-image: url(https://s-media-cache-ak0.pinimg.com/originals/58/56/fa/5856fa2b30ff37474d93433dcacf2677.jpg) !important;
  background-attachment: fixed !important;
}

.header-featured-track, .share-desktop, div.nag-bar, .grid-items-item-playlink, .image-overlay-playlink-link {
display:none !important;
}
  
.avatar, img.avatar-img, .header-metadata-avatar, .nowslider_img {
border-radius:5% !important;
}

.auth-avatar-desktop, .header-metadata-user-stats-avatar {
  border-radius:10% !important;
}
  
.tasteometer-avatar {
  border-radius:50% !important;
}
 
/* Content primary background color */  
.content-top, .container.page-content, .stationlink, .content-top-lower, .row, .stationlinks-header, .col-sidebar.col-sidebar--long, .stationlink-list, .page-content, .two-column-layout.container, .clearit, div.sic #content .fullWidth { 
background-color:lightgrey !important;
}
  
/* Content secondary background color */ 
.chartlist tr:nth-child(2n) td, .recs-feed .context, .date-range-picker-presets a:hover, .date-range-picker-presets a:focus, .js-playlink-station {
  background-color:gray !important;
}  
  
.chartlist-image, .image-overlay-playlink-link {
display:none !important;
}

.two-column-layout .container {
    background-image:none !important;
}        
  
.chartlist-index, .chartlist.chartlist--has-image td, .chartlist-ellipsis-wrap, .chartlist-countbar, .chartlist-name, .chartlist-ellipsis-wrap, .chartlist-timestamp,  .chartlist-delete {
padding-top:1px !important;
padding-bottom:1px ! important;
padding-left: 1px ! important;
padding-right: 1px ! important;  
}

.chartlist-play {
height:100% !important;
}
  
.chartlist-name {
height:100% !important;
}
  
.chartlist-image, .image-overlay-playlink-link {
display:none !important;
}

.hidden-xs.header-scrobble-since {
color:#FFFFFF !important;
}
  
.countbar-bar-slug {
background: #48779A !important;
}
  
div.feedback-message, .copyright.col-xs-12, .col-xs-2 {
display:none !important;
}

img.avatar-img {
width:120px !important;
height:120px !important;
}

.overlay-leaderboard {
display:none !important;
}
  
.overlay-content {
margin-top:100px !important;
}

.overlay-control-bar {
top:50px !important;
}

div.overlay-side-rail {
margin-top:111px !important;
}
  
.widget_listeningnow {
display: none !important;
}

.user-follow {
display: none !important;
}
  
/* Listening Trends */  
    
.kerve .trendwave_inner .bar, .kerve a.btn_trendnav.current, .kerve .trenduser, .kerve a.btn_trendnav.current, .kerve .trenduser {
background: #48779A !important;
}

/* Ads */

#leader_top, #mpu_top, #mpu_bottom, .related-ads, #leader_bottom, .related-content, .yahoo-search-ads, .yahoo-search-ads iframe, .features-promoted, .mobile_pos_1, .mobile_pos_2, .mobile_pos_3, .mobile_pos_4 {
display: none !important; 
}

 /* Options */
/* Background */
body:not([class*='namespace--dashboard']) #content, body {
  background-image: url(https://s-media-cache-ak0.pinimg.com/originals/58/56/fa/5856fa2b30ff37474d93433dcacf2677.jpg) !important;
  background-attachment: fixed !important;
} 
 
/* Footer Content */
 .footer-wrapper {
  background-color: transparent !important;
}

.footer-wrapper .container {
    background: none !important;
  padding-left: 15px !important;
  padding-right: 15px !important;
} 
  
 /* Cosmetic */

.large-header-layout .header.header--overview {
    background:lightgray;
} 

  /* Hardware Page */
  
.masthead {
    background-color:black;
    background-image: linear-gradient(#000, #000);
    border:none;
    border-bottom:none;
    box-shadow: rgba(0,0,0,0) 0 2px 5px;
    position: relative;
}
  

.lfmRed .site-search .search-box {
    background: #333333;
    border-color: #000;
    color: #fff;
    box-shadow: inset #333333 0 -5px 5px, inset #131313 0 1px 5px, rgb(0, 0, 0) 0 1px 0;
}  
  
  
  }
