css:
@-moz-document domain("forbes.com") {
/*forbes is one hell of a jerk so it's worth it:
 * http://onforb.es/1kIdVZG
 * http://dizzyzane.web.fc2.com/SpeedeeArticles/DizzyZane/Blocking/
 */
 ::before,
 ::after,
 .article-header .article-topper,
 .article-contrib-container,
 .article-article-body .article-text~*,
 .vestpocket[vest-pocket],
 .page-main>:not(#article-page),
 .tweet_quote span::before,
 .tweet_quote span::after,
 #app>:not(#navigation),
 .navigation-container *:not(a) {
  display: none!important;
 }
 .page-main {
  padding-left: 0!important
 }
 img {
  max-width: 50%!important; display: block!important; margin: 0 auto!important;
 }
 .wp-caption-text {
  display: block; text-align: center
 }
 [href*="twitter"][href*="intent"],
 .tweet_quote {
  pointer-events: none; cursor: vertical-text;
 }
 .page-main .article-container.comments-expanded,
 .page-main .article-container.nav-expanded,
 .page-main .article-container.trending-expanded {
  transform: translate(0, 0)!important
 }
 #article-container-spinner {
  position: relative; top: 50vh
 }
 .article-header .article-headline {
  font-size: 1em!important
 }
 h1,
 .page-main .article-article-body .article-body-content {
  margin: 0!important
 }
 .navigation-container .countdown,
 .navigation-container .continue-button {
  position: fixed; top: 1em; left: 1em; display: block; width: calc(100% - 2em); height: calc(100% - 2em);
 }
 .navigation-container .continue-button {
  z-index: 9999!important; opacity: 1!important; visibility: visible!important; display: block!important; color: snow!important; background: #666!important;
 }
}
