css:
/* uso #426395 (the style formerly known as 'uso - less is more')
Feb 27 - update + new screenshots soon */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://userstyles.org") { /* site */
  .ad  { display: none !important; }
  /* site stylesheet */
  .install-status a, .install-status a:visited, .install-status a:active { color: white !important; }
  a:visited:not(:hover)  { color: #224375 ; }/* no over-ride */
  /*.*/
  body  { max-width: none !important;    margin-left: 60px !important;    margin-right: 60px !important; }
  /* header */
  #main-header  { background-color: #7392C1 !important;    color: #fff !important;    border: 6px solid #426395 !important; }
  header#main-header h1 a:visited  { color: #224375 !important; }
  header#main-header h1 a:visited:hover  { color: #fff !important; }
  header#main-header h1 a  {  text-decoration: none !important;    background-color: #7392C1 !important;
    margin-right: -20px !important;    position: relative !important;    z-index: 2 !important;    padding-right: 10px !important; }
  /* general touch-up */
  #content-wrapper  { min-height: 215px !important; }
  ul#subcategory-list  { -moz-column-count: 2 !important; }
  ul#subcategory-list a:not(:hover)  { text-decoration: none !important; }
  /* sidebar */
  #advanced-search-submit > button[onclick] , #show-advanced-search , #search-submit  { display: none !important; }
  #advanced-search  { display: block !important; }
  #advanced-search > select#category-select  { min-width: 120px !important;  max-width: 160px !important; }
  #advanced-search-submit > button[onclick] + input[type][value][name] ,
  #simple-search #search-terms  { width: -moz-available !important; }
  
  /* -- start of wider left sidebar "Easter egg", no change in content --
  -->  remove the double-plus between the '*' (asterisk) and the '/' (slash) to activate the code --> ***forget that***
  !!!-->  the wider left sidebar is now the default, but you can revert by separating the '*' from the '/' here--> */
  #main-header  { padding-left: 15px !important; }
  body  { margin-left: 5px !important; }
  #left-sidebar  { width: 235px !important; }
  #main-article  { margin-left: 245px !important; }
  
  #advanced-search > select#category-select  { min-width: 140px !important;    max-width: 170px !important; }
  #advanced-search  { font-size: xx-small !important; }
  #advanced-search select  { font-size: 11px !important;    color: #0064AA !important; }
  #advanced-search select[id="sort-select"] > option  { padding-right: 2px !important; }
  #advanced-search select[id="sort-select"] + select > option  { padding-right: 3px !important; }
  /* ---- end of wider left sidebar "Easter egg" ---- */
  
  #left-sidebar > nav, #left-sidebar > footer#related  {  background-color: #B4D5FF !important; }
  #left-sidebar > footer#related > ul li:not(.more)  { background-color: #DFEFFF !important; }
  #left-sidebar > footer#related > ul li.more  { margin-top: 2px !important;    border-top-width: 0 !important; }
  #left-sidebar > footer#related > h2  { padding-bottom: 4px !important; }
  #left-sidebar > nav a:not(:hover), #left-sidebar > footer#related a:not(:hover)  { text-decoration: none !important; }
  #left-sidebar > nav a:visited  { color: #224375 !important; }
  /* contact admin */
  /* links to the forum look like the forum buttons
     'contact the admin' doesn't actually link to the forum, but it links to a link to the forum */
  a[href^="http://forum.userstyles.org/messages/add/"] , body > footer > p > a[href="/contact"]
  { text-decoration: none !important;    text-align: center !important;
    cursor: pointer !important;    margin: 0 0 0 5px !important;    font-size: 12px !important;
    font-weight: bold !important;    padding: /*4*/1px 8px 2px !important;    background: #f8f8f8 !important;
    background: linear-gradient(#ffffff,#cccccc) !important;    color: #02475A !important;
    text-shadow: 0 1px 0 #fff !important;    border: solid 1px #999 !important;
    border-radius: 3px !important;    box-shadow: 0px 0px 2px #999 !important; } /*.*/
  body > footer > p > a[href="/contact"]
  { width: 168px !important;    max-width: 168px !important;    display: block !important;    padding: 6px 0 !important; } /*.*/
  a[href^="http://forum.userstyles.org/messages/add/"]:hover , body > footer > p > a[href="/contact"]:hover
  { color: #111 !important;    border: 1px solid #666 !important;    box-shadow: 0px 0px 5px #aaa !important; } /*.*/
  a[href^="http://forum.userstyles.org/messages/add/"]:focus , body > footer > p > a[href="/contact"]:focus
  { background: #eee !important;    box-shadow: 0px 0px 2px #999 !important; } /*.*/
  /* link on the users page is BIGGER than the others */
  #main-article > dl > dd > a[href^="http://forum.userstyles.org/messages/add/"]
  { width: -moz-fit-content !important;    margin: 4px 0 10px !important;    display: block !important;
    font-size: 15px !important;    padding: 8px 18px !important; } /*.*/
  /* mailto: links */
  a[href^="mailto:"] {
    text-decoration: none !important;    padding: 0 2px 1px 21px !important;
    background-color: rgba(115,146,193,0.2) !important;    
    background-image:
