  /* front page */
  #front-page-best  {  background-color: #DFEFFF !important;
    outline: 6px solid #7392C1 !important;    -moz-outline-radius: 5px !important;
    margin-right: 20px !important;    padding-right: 5px !important; }
  #front-page-newest {  background-color: ghostwhite !important;
    outline: 6px solid #7392C1 !important;    -moz-outline-radius: 5px !important; }
  [id^="front-page-"] > h2  { margin-left: 5px !important; }
  [id^="front-page-"] > ul  { padding-left: 25px !important;    margin-right: 5px !important; }
  [id^="front-page-"] > ul > li {
    list-style-position: inside !important;  list-style-type: circle !important;    padding-left: 1px !important;
    line-height: 1.4em !important;    text-overflow: ellipsis !important;    white-space: nowrap !important;    overflow-x: hidden !important; }
  [id^="front-page-"] > ul > li.additional-link  { list-style-type: none !important; }
  [id^="front-page-"] > ul > li.good-rating {
    background-image: linear-gradient(to right, #0f0 8px, transparent 8px) !important;    border-left-color: #0f0 !important; }
  [id^="front-page-"] > ul > li.ok-rating {
    background-image: linear-gradient(to right, #ff0 8px, transparent 8px) !important;    border-left-color: #ff0 !important; }
  [id^="front-page-"] > ul > li.bad-rating {
    background-image: linear-gradient(to right, #f00 8px, transparent 8px) !important;    border-left-color: #f00 !important; }
  [id^="front-page-"] > ul > li > a:not(:hover)  { text-decoration: none !important; }
  [id^="front-page-"] > ul > li.additional-link > a  {  color: #426395 !important;    padding-left: 15px !important;
    font-style: italic !important;    font-weight: bold !important;    font-family: serif !important; }
  [id^="front-page-"]  { position: relative !important; }
  [id^="front-page-"] > ul > li:not(.additional-link):hover:after {
    content:
      "Average rating:  " attr(average-rating) "  ("attr(class)" )" "\00000A"
      "Total install count:  " attr(total-install-count) "\00000A"
      "Weekly install count:  " attr(weekly-install-count);
    position: absolute;    top: 0;    left: 0;    white-space: pre;    font-size: 12px;    line-height: 14px;
    background-color: #ffb !important;    outline: 6px solid #DAA520 !important;    -moz-outline-radius: 5px !important;
    padding: 5px 15px !important;    min-width: 260px;
  }
  /* pagination */
  .pagination  { font-weight: bold !important;    cursor: default !important;    text-align: end !important; }
  .pagination a, .pagination span.disabled, .pagination em.current { 
    padding: 0 5px !important;    color: #426395 !important;    background-color: #D1EEEE !important;
    text-decoration: none !important;    border: 1px solid #426395 !important;    border-radius: 3px !important; }
  .pagination em.current,
  .pagination a:hover  { background-color: #bf7 !important;    color: black !important;    border-color: black !important; }
  .pagination em.current  { background-color: #ff7 !important; }
  .pagination > *  { margin-left: -1px !important;    margin-right: -1px !important; }
  .pagination > *:first-child  { margin-left: 0 !important; }    .pagination > *:last-child  { margin-right: 0 !important; }
  .pagination span.disabled  { opacity: 0.6 !important; }
  /* lightbox */
  #lightbox.lightbox  { top: 20px !important; }
  #lightbox .lb-dataContainer  { background-color: rgba(0,10,20,0.7) !important; }
  #lightbox .lb-prev, #lightbox .lb-next  { border-top: 5px solid #ff9500 !important; }

  /* style info *./
  #info-tables  { /*max-width: 300px*./ position: relative !important; } /*<-still testing.*./
  #info-tables:hover  { z-index: 9 !important; }/*.*/
  #control-panel  { width: -moz-fit-content !important;
    position: relative !important;    bottom: 30px !important;    left: 230px !important;
    z-index: 9 !important;
  }
  /* for authors */
  .style-brief-control-links a , #control-panel a  {
    margin-left: -6px !important;    padding: 2px 8px !important;
    text-decoration: none !important;    color: white !important;    font-weight: bold !important; }
  .style-brief-control-links a:first-child , #control-panel a:first-child  { background-color: #426395 !important; }
  .style-brief-control-links a:nth-child(2) , #control-panel a:nth-child(2)  { background-color: #FF6347 !important; }
  .style-brief-control-links a:last-child , #control-panel a:last-child  { background-color: #489615 !important; }
  .style-brief-control-links a:first-child , #control-panel a:first-child  { margin-left: 2px !important; }
/* ==========================================================================   modify before update */
  /* remove install notification/button from normal flow - smooth the page load */
  #main-style-info > #long-description ~ .install-status {
    position: absolute !important;    right: 60px !important;    max-width: 400px !important;    z-index: 3 !important;
  }
  /* activate the following if you're using Stylish in Firefox - remove unhelpful install notifications (blue box)
  -->  remove the double-plus between the '*' (asterisk) and the '/' (slash) to activate the code -->  *++/
  #main-style-info > #long-description ~
  .install-status:not(#stylish-installed-style-not-installed):not(#stylish-installed-style-installed):not(#stylish-installed-style-needs-update) {
    visibility: collapse !important;
  }/*.*/
/* ========================================================================== */
  /* style page */
  #show-button {
    position: fixed !important;    right: 0 !important;    top: 0 !important;    bottom: 0 !important;
    z-index: 3 !important;    cursor: pointer !important;    text-decoration: none !important;
    margin: 1px 1px 1px 0 !important;    padding: 70px 14px 0 12px !important;    width: 14px !important;
    border: 5px solid #426395 !important;    border-radius: 5px !important;    word-break: break-all !important;
    font-family: consolas !important;    font-size: 26px !important;    font-weight: bold !important;
    text-align: center !important;    color: #fff !important;    background-color: #7392c1 !important;
    /* bg-img: 32x32 original Stylish icon */ }
  #show-button:hover  { background-color: #426395 !important;    z-index: 505 !important; }
  #show-button:after  { content: " . code";  color: #fff;  background-color: #7392c1;  position: relative;  top: -3.7em; }
  #show-button:hover:after  { background-color: #426395; }
  #show-code pre#view-code[style$="block;"] {  background-color: rgba(0,8,22,0.95) !important;
    max-width: none !important;    overflow: auto !important;    max-height: 100% !important;
    position: fixed !important;    z-index: 500 !important;
    top: 5.5em/*72px*/;    bottom: 0;    left: 0;    right: 0;    margin: auto !important;
    padding-left: 12px !important;    border-left: solid 12px #ffcb00 !important; }
  #show-code pre#view-code code#stylish-code  {  color: #f5f5f5 !important;
    font-size: 13px !important;    font-weight: 500 !important;    font-family: Consolas, monospace !important; }
  #show-code *::-moz-selection  { background: #ffcb00 !important;    color: #000 !important; }
  IMG[src^="/images/"][class$="-average-rating"][alt] {
    padding: 7px 15px !important;    width: 0 !important;
    position: relative !important;    top: 2px !important;
    border: 2px solid #012345 !important;    border-radius: 4px !important; }
  IMG[src^="/images/good."][class^="good-"][alt="Good"]  { background-color: #0d0 !important; }
  IMG[src^="/images/bad."][class^="bad-"][alt="Bad"]  { background-color: #f11 !important; }
  IMG[src^="/images/ok."][class^="ok-"][alt="OK"]  { background-color: #fe0 !important; }
  /* style-author info, feedback */
  #info-tables  { padding-bottom: 3px !important; }
  #additional-info-text:not(:empty) { min-height: 2em !important; }
  #additional-info-text { border: 1px dotted transparent !important;    padding: 0 5px 5px !important; }
  #additional-info-text:hover { border-color: #666 !important; }
  #discussions-area, #style-info #info-tables  {  background-color: #DFEFFF !important;    width: -moz-fit-content !important;
    border: 6px solid #7392C1 !important;    border-radius: 5px !important;    padding-right: 20px !important; }
  #style-info  { margin-bottom: 10px !important; }
  #discussions-area > h2  { padding-left: 5px !important; }
  ul#discussions li {  font-family: serif !important;
    font-style: italic !important;    font-weight: bold !important;    color: #222 !important; }
  ul#discussions li a  { font-family: Verdana !important;    font-style: normal !important;    font-weight: normal !important;
    max-width: 750px !important;    text-overflow: ellipsis !important;    white-space: nowrap !important;    overflow-x: hidden !important; }
  ul#discussions  { padding-left: 5px !important; }
  ul#discussions li  { list-style-image: none !important;    list-style-type: none !important; }
  ul#discussions li:not(:hover), ul#discussions li a:not(:hover)  { text-decoration: none !important; }
  ul#discussions li:before  { content: "â—";    color: #288be3;    font-size: 20px;    line-height: 15px !important;
    padding-right: 5px;    position: relative;    top: 1px; }
  ul#discussions li.good-rating:before  { color: #0c0; }
  ul#discussions li.ok-rating:before  { color: #ec0; }
  ul#discussions li.bad-rating:before  { color: #f11; }
  ul#discussions li:not([class]):last-child a {
    font-family: serif !important;    font-style: italic !important;    font-weight: bold !important; }
  ul#discussions li:not([class]):last-child:before  { color: transparent !important; }
  /* use opacity:0; instead? */
  #social iframe, #social *  { display: none !important; }    #social  { height: 0 !important; }
  /* style page and styles browse listing */
  /* styles browse */
  article.style-brief  {  background-color: #D5FCBB !important;
    outline: 6px solid #8dbc7b !important;    -moz-outline-radius: 5px !important;    position: relative !important; }
  article.style-brief:hover:after {
    content: "Total installs: " attr(total-install-count) " | Avg. rating: " attr(average-rating);
    position: absolute;    bottom: 0;    left: 0;    white-space: pre;    font-size: 12px;    line-height: 14px;
    background-color: #ffb !important;  outline: 6px solid #DAA520 !important;  -moz-outline-radius: 5px !important;
    padding: 5px 10px !important;    pointer-events: none; }
  /* use if you want *++/
  article.style-brief:hover:before {
    content: "user " attr(author-id);
    position: absolute;    bottom: 0;    right: 0;    white-space: pre;    font-size: 12px;    line-height: 14px;
    background-color: #ffb !important;  outline: 6px solid #DAA520 !important;  -moz-outline-radius: 5px !important;
    padding: 1px 4px 2px !important;    pointer-events: none; }/* ---- user ###### ---- */
  .style-brief-text  { background-color: transparent !important; }
  .style-brief-text > p  { max-width: none !important;    overflow-x: hidden !important;    margin-right: 20px !important; }
  .style-brief-stats  { background-color: #fffbff !important;  min-width: 36em !important; }
  .style-brief-stats span {  min-width: 12em !important;    padding-right: 8px !important;
    padding-top: 2px !important;   padding-bottom: 3px !important;    padding-left: 8px !important; }
  .style-brief-stats span:first-child  { background-color: #A3C2F1 !important; }
  .style-brief-stats span:nth-child(2)  { background-color: #93d54b !important;    margin-left: -4px !important; }
  .style-brief-stats span:last-child  { background-color: transparent !important;    margin-left: -4px !important; }
  .style-brief-stats span:last-child img  { display: none !important; }
  .style-brief.good-rating .style-brief-stats  { background-color: #0f0 !important; }
  .style-brief.ok-rating .style-brief-stats  { background-color: #ff0 !important; }
  .style-brief.bad-rating .style-brief-stats  { background-color: #f00 !important; }
  .style-brief.bad-rating .style-brief-stats span:last-child  {  color: #fff !important; }
  .style-brief.good-rating .style-brief-stats span:last-child:after  { content: "Good"; }
  .style-brief.ok-rating .style-brief-stats span:last-child:after  { content: "OK"; }
  .style-brief.bad-rating .style-brief-stats span:last-child:after  { content: "Bad"; color: #fff; }
}

@-moz-document url-prefix("https://userstyles.org/users/") { /* authors */
  #main-article > ul:not([id]) {
    list-style: none !important;    background: #B4D5FF !important;
    padding: 6px 8px;    margin: 0 !important;    display: block;    border-radius: 5px;
  }
  #main-article > ul:not([id]) li  { display: inline-block !important; }
  #main-article > ul:not([id]) a {
    text-decoration: none !important;    font-weight: bold;    line-height: 3.4;
    padding: 5px 10px;    margin: 5px;    border: solid 4px #426395;    border-radius: 5px;
    background: #7392C1;    color: #fff;    display: inline;    white-space: nowrap;
  }
  #main-article > ul:not([id]) a:hover  { background: #426395; }
  /* AMO link - for those who have one */
  a[href^="https://addons.mozilla.org/en-US/firefox/user/"] {
    color: #fff !important;    font-weight: bold !important;    font-size: 14px !important;
    font-family: Trebuchet MS !important;    text-decoration: none !important;    padding: 2px 6px !important;
    background: #5784BF;    border-radius: 0;    box-shadow: none;    border: 0px none;
  }
  a[href^="https://addons.mozilla.org/en-US/firefox/user/"]:hover {
    box-shadow: 0px 0px 100px rgba(255, 255, 255, 0.2) inset;
  }
  /* Thanks to my bro Glenn for the table */
  #main-article > dl > dt  { font-weight: bold !important;    font-style: italic !important; }
  /*.*/
  table.author-styles td:last-child  { min-width: 160px !important; }
  table.author-styles td.date-value  { max-width: 76px !important; }
  table.author-styles td:last-child > a[href*="/styles/"]  {  position: relative !important;    top: -1px !important;
    text-decoration: none !important;    color: #fff !important;    font-weight: bold !important;
    padding-left: 4px !important;    padding-right: 4px !important;    padding-bottom: 1px !important;
  }
  table.author-styles td:last-child > a[href*="/edit"]  { background-color: #426395 !important;    margin-left: -6px !important; }
  table.author-styles td:last-child > a[href*="/delete"] {
    background-color: #FF6347 !important;    margin-left: -3px;    margin-right: -3px; }
  table.author-styles td:last-child > a[href*="/stats"]  { background-color: #489615 !important; }
  table.author-styles tbody tr:nth-child(odd) td  { background-color: #e7eff7 !important; }
  table.author-styles tbody tr td:nth-child(even) , table.author-styles tr th:nth-child(even) {
    background-color: #d7dfe7 !important; }
  table.author-styles tbody tr:nth-child(odd) td:last-child  { background-color: transparent !important; }
  table.author-styles td a  { text-decoration: none !important; }
  table.author-styles td:first-child a  { display: block !important; }
  table.author-styles td:first-child a:visited:not(:hover)  { color: #247 !important; }
  table.author-styles td:first-child a:visited:hover  { color: #5273a5 !important; }
  table.author-styles td.obsolete a  { font-style: italic !important;    color: #ff6347 !important; }
  table.author-styles td.obsolete:first-child a:visited  { color: #ff6347 !important; }
  table.author-styles td.obsolete:first-child a:hover  { color: #800 !important; }
  table.author-styles td,  article .author-styles th  { padding-left: 6px !important;    padding-right: 6px !important; }
  table.author-styles thead th  { width: -moz-min-content !important; }
  
  table.author-styles tbody tr:hover td:not(:last-child)  { background-color: wheat !important; }
  table.author-styles tbody tr:hover td.obsolete:first-child a  { color: #800 !important; }
  table.author-styles tbody tr td:not(.obsolete):first-child a:hover  { color: #224375 !important; }
  /* style warnings */
  img.style-warning-icon {
    width: 16px !important;    height: 16px !important;
    background-color: rgba(0,0,0,0.8) !important;
    padding: 1px !important;
  }
  tr[id^="style-warning"].style-warnings td { padding: 0 !important; }
  tr[id^="style-warning"].style-warnings td div { padding: 0 8px !important; }
  tr[id^="style-warning"].style-warnings + tr:empty { display: none !important; }
}
/* point to yourself? going nowhere fast */
@-moz-document url("https://userstyles.org/contact")  { #left-sidebar + footer  { display: none !important; } }


/* generally, this stuff is only used by authors of styles */
@-moz-document regexp("https?://userstyles.org/styles/(new|create|update|\\d+/edit)") {
  /* uso edit page */
  body  { background-color: #CFE7FF/*dfefff*/ !important; }
  /* uso Edit page source editor + Stylish/Scratchpad (CodeMirror) - for my setup & preferences */  
  .CodeMirror-scrollbar-filler { background-color: black /* dflt is 'white' */ !important; }
  .cm-s-default .cm-comment /* dflt is 'color: #a50;' *+/ ,
  .CodeMirror.cm-s-mozilla span.cm-comment /* Scratchpad & Stylish editor */ {
    color: #070;    background-color: rgba(187,221,170,0.4);    font-weight: 600; }
}

/* uso edit details and more pages - blue */
@-moz-document regexp("https?://userstyles.org/users/\\d+/edit") ,
    url-prefix("https://userstyles.org/users/edit_login_methods/") ,
    url-prefix("https://userstyles.org/help/widget") {
  body { background-color: #dfefff !important; }
}

@-moz-document url-prefix("https://userstyles.org/styles/delete/") {
  /* uso delete page - red-ish (uso deleted bg color) */
  body  { background-color: #FF6347 !important; }
  #main-article  { background-color: #ffb387 !important;    padding: 10px !important;    border-radius: 5px !important; }
}

@-moz-document url-prefix("https://userstyles.org/styles/stats/") {
  /* uso stats page - green */
  body { background-color: #d5fcbb !important; }
  #install-stats-chart-container { background-color: #fff !important;    border: 1px solid #6D9C5B !important; }
}

