css:
@-moz-document domain("habitica.com") {
/*------------------------------ SITE HEADER (MENUS & AVATAR) ------------------------------ */
  


    
    /* we remove the space between buttons */
    .toolbar-options > li:not(:first-of-type) {
      margin-left: 0;
    }
    .toolbar-nav > li {
      margin-right: 1;
    }
  
    /* the wrapper height has to reflect the toolbar height */
    .header-wrap {
      padding-top: 28px;
      border-bottom: 1px solid #4B0082;
                                            
    }

    
    /* we update the background color of the avatar on hover */
    .herobox.noBackgroundImage:hover {
      background-color: #8A2BE2
                                                                }
      .herobox.noBackgroundImage {        
       background-color: #4B0082
    }
   

    /* see http://benkaminski.com/2014/08/30/how-to-customize-bootstrap-3-popovers-individually-using-only-css/ for more details about bootstrap popover styling */
    .popover {
      border-radius: 5;
      border-color: #9ea9ab;
      -webkit-box-shadow: none !important;
              box-shadow: none !important;
    }
    
    /* we remove the top arrow then remove the top margin so the popover appears like the other submenus */
    .popover.bottom > .arrow, .popover.bottom > .arrow:after { display: none !important; }

    .popover.bottom{
      margin-left: -30px !important; /* blob to display it completely now that the button is fully to the right */
      margin-top: 0; 
}
/*ICONS*/

.shop_gold {
    background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/gold_coin.imageset/header_coin@1x.png?raw=true) !important;
    background-position: 0px 0px !important;
    background-repeat: no-repeat !important;
    margin-left: 5px;
    margin-right: -5px;
    margin-top: -1px !important;
}
.shop_silver {
    background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/silver_coin.imageset/header_coin_silver@1x.png?raw=true) !important;
    background-position: 0px 0px !important;
    background-repeat: no-repeat !important;
    margin-left: 5px;
    margin-right: -5px;
    margin-top: -1px !important;
}
.Pet_Currency_Gem1x:not(#foo) {
    background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/Gem.imageset/header_gem@2x.png?raw=true);
    width: 24px;
    height: 24px;
    margin: 0px;
    margin-top: 0px !important;
    background-position: 0 0;
}
.Pet_Currency_Gem2x:not(#foo) {
    background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/Gem.imageset/header_gem@1x.png?raw=true);
    width: 19px;
    height: 13px;
    margin-top: 3px;
    background-position: 0 0;
}
/*------------------------------ SHOW/HIDE ITEMS ------------------------------ */
/* Hide heart */


.glyphicon-heart {
    display: none !important;
}
.glyphicon-star {
    display: none !important;
}
.glyphicon-fire {
    display: none !important;
}
.glyphicon-signal {
    display: none !important;
}
/* hide footer */

.footer {
    display: none !important;
}
/* hide tasks 'move to top' icon */

.glyphicon-open {
    display: none !important;
}
/* hide tasks 'task notes' icon */

.task-notes {
    display: none !important;
}
/* hide task filters */

.task-filter {
    display: none !important;
}
/* hide tags */
/*.filters, 
    .task-column:not(.rewards) .task-options > form > fieldset:nth-last-of-type(1), .task-column.rewards .rewards.main-list .task-options fieldset:nth-last-of-type(1) { display: none !important; }*/
/* NAVBAR */


.toolbar-nav {
    padding: 0 .818em !important;
}
/* buttons */

nav.toolbar li.toolbar-button a,
nav.toolbar li.toolbar-button-dropdown,
nav.toolbar li.toolbar-button-dropdown a,
nav.toolbar ul.toolbar-options a,
nav.toolbar .toolbar-button-dropdown.highlight,
nav.toolbar .toolbar-button-dropdown.highlight a,
.toolbar-nav .toolbar-button-dropdown.highlight:hover {
    background: none !important;
    border: none !important;
}
.toolbar-nav .toolbar-button-dropdown > a:nth-of-type(2) {
    border: none !important;
}
.toolbar-nav .toolbar-button-dropdown.highlight>a:nth-of-type(2) {
    border: none !important;
}
nav.toolbar .toolbar-subscribe {
    display: none !important;
}
/* HEADER */

.header-wrap {
    border: none !important;
    padding: 55px 0 0 !important;

    /* hides scrollbars */
}
.site-header {
    margin-bottom: -16px !important;
}
.sticky-wrapper {
    height: 135px !important;
}
.herobox {

    margin-top: -26px !important;
    outline: none !important;
}
/* bars */

.hero-stats {
    border: none !important;
    background: none !important;
    min-width: 300px !important;
    padding: 0 2.5em 1.5em !important;
}
.hero-stats .meter-label {
    
    margin-right: 0;
    /* default: 0.618em*/
    
    border-top-left-radius: 1.3em !important;
    border-bottom-left-radius: 1.3em !important;
    outline: 0;
    height: 2.31em;
    width: 3em;
    padding: 0.5em 0 0.5em 0.4em;
    }
    .hero-stats .meter-label[tooltip='Health'], .hero-stats .meter-label[tooltip='Boss Health'] {
    background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/icon_health.imageset/header_heart@2x.png?raw=true);
    background-repeat: no-repeat;
    background-position: 3px;                             
	border: none;
    background-color: #7D26CD !important;
    background-color: Transparent !important;
}
  
    .hero-stats .meter-label[tooltip='Experience'] {
background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/icon_experience.imageset/header_exp@2x.png?raw=true);
    background-repeat: no-repeat;
    background-position: 3px;
	border: none;
    background-color: Transparent !important;
}
.hero-stats .meter-label[tooltip='Mana'] {
background-image: url(https://github.com/HabitRPG/habitrpg-ios/blob/master/HabitRPG/Images.xcassets/icon_magic.imageset/header_magic@2x.png?raw=true);
    background-repeat: no-repeat;
    background-position: 6px;
	border: none;
    background-color: Transparent !important;
}
.hero-stats .meter {
    outline: 0;
    height: 1.85em;
    /* default: 1.618em; */
    
    border-top-right-radius: 1.3em !important;
    border-bottom-right-radius: 1.3em !important;
}
.hero-stats .meter-text.value {
    right: 0.75em;
}
.meter-text .glyphicon {
    display: none !important;
}
.meter {
    font-weight: 150 !important;
    color: #BDA0CB !important;
    background: #4B0082 !important;
    border: none !important;
    /*   outline: none !important; MAKES IT HARD TO SEE BOSS PROGRESS*/
    
    margin: 0 0 4px !important;
}
/* TAGS */



.filters .filters-controls,
.filters .filters-edit,
.filters .filters-tags,
.filters .filters-search {
    margin-right: 1px;
    margin-bottom: 4px;
}
.filters {
    margin: 0 !important;
}
.pull-left {
    display: none !important;
}
/* hide "Tags:" */

.nav.nav-pills .glyphicon {
    opacity: 0.33 !important;
}
.nav > li > a {
    padding: 0px 10px 4px !important;
}
/* TASKS */

habitrpg-tasks {
    font-size: 12px !important;
}
.task-text {
    color: rgba(0, 0, 0, 0.75) !important;
}
/* task columns */

.tasks-lists .row .col-md-3 {
    padding: 0 4px !important;
}
.task-column {
    padding: 0 !important;
    border: none !important;
    background: none !important;
}
/* make rewards column skinnier */

.col-md-3:not([class="rewards-module"]) {
    width: 27% !important;
}
.col-md-3.rewards-module {
    width: 19% !important;
}
h2.task-column_title {
    font-size: 1.4em !important;
    text-align: center !important;
    font-weight: 400 !important;
    line-height: 0.75 !important;
    color: white !important;
}
/*bring the add box closer to the title */

.task-add {
    margin-top: 1em;
    /* default: 1.5em */
}
/*bring the task filter closer to the add box */

.task-filter {
    margin-top: 0;
    /* default 1.5 em */
    
    margin-bottom: 0;
    /* default: 1em; use .main-list instead so it still loks good when filters aren't displayed */
}
.main-list {
    margin-top: 0em;
}
/* bring the 'add multipl' button closer to the input bar */

.help-block.btn-link {
    margin-top: 2px;
    /* default: 5px */
}
/* New Task boxes */

input[type="text"]:not(#foo) {
    background-color: #7D26CD !important;
    color: #009999 !important;
    border: none !important;
}
.task-add button:not(#foo) {
    background-color: #4B0082 !important;
    border: none !important;
}
/*task filter */

.task-filter li>a:not(#foo),
.task-filter li>button:not(#foo),
.task-filter li>input:not(#foo) {
    color: #a6a6a6 !important;
    background-color: #FFFFFF !important;
}
.task-filter li.active a:not(#foo) {
    color: white !important;
    background: black !important;
    box-shadow: none !important;
}
/* Task body */

.task {
    border: none !important;
    min-height: unset !important;
    margin: 0 0 2px 0 !important;
    /*  clear: unset !important;  BREAKS EDIT CHALLENGE */
}
.task-meta-controls {
    margin: 5px 2px 0 0 !important;
}
/* Daily & To-do checkboxes */

.daily .task-checker label,
.todo .task-checker label {
    width: 24px !important;
}
.task-meta-controls a.badge {
    width: 0px !important;
}
/* inner box */

.action-yesno label:after {
    margin: 7px auto !important;
}
.action-yesno label:before {
    /* fixes weird clickable area */
    
    width: auto !important;
}
/* checkmark */

.action-yesno label:hover:after,
.action-yesno label:focus:after,
.action-yesno input[type="checkbox"]:checked + label:after {
    font-size: 1.1em !important;
    height: auto !important;
    margin: 5px 0 0 0 !important;
    width: 24px !important;
    content: "âœ”" !important;
    transform: none !important;
}
.dailys .task-text,
.todos .task-text {
    padding: 3px 0 3px 30px !important;
}
/* lists */

.task-text .checkbox {
    margin: 0 !important;
    min-height: unset !important;
}
/* HABITS */

.habits .task-actions,
.task-checker label {
    width: 2.5em;
    /*default: 3em */
}
.habits .task-text {
    padding: 3px 0 3px 38px !important;
}
/* REWARDS */

.rewards .task-text {
    padding: 3px 0 3px 58px !important;
}
.rewards .btn-buy span {
    left: 3px;
}
.rewards {
    margin-bottom: 3px !important;
    padding-bottom: 3px !important;
}
.items.rewards .task {
    min-height: 40px !important;
}
.items.rewards .task-text {
    padding: 10px 0 0px calc(2.5em + 40px) !important;
}
.btn-buy {
    min-height: unset !important;
    padding: 4px 12px 0px 0px !important;
    text-align: right !important;
    vertical-align: middle !important;
}
/* hide gold coin */

.btn-buy .shop_gold {
    display: none !important;
}
/* MISC */

footer {
    display: none !important;
}
.current-pet {
    bottom: -2px !important;
}
/* hide the always empty items rewards ul of all columns except for rewards */

.task-column:not(.rewards) .items.rewards,
.task-column.rewards .items.rewards:not(.ng-scope) {
    display: none !important;
}

.task > div:first-of-type > div:first-of-type > div:first-of-type > div:first-of-type {
    top: -15px !important;
    left: 10px !important;
}
/* POP-UP NOTIFICATIONS */

.ui-pnotify-shadow {
    box-shadow: none !important;
}
.ui-pnotify-container {
    padding: 0.25em 0.5em !important;
    outline: 1px solid rgba(0, 0, 0, 0.1) !important;
}
/* CHAT TWEAKS */
/* removes scrollbars */

.tavern-chat li .scrollable-message,
.party-chat li .scrollable-message {
    max-height: unset !important;
    overflow-y: unset !important;
}
/*fixes chat buttons*/

.chat-buttons>input,
.chat-buttons>button {

    background-color: white
                                
}
/* a span.glyphicon {
   color: pink;
 } */
/* STILL NEEDS WORK */
/*     h2.task-column_title {display: none !important;} */
/* CONTROLLING COLORS TO GO WITH THE BACKGROUNDS */
/* color of toolbar text and buttons */

nav.toolbar li.toolbar-button a,
nav.toolbar li.toolbar-button-dropdown,
nav.toolbar li.toolbar-button-dropdown a,
nav.toolbar ul.toolbar-options a,
nav.toolbar .toolbar-button-dropdown.highlight,
nav.toolbar .toolbar-button-dropdown.highlight a,
.toolbar-nav .toolbar-button-dropdown.highlight:hover,
.toolbar-currency.gold,
.toolbar-currency.silver,
.gem-wallet,
.toolbar .glyphicon {
    color: grey
    /* Other ptions include: grey, rgba(0,0,0,0.4) */
    
    !important;
}
/* color of notifications background (above text color applies to notifications text so this may need to be adjusted depending on what color you used) */

.toolbar-notifs div {
    background-color: white !important;
}
/* color of upper left buttons on mouse hover */

.toolbar-nav .toolbar-button > a:hover,
.toolbar-nav .toolbar-button button:hover,
.toolbar-nav .toolbar-button-dropdown > a:hover,
.toolbar-nav .toolbar-button-dropdown button:hover,
.toolbar-nav .toolbar-button-dropdown.highlight > a:hover,
.toolbar-nav .toolbar-button-dropdown.highlight button:hover {
                                            
    /* Other options include: tan, rgba(0,0,0,0.1) */
    
}
/* color of text in stat bars */
/* .meter { 
     color: rgba(0,0,0,0.5) !important;
} */
/*BACKGROUNDS*/

.header-wrap {
    background-repeat: repeat;
    box-shadow: 5px 0px 15px;
    background:	#8A2BE2;
     background: 8A2BE2 !important;
    background-size: contain
    /* COVER VS. CONTAIN: WHICH ONE YOU NEED DEPENDS ON YOUR SCREEN SIZE AND WHICH BACKGROUND YOU PICKED. TRY IT BOTH WAYS BEFORE YOU GIVE UP ON A BACKGROUND. MANY BACKGROUNDS LOOK COMPLETELY DIFFERENT WITH ONE SETTING COMPARED TO THE OTHER ONE. */
       }
}
