css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("online.sberbank.ru") {

  body {background:#fff; }
  
  * {border-radius: 0 !important; box-shadow: none !important; text-shadow: none !important;} 
  .b-social, .b-apps, .bunnerArea, .currencyRate ,.greenGradient, .buttonSelectLeft, .buttonSelectRight, .mainPageNews, .footerRound, .aroundTheClock, #linkConteiner ,#mobileBanner .greenBannerArea, #avatarIconEl img,   .Logo a img, .b-news , img.newGroup {display:none !important; }
  .infMesOrange, #warnings .orange, .workspace-box.orange, .messageBlock .orange, .orangeBlock, .b-field .field_select, .b-field .field_input {border:0;}
  .productListOperation .buttonSelectCenter, .hoarRCL, .hoarRCR, .hoarRTC, .hoarRTR, .hoarRTL, .hoarRBC, .hoarRBR, .hoarRBL,.productListOperation .buttonSelectCenter ,.personalMenuWithTitleItem ul.underlined li .greenTitle , .grayGradient {background-image:none;}
  
  
  #header {border-top:3px solid #ddd;}
  .hdrContainer {margin-top:10px;}
  #avatarIconEl .relative:after {content:'☠'; font-size:3em; position:relative; top:-35px; left:-5px;} 
  .Logo a:after   {display:block; content:''; width:146px; height:42px; background-image: url("data:image/jpeg;base64,");}
  .PhoneNambers {opacity:.5;}
  
  #loginForm > input:nth-child(1)  {background:#b41515 !important; cursor:pointer; color:#fff !important;}

  #menu td { border:0;}
  #menu .active {background:#f5ad24; border:0;}
  #menu td:hover {border:0;}
  #menu td a {text-shadow: none;}
  #menu td #dropDownMenu td a  {background:#eee ; border:0; border-radius: 0;}
  
  .personalMenu {padding:20px; background:#eee ;}
  .personalMenuWithTitleItem ul.underlined .linksListTitle {width:inherit;}

  .buttonSelectCenter { background:#f6f6f6; text-decoration: underline;}
  .moreListOfOperation {background:#f6f6f6; border:1px solid #999;}
  table.productOperationList td a:hover {background:#f59c24}
   .footerBorder, .mobileBannerBCTop, #mobileBanner .operators, .mobileBannerBC {background:#fff;}
  div.operator:nth-child(1), div.operator:nth-child(2), div.operator:nth-child(3), div.operator:nth-child(4), div.operator:nth-child(5), div.operator:nth-child(6) {opacity:.1;transition: opacity .4s ease-in-out;}
    div.operator:nth-child(1):hover, div.operator:nth-child(2):hover, div.operator:nth-child(3):hover, div.operator:nth-child(4):hover, div.operator:nth-child(5):hover, div.operator:nth-child(6):hover {opacity:1;transition: opacity .3s ease-in-out;}



  
  .feedback {position:absolute; margin:0 auto; top:16px;  width:950px; z-index:10;}
  .feedback DIV {float:right;}
  .feedback DIV .inlineBlock {font-size:.9em; opacity:.8;}
  
 
  
  
  
  
  
  
}
