css:
@-moz-document domain("oglobo.globo.com") {
div.comentarios {
    display: none !important;
}
}

@-moz-document domain("g1.globo.com") {
.widget-comentarios, .box-comentarios, .glbComentarios {
    display: none !important;
}
}

@-moz-document domain("folha.uol.com.br") {
section#article-comments {
    display: none !important
}
}

@-moz-document domain("estadao.com.br") {
div#wrap-comments {
    display: none
}
}

@-moz-document domain("noticias.uol.com.br") {
div#comentarios {
    display: none !important;
}
}

@-moz-document domain("abril.com.br") {
div.abril-comentarios-widget {
    display: none !important;
}
}

@-moz-document domain("yahoo.com") {
button.view-comments {
    display: block !important;
}
div#Col1-2-Comments {
    display: none !important;
}
}

@-moz-document domain("ig.com.br") {
div.tab {
    display: none;
}
div.tab[data-name=comentarios] {
    display: none !important;
}
.comentarios-social {
    display: none;
}
}

@-moz-document domain("terra.com.br") {
.iconComment,
.commentLabel {
    display: block !important;
}
}

@-moz-document domain("terra.com.br"), domain("cartacapital.com.br") {
/*DISCUS*/

.btn-social-comment,
div.social-comments,
div#disqus_thread,
div.container-disqus {
    display: none !important;
}
}
