css:
@-moz-document domain("news.ycombinator.com") {
  body {
    font-size: 10pt !important;
  }

  body > center > table,
  #layout {
    width: auto;
    max-width: 700px !important;
  }

  td[bgcolor="#ff6600"],
  #layout-header, .toolbar-wrapper {
    border-radius: 4px;
    background-color: #efefef !important;
  }

  .hn_search_toolbar {
    margin: 4px;
  }

  .toolbar-logo-ycombinator {
    background-color: #F60;
  }

  .content-pagination a {
    font-size: 1em !important;
  }

  table[bgcolor="#f6f6ef"],
  #layout-body,
  #layout-footer {
    background-color: transparent !important;
  }

  a[href="news"] {
    font-weight: 100 !important;
  }

  table[bgcolor="#f6f6ef"] table:first-child {
    padding: 5px !important;
    width: 100% !important;
  }

  table[bgcolor="#f6f6ef"] table table {
    border-left: solid 1px #efefef !important;
  }

  body > center > table td.title,
  body > center > table center {
    padding: 10px 0 5px !important;
  }

  .topSel a {
    color: inherit !important;
    text-decoration: underline !important;
  }

  body > center > table > tbody > tr:nth-child(2) {
    display: none;
  }

  form[action="/r"] textarea[name="text"] {
    width: 100% !important;
  }

  .subtext {
    color: #828282 !important;
  }

  .default p {
    text-align: justify !important;
  }

  td,
  .comment font {
    color: #111 !important;
    line-height: 17px !important;
  }

  a[href="user?id=pg"] {
    color: darkOrange !important;
  }

  .op {
    color: blue !important;
  }

  td[valign="top"] {
    width: 10px;
  }
}
