css:
@-moz-document domain("convert2mp3.net") {
  
  :root {
    --bg1: #111;
    --bg2: #151515;
    --bg3: #1c1c1c;
    --bg4: #222;
    --bg5: #252525;
    --bg6: #2c2c2c;
    
    --orange: #dd6807;
    --orangeHover: #ff7400;
    
    --green: #4CAF50;
    --greenHover: #66BB6A;
  }
  
  * {
    border-radius: 0 !important;
    outline-width: 0 !important;
  }
  body, html {
    background: var(--bg1) !important;
  }
  p, h1, h2, h3, span, body, b, strong {
    color: #aaa !important;
  }
  .carousel-inner, .span3, footer, .span9 p:nth-child(7), .span9 p:nth-child(8), .span4, .span9 b:nth-child(9), iframe {
    display: none !important;
  }
  .container .span9 {
    margin-top: 100px;
    width: 100% !important;
    color: transparent;
  }
  .navbar-wrapper {
    position: absolute;
    opacity: .05;
    transition: 0.4s ease;
    -webkit-transition: 0.4s ease;
  }
  .navbar-wrapper:hover {
    opacity: 1;
  }
  input[type="text"], select {
    background: var(--bg3) !important;
    border-color: var(--bg3) !important;
    transition: 0.4s ease;
    -webkit-transition: 0.4s ease;
    color: #aaa;
  }
  input[type="text"]:focus, select:focus {
    box-shadow: 0 0 5px var(--orange);
    background: var(--bg4);
    border-color: var(--bg4);
    color: #fff;
  }
  .btn {
    background: var(--bg3);
    text-shadow: none;
    color: #aaa;
    box-shadow: none;
    border-color: var(--bg3);
  }
  .btn:hover, .btn-group.open .btn.dropdown-toggle {
    background: var(--bg4) !important;
    border-color: var(--bg4) !important;
    color: #fff;
  }
  .btn:focus {
    background: var(--bg4);
    color: #fff;
  }
  .caret {
    border-top-color: #aaa;
  }
  .btn.orange {
    background: var(--orange) !important;
    border-color: var(--orange) !important;
  }
  .btn.orange:hover {
    background: var(--orangeHover) !important;
    border-color: var(--orangeHover) !important;
  }
  .dropdown-menu {
    background: var(--bg2);
  }
  .dropdown-menu .divider {
    background: var(--bg4) !important;
    border-color: var(--bg4) !important;
  }
  .bootstrap-select.btn-group .dropdown-menu li > a:hover span {
    color: #fff !important;
  }
  .btn-success {
    background: var(--green) !important;
    color: #fff !important;
  }
  .btn-success:hover {
    background: var(--greenHover) !important;
  }
  .alert-success {
    color: var(--green) !important;
    background: var(--bg4) !important;
    border-color: var(--bg4) !important;
    text-shadow: none !important;
  }
  .span7 {
    color: #aaa !important;
  }
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
}
