css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("www.neopets.com") {
/*
  * Theme-Specific Style Sheet
  *
  * Theme: 8
  * 	Curse of Maraqua
  * 	yup
*/


/********************************************************************/
/* HTML Tag Styles **************************************************/
/********************************************************************/

	BODY {
		background-color: #999999;
	}

	A:Link, A:Visited {
		color: #4F6C95;
	}

	A:Hover {
		color: #4B9292;
	}

/********************************************************************/
/* Foundation IDs ***************************************************/
/********************************************************************/

	#main, .main_theme {
		border-left: 2px solid #747474;
		border-right: 2px solid #747474;
	}

	#header {
		background-color: #275881;
		background-image: url("http://images.neopets.com/themes/008_com_e529a/header_bg.png");
	}

	#footer {
		background-color: #275881;
		background-image: url("http://images.neopets.com/themes/008_com_e529a/footer_bg.png");
	}


/********************************************************************/
/* Header Stuff *****************************************************/
/********************************************************************/

/* Navigation.  Duh.  */
	ul.dropdown {
		border: 1px solid #000000;
		background-color: #4F6C95;
	}

	LI:hover UL.dropdown A, LI.over UL.dropdown A {
		color: #000000;
	}

	LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {
		color: #C6CFE1;
	}

/* Event Icons */
	.eventIcon, .user {
		color: #FFFFFF;
	}

	.eventIcon A:Link, .eventIcon A:Hover, .eventIcon A:Visited,
	.user A:Link, .user A:Hover, .user A:Visited {
		color: #9AB9D7;
	}

/* Clock */
	#nst {
		color: #000000;
	}


/********************************************************************/
/* Sidebar Classes **************************************************/
/********************************************************************/

	.sidebarModule {
		border: 2px solid #E4E4E4;
	}

	.sidebarTable {
		border: 2px solid #C9C9C9;
	}

	.sidebarHeader {
		background-color: #4F6C95;
		border-bottom: 2px solid #C9C9C9;
	}

	.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited {
		color: #000000;
	}


/********************************************************************/
/* Content Classes **************************************************/
/********************************************************************/

/* We need a few styles to make a module work */
	.contentModuleHeader {
		background-color: #000000;
		color: #FFFFFF;
	}

	.contentModuleHeader A:Link, .contentModuleHeader A:Hover, .contentModuleHeader A:Visited {
		color: #C5C5C5;
	}

	.moreLink {
		border: 1px solid #8F8F8F;
	}

	.contentModuleHeaderAlt {
		background-color: #2F3E5B;
		color: #E6E9F0;
	}

	.contentModuleHeaderAlt A:Link, .contentModuleHeaderAlt A:Hover, .contentModuleHeaderAlt A:Visited {
		color: #C6CFE1;
	}

	.moreLinkAlt {
		border: 1px solid #536FA2;
	}

/********************************************************************/
/*  Rounded Module Classes ******************************************/
/********************************************************************/
	/* styles for dark colored rounded modules in gamesroom */
	.rcModuleHeader, .rcModuleHeader a, .rcModuleHeader a:hover, .rcModuleHeader a:visited, .rcModuleHeader a:link {
		color: #FFFFFF;
	}

	.gamesRoomDarkModule .rcModuleContent {
		border-color: #2F3E5B;
	}

	.gamesRoomDarkModule .rcModuleContentOuter {
		border-color: #CDD1D8;
	}

	.gamesRoomDarkModule .rcModuleHeader, .gamesRoomDarkModule .rcModuleTopLeft,
	.gamesRoomDarkModule .rcModuleTopRight {
		background-color: #2F3E5B;
	}

	.gamesRoomDarkModule .rcModuleBottomLeft {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/bot_left_corner_dark.png') no-repeat top left;
		_background: none;
	}

	.gamesRoomDarkModule .rcModuleBottomRight {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/bot_right_corner_dark.png') no-repeat top right;
		_background: none;
	}

	.gamesRoomDarkModule .rcModuleTopLeft,  .gamesRoomDarkModule .rcModuleTopRight {
		_background:none;
	}

	/* styles for light colored rounded modules in gamesroom */
	.rcModuleHeader, .rcModuleHeader a, .rcModuleHeader a:link, .rcModuleHeader a:hover .rcModuleHeader a:visited {
		color: #FFFFFF;
		text-decoration:none;
	}

	.gamesRoomLiteModule .rcModuleContent {
		border-color: #4B6492;
	}

	.gamesRoomLiteModule .rcModuleContentOuter {
		border-color: #CDD1D8;
	}

	.gamesRoomLiteModule .rcModuleHeader, .gamesRoomLiteModule .rcModuleTopLeft,
	.gamesRoomLiteModule .rcModuleTopRight {
		background-color: #4B6492;
	}

	.gamesRoomLiteModule .rcModuleBottomLeft {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/bot_left_corner_lite.png') no-repeat top left;
		_background: none;
	}

	.gamesRoomLiteModule .rcModuleBottomRight {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/bot_right_corner_lite.png') no-repeat top right;
		_background: none;
	}

	.gamesRoomLiteModule .rcModuleTopLeft,  .gamesRoomLiteModule .rcModuleTopRight {
		_background:none;
	}
	
	/* games room tabs on category page */
	.gamesRoomCategoryList .rcNavTab {
		background-color:#4B6492;
	}
	.gamesRoomCategoryList .rcSelectedTab {
		background-color:#2F3E5B;
	}

	a #carouselPrev {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_back_arrow.png') no-repeat top right;
		_background: none;

	}

	a #carouselNext {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_next_arrow.png') no-repeat top right;
		_background: none;
	}

	a:hover #carouselPrev {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_back_arrow_ov.png') no-repeat top right;
		cursor:pointer;
		_background: none;

	}

	a:hover #carouselNext {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_next_arrow_ov.png') no-repeat top right;
		cursor:pointer;
		_background: none;

	}
	

    .gamesBtn .btnLeft {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_left_sel.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn .btnRight {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_right_sel.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn .btnCenter {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_center_sel.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn a .btnLeft {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_left.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn a .btnRight {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_right.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn a .btnCenter {
		 background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_center.png') no-repeat top left;
		 _background: none;
	}

	.gamesBtn a, .gamesBtn a:link, .gamesBtn a:hover, .gamesBtn a:visited {
		color:#ffffff;
	}

	.gamesBtn  a:hover .btnLeft {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_left_ov.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn  a:hover .btnRight {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_right_ov.png') no-repeat top left;
		_background: none;
	}

	.gamesBtn a:hover .btnCenter {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/btn_center_ov.png') no-repeat top left;
		_background: none;
	}
	
	.showAllWrap .rcModuleContent {
		background: transparent url('http://images.neopets.com/themes/008_com_e529a/games/showallbg.jpg') no-repeat top left;
	}

	#showAllSorting  {
		color:#ffffff;
	}

	.category {
		background-color:#2F3E5B;
		color:#ffffff;
	}


/********************************************************************/
/* Footer Classes ***************************************************/
/********************************************************************/

	.footerForm {
		color: #FFFFFF;
	}

	.copyright, .copyright A:Link, .copyright A:Hover, .copyright A:Visited {
		color: #FFFFFF;
	}


/********************************************************************/
/* Misc Classes of Doom *********************************************/
/********************************************************************/

/* Ad banners.  No comment */
	#ban, .ban_theme_bg {
		background-color: #747474;
		background-image: url("http://images.neopets.com/themes/008_com_e529a/banner_bg.png");
	}

	.marqueeSlot, .marqueeSlot A:Link, .marqueeSlot A:Hover, .marqueeSlot A:Visited {
		background-color: #4B6492;
		color: #E6E9F0;
	}

	.marqueeSlotOn, .marqueeSlotOn A:Link, .marqueeSlotOn A:Hover, .marqueeSlotOn A:Visited {
		background-color: #2F3E5B;
		color: #E6E9F0;
	}
		
/********************************************************************/
/* MAMABAR Classes **************************************************/
/********************************************************************/

ul.brand-mamabar-list a {
	display: block;
	line-height: 18px;
	color: #9a9a9a;
	font-size: 11px;
	text-decoration: none;
	font-weight: bold;
	font-family: Arial, Helvetica, sans-serif;
}

ul.brand-mamabar-list a:hover { text-decoration: underline; }

/* ACTIVE STATE */
ul.brand-mamabar-list a.brand-mamabar-active {
	color: #4F6C95;
	cursor: default;
}

}
