css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flowdock.com") {
  .activity-indicator {
    background-color: hsl(120, 100%, 30%) !important;
  }
  
  .activity-indicator-mentions {
    background-color: hsl(320, 100%, 70%) !important;
  }
  
  .thread {
    border-left: none !important;
    width: 26px !important;
  }
  
  .thread:before {
    font-size: 18px;
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    z-index: 1;
    color: hsla(0, 0%, 10%, 0.8);
    text-align: center;
    padding-top: 8px;
  }
  
  .red.thread:before {
    content: '♡';
    background: hsl(0, 100%, 70%);
  }
  
  .orange.thread:before {
    content: '⚑';
    background: hsl(40, 100%, 70%);
  }
  
  .yellow.thread:before {
    content: '★';
    background: hsl(60, 100%, 45%);
  }
  
  .green.thread:before {
    content: '▰';
    background: hsl(120, 100%, 30%);
  }
    
  .dirty-green.thread:before {
    content: '◉';
    background: hsl(160, 100%, 40%);
  }
  
  .teal.thread:before {
    content: '♌';
    background: hsl(200, 100%, 70%);
  }
  
  .blue.thread:before {
    content: '♒';
    color: hsla(0, 0%, 100%, 0.8);
    background: hsl(240, 100%, 40%);
  }

  .purple.thread:before {
    content: '◈';
    background: hsl(280, 100%, 70%);
  }
  
  .pink.thread:before {
    content: '♫';
    background: hsl(320, 100%, 80%);
  }
  
  .bubble.thread {
    opacity: 1 !important;;
  }
}
