css:
@-moz-document domain("livejournal.com") {
.b-singlepost-withbodybanner .b-singlepost-bodywrapper .b-singlepost-body {
    max-width: 1100px;
    margin: auto;
}

.b-singlepost {
    background: #f9f9f9;
}

.b-singlepost-about .author {
    margin: auto 30px;
}
.b-singlepost-about .author {
    max-width: 1100px;
    margin: auto;
    background: #ffffff;
    padding: 30px 30px 15px;
}
.b-singlepost-standout {
    margin: 20px auto;
    text-align: center;
    background: #ffffff;
    padding: 30px;
    max-width: 1100px;
}

.b-grove {
    margin: 30px;
}

.b-singlepost-withbodybanner .b-singlepost-meta, .b-singlepost-withbodybanner .b-singlepost-title, .b-singlepost-withbodybanner .b-singlepost-body, .b-singlepost-withbodybanner .b-singlepost-tags {
    max-width: 1100px;
    margin: auto;
    background: #ffffff;
    padding: 30px;
}

.b-xylem-cell-amount {
    float: right;
    width: auto;
}

.b-xylem-first {
    border-top: none;
    max-width: 1100px;
    margin: -15px auto 20px;
    background: #ffffff;
    padding: 30px;
}

#multiform {
    position: relative;
    padding: 30px 0px;
    background: #ffffff;
}
}
