css:
@-moz-document domain(twitter.com) {
/* font */
body {background-color:#fff}
body,body.ms-windows {font-family:sans-serif}

/* hide useless boxes */
.route-home .wtf-module,
.route-home .wtf-module.has-content,
.route-home .trends,
.route-home .Footer,
.route-home .ProfilePreviewBanner,
.route-home .has-recap {display:none!important}
 

/* grid */
.route-home .wrapper {background:#fff; padding:56px 15px 15px}
.route-home .wrapper,
.route-home .dashboard {width:590px!important}
.route-home .dashboard,
.route-home .content-main,
.route-home .profile-card.profile-header {float:none!important; margin:0 auto!important}

/* profile */
.route-home .profile-card.profile-header {margin-bottom:10px}
.route-home .DashboardProfileCard-tweetbox {width:564px}
.route-home .DashboardProfileCard-avatarLink,
.route-home .DashboardProfileCard-userFields,
.route-home .DashboardProfileCard-bg {display:none!important}

/* nav */
.route-home .global-nav {background:#fff}
.route-home .global-nav-inner {width:590px; margin:0 auto}
.route-home .nav>li>a .text,
.route-home .bird-topbar-etched,
.route-home .topbar-tweet-btn,
.route-home .translate-button {display:none!important}
.route-home .global-nav .form-search {width:auto}
.route-home .global-nav .search-input {width:150px}
.route-home .global-nav .settings {margin-right:0!important; border-radius:0}
.route-home .global-nav .settings:before {display:none!important}

/* removing border radius */
.route-home .module .flex-module:first-child,
.route-home .tweet-user,
.route-home .with-media-forward .cards-base .media-forward,
.route-home .profile-banner-footer,
.route-home .module,
.route-home .permalink,
.route-home .OldMedia, .route-home .OldMedia-video
	{border-radius:0!important}
.route-home .permalink
	{border:0}

/* tweet textarea */
.route-home .home-tweet-box {background:#fff!important}
.route-home .timeline-tweet-box {border-width:0 1px 1px!important; margin-top:-20px; margin-bottom:20px}
.route-home .top-timeline-tweet-box-user-image {display:none}
.route-home .top-timeline-tweetbox .timeline-tweet-box .t1-form {margin-left:0}

/* hide timelime title */
.route-home .header-inner {display:none}

/* tweet */
.route-home .tweet {padding-left:0; padding-bottom:5px}
.route-home .stream-item {border-width:1px 0!important; margin:0!important; border-radius:0!important}
.route-home .stream-item.open {border-color:#fff; border-radius:0}
.route-home .tweet .tweet-geo-text {display:none}
 
/* name */
.route-home .stream-item-header .account-group {position:relative; overflow:visible}
.route-home .avatar,
.permalink-header .avatar
   {border-radius:50%!important}
.route-home .stream-item-header .FullNameGroup {font-weight:normal; position:absolute; top:0; left:100%; margin-left:10px; white-space:nowrap}
.route-home .stream-item-header .fullname {font-weight:normal; color:inherit; overflow:visible!important; max-width:none}
.route-home .stream-item-header .username s {display:none}
.route-home .stream-item-header .username b {font-weight:bold; font-size:14px}
.route-home .tweet .time {position:absolute; top:10px; right:10px}
.route-home .tweet .time:before {display:none}
.route-home .tweet .time a {opacity:0}
.route-home .tweet:hover .time a {opacity:1}
.route-home .ProfileTweet-action--more {position:absolute; bottom:5px; right:15px; opacity:0}
.route-home .tweet:hover .ProfileTweet-action--more {opacity:1}

.route-home .js-retweet-text .pretty-link.js-user-profile-link:before {content:attr(href)}
.route-home .pretty-link.js-user-profile-link b {display:none}
.route-home .js-retweet-text {display:block; overflow:hidden}
.route-home .js-retweet-text:first-letter {margin-left:-3.6px}


/* tweet footer */
.route-home .stream-item-footer *,
.route-home .bottom-tweet-actions * {opacity:0}
.route-home .tweet:hover .stream-item-footer *,
.route-home .tweet:hover .bottom-tweet-actions * {opacity:1}

/* green retweet icon */
.route-home .Icon--retweeted {display:none}
.route-home .tweet > .context .with-icn,
.route-home .tweet > .context .with-icn * {margin-left:0}

/* image*/
.route-home .AdaptiveMedia {height:50px}
.route-home .tweet:hover .AdaptiveMedia.is-generic-video {height:auto}

.SummaryCard--large {border-radius:5px}
.card-type-summary_large_image {min-height:0!important}
.TwitterCard .SummaryCard--large .SummaryCard-image {height:0; overflow:hidden; border:0}

/* profile */
.route-profile .GridTimeline {font-family:sans-serif!important}
.route-profile .ProfileTweet-text {font-family:sans-serif!important; font-size:14px!important; line-height:18px!important}

.conversation-module>li:after,
.conversation-module>li:before {left:22px}
.vellip {left:22px}

/* remove ads */
.route-home .promoted-tweet {display:none!important}

}
