css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("infobae.com") {
  
  
  /* -------------- VARIABLES --------------* */


  :root {
--fondo-oscuro: #424242 !important;       
--fondo-mas-claro: #525252 !important; 
--fondo-mas-oscuro: #777 !important;      				
--letra-clara: #D8D8D8 !important;        			
--letra-mas-oscura: #BDBDBD !important;      		
--letra-mas-mas-oscura: #999 !important; 
--letra-naranja: #F98300 !important;
--amarillo-resaltado: #FF0 !important;
--pruebas: #FF0040 !important;
}
  
    /* ----------------- PORTADA FONDOS ----------------- */
  
  
  body {
    background-color: var(--fondo-oscuro);
}


  .pb-f-homepage-masthead .mastnav {
    background-color: var(--fondo-oscuro);
}
  
  .skin.skin-card {
    padding: 15px !important; 
    border: 1px solid var(--letra-mas-mas-oscura) !important;
    border-bottom-color: var(--letra-mas-mas-oscura) !important;
    border-bottom-width: 2px !important;
    border-radius: 4px !important;
    background-color: var(--fondo-oscuro) !important;
    color: var(--letra-mas-mas-oscura) !important;
}
  
  .pb-f-homepage-story .flex-item {
    background-color: var(--fondo-oscuro);
}
  
  .with-right-border {
    background-color: var(--fondo-oscuro);
}
  
  section#bomb-1.col-xs-12, section#bomb-2.col-xs-12, section#bottom.col-xs-12, section#sub-footer {
    background-color: var(--fondo-oscuro);
}  
  
  .col-lg-4 {
    background-color: var(--fondo-oscuro);
}
  
    .skin.skin-card {
    background-color: var(--fondo-oscuro);
}
  
  .pb-f-global-currency-exchange .cur-exchange .skin.skin-card {
    background-color: var(--fondo-mas-claro);
}
  
  .pb-f-global-most-read aside.skin.skin-card {
    background-color: var(--fondo-mas-claro);
}
  
  .pb-f-homepage-story .skin.skin-card {
    background-color: var(--fondo-oscuro);
}
  
  .pb-f-global-footer-colored {
    background-color: var(--fondo-oscuro);
}
  
    .pb-f-global-footer-colored footer.light {
    background-color: var(--fondo-oscuro);
}
  
.pb-f-global-header-nav .headernav #slider-menu {
    background-color: var(--fondo-mas-claro);
}
  
  .pb-f-global-header-nav .headernav.skinny #slider-menu #slider-menu-scroll ul li {
    background-color: var(--fondo-mas-claro);
}
  
  .pb-f-global-header-nav .headernav #slider-menu #slider-menu-scroll ul li.main-nav-border {
    background-color: var(--fondo-mas-claro);
    margin: 0px 0px !important;
}

  .pb-f-global-header-nav .headernav.skinny #drawer .sub-nav {
    background-color: var(--fondo-mas-oscuro);
}
  
  .pb-f-global-sub-footer-logo aside.sub-footer-logo {
    background-color: var(--letra-naranja);
}

  
    /* ----------------- PORTADA FUENTES ----------------- */
  
  .pb-f-homepage-masthead .masthead .logo-sub-nav a {
    color: var(--letra-clara);
}
  
  .pb-f-homepage-masthead .masthead .logo-sub-nav a:hover {
    color: var(--letra-naranja);
}
  
  .pb-f-global-hot-topics .hot-topics p a {
    color: var(--letra-clara);
}
  
  .pb-f-global-hot-topics .hot-topics p a:hover {
    color: var(--letra-naranja);
}
  
  body a, body a:visited, body a:hover {
    color: var(--letra-clara);
}
  
  body a:visited {
    color: var(--letra-mas-mas-oscura);
}
  
  .pb-f-homepage-story .flex-item .blurb, .pb-f-global-footer-colored footer.light .footer-row-1 aside.header-label, .pb-f-global-footer-colored footer.light a {
      color: var(--letra-mas-oscura);
}
  
  .pb-f-homepage-story .label.label-kicker, .label, .pb-f-global-most-read .label.label-kicker {
      color: var(--letra-clara);
}
    
  .pb-f-global-currency-exchange .cur-exchange table td {
      color: var(--letra-clara);
}
  
  .pb-f-global-currency-exchange .cur-exchange table td span i {
    color: var(--letra-mas-mas-oscura);
}
  
  .pb-f-global-most-read article .number.left {
    color: var(--letra-clara);
}
  /* Estos son todos los titulos, como GALERIA, TENDENCIAS, etc. */
  div[class~="label"] {
    color: var(--letra-clara) !important;
}
  
  .pb-f-global-currency-exchange .cur-exchange .label.label-kicker {
    color: var(--letra-clara);
}

  .pb-f-global-header-nav .headernav #drawer a:hover {
    color: var(--letra-naranja);
}
  
  
  
    /* ----------------- ARTÍCULOS FONDO ----------------- */
  
  
  .pb-f-global-sharebar .sharebar-wrapper .social-tools-wrapper {
    background-color: var(--fondo-oscuro);
}
  
  #pb-root {
    background-color: var(--fondo-oscuro);
}
  
  .sharebar-wrapper.sharebar-top-desktop, .sharebar-wrapper.sharebar-sticky-desktop.stick {
    background-color: var(--fondo-oscuro);
}
  
  
    /* ----------------- ARTÍCULOS FUENTES ----------------- */
  
  
  header h1 {
    color: var(--letra-clara);
}
  
  header .subheadline {
      color: var(--letra-mas-oscura);
}
  
  .pb-f-article-byline .byline .byline-author, .pb-f-article-byline .byline .byline-bio {
      color: var(--letra-mas-oscura);
}
  
  .pb-f-article-body .element-paragraph {
    color: var(--letra-clara);
}
  
  .pb-f-article-body .article-body .body-text-highlight {
    background-color: var(--letra-naranja) !important;
}
  
  .pb-f-article-body figcaption {
    color: var(--letra-mas-mas-oscura);
}
  
  .result-teaser-normal h4 {
    color: var(--letra-clara);
}
  
  body .excerpt, body p.excerpt, body span.excerpt {
    color: var(--letra-clara);
}
  

}
