css:
@-moz-document domain("symfony.com") {
/*logo download too flashy*/

header #header-bottom #header-download-sticky {
    background-color: #404040;
}
body {
    background-color: black !important;
    color: white;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}
body.doc_article #main h1.content_title {
    color: white;
}
/* title in menu */

body.doc_index.cookbook #main ul.simple>li>a,
body.doc_index.cookbook #main ul.simple>li>strong,
body.doc_index.components #main ul.simple>li>a,
body.doc_index.components #main ul.simple>li>strong,
body.doc_index.contributing #main ul.simple>li>a,
body.doc_index.contributing #main ul.simple>li>strong {
    color: white;
    font-size: 16px;
    font-weight: bold;
}
.literal-block .highlighttable td.linenos pre {
    background-color: black;
    color: black;
    border: 1px solid black;
}
#sidebar {
    background: black;
}
body.doc #sidebar .toc .toc-content {
    background: black;
}
body.doc #sidebar .toc .toc-content a {
    color: dodgerblue;
}


/*white banner for logo ... */

header #header-bottom #header-main-menu>li>a {
    color: white;
}

#sidebar .submenu .list_submenu li a {
    color: white;
}
header {
    background: #404040;
}
header #header-bottom {
    background: #404040;
}
/* note 'idea' */

.box-secondary {
    background: #404040;
}
div.admonition-wrapper div.admonition {
    background-color: #404040;
}
.literal-block .highlighttable td.linenos {
    background-color: #404040;
    border: 1px solid black;
}
/* when code in note 'idea' */

div.admonition-wrapper div.admonition td.linenos pre {
    background: #404040;
}

p.sidebar-title {
    color: white;
}

table th {
    background: #404040;
}
div.admonition-wrapper div.admonition-wrapper div.admonition {
    background-color: #454545;
}
/* for special notices when dedicated behavior exist for a sf version*/

/*when tabbed code zones exist, the non selected ones must be with grey background and white font */
div.jsactive li {
    background-color: #404040;
}

div.jsactive li a {
    color: white;
}

body.doc .versionadded {
    background-color: #404040;
}
#p-0 .symfony-clients {
    background: #404040;
}
/*promo banner before footer*/

footer .promos {
    background-color: #404040;
}
/* "contributed by" zone in some pages like this http://symfony.com/blog/new-in-symfony-2-8-guard-authentication-component */

.post-content div {
    background-color: #404040 !important;
}


/*for Symfony API*/
div.header {
    background-color: #404040;
}
.description {
    background-color: #404040;
}

h2 {
    background-color: #404040;
}

a:link, a:visited {
    color: dodgerblue;
}

/*for Symfony Live/con sub domain*/
.content-container {
	background-color: black;
}
}
