css:
@-moz-document regexp("http://www.lpga.com/tournaments/[^/]*/leaderboard") {

  #header { display: none;}
  #backbonePlaceholder {margin:0px;}
  #contentHeaderContainer { display: none;}
  
  #mainContainer .fulltabs { display: none;}
  #mainContainer .page-header { display: none; }
  .main-container {margin:0px !important;}
  
  .iab-wrapper  { display: none;}
  .leaderboard-header  { display: none;}
  .leaderboard-header-sticky{top:0px;}
  
  .leaderboard-header-sticky-columns, .live-leaderboard {padding: 0px !important; margin:0px !important;}
  .container { margin: 0px; width: 820px; padding:0px; }
  .leaderboard-header-sticky-columns table { width: 820px; margin: 0px; }
  /*
  */
}
