css:
@-moz-document domain("nosue.me") {
a {
    color: #A73A3A;
    text-decoration: none;
}
body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #378C9A;
    background-image: url("http://images.123hdwallpapers.com/20150506/apple-galaxy-wallpapers-2560x1440.jpg");
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
}
.jumbotron {
    padding-top: 30px;
    padding-bottom: 30px;
    margin-bottom: 30px;
    color: inherit;
    background-color: rgba(84, 88, 99, 0.5);
}
.alert {
    padding: 15px;
    margin-bottom: 20px;
    border: 0px solid transparent;
    border-radius: 4px;
}
.alert-danger {
    color: rgb(202, 77, 77);
    background-color: rgba(113, 16, 16, 0.5);
    border-color: rgba(255, 255, 255, 0);
}
.alert-success {
    color: #3c763d;
    background-color: rgba(16, 53, 1, 0.5);
    border-color: #d6e9c6;
}
.panel-heading {
    background-color: rgb(141, 171, 202) !important;
    background-image: none !important;
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    box-shadow: none !important;
}
.panel-body {
    padding: 15px;
    color: inherit;
    background-color: rgba(199, 199, 199, 0.5);
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid rgba(0,0,0,.5);
    color: #000000;
    background-color: rgba(236,236,236,0.60)
}
img {
	border: 0px;
	-webkit-border-radius: 10px;
	-moz-border-radius: 10px;
	border-radius: 10px;
}
}
