css:
@-moz-document url-prefix("https://www.reddit.com/r/rupaulsdragrace"), url-prefix("http://www.reddit.com/r/rupaulsdragrace") {

body {
    background-color: #edebe8;
}

#sr-header-area {
    background-color: rgba(226,214,150,1);
}

#header-img {
	-webkit-filter: drop-shadow(0px 2px 2px rgba(84,72,34,0.50));
	filter: drop-shadow(0px 2px 2px rgba(84,72,34,0.50)); 
}

#header .pagename a {
	text-shadow: 0px 2px 6px rgba(84,72,34,0.50);   
}

#header .tabmenu:before {
	background-color: rgba(209,188,125,0.5);
}

#header .tabmenu a {
    color: #544822 !important;
}

#header .tabmenu li.selected a {
        border-bottom-color: #726436;
}

#header-bottom-left a {
    color: #7e6d36 !important;
    color: white !important;
}



.morelink, .side .titlebox .md h3 a, .side .titlebox h1.redditname:before, .drop-choices a.choice:hover, .submit-page #newlink.submit.content ul.tabmenu.formtab, .submit_text.enabled.roundfield, body .btn, body button, .content .infobar, .titlebox .usertext .md>h1 {
    background-color: #fe8c81;
    border-bottom-color: #bb6057;
}


.nestedlisting .comment.thing {
    padding-bottom: 3px !important;
}

.footer-parent {
    margin-top: -20px;
    height: 600px;
    background-size: contain;
}

.footer {
    margin-top: 100px;
	background-color: rgba(20,20,20,0.0);
}

li.flat-vert {
    color: #333333 !important;
}

p.bottommenu, .bottommenu a{
	color: #333333;
}

p.bottommenu bottommenu-advertise {
    margin: 500px;
}

p.bottommenu-advertise {
    margin-bottom: 20px;
}

}
