css:
@-moz-document domain("electricmotorcycleforum.com"), domain("www.electricmotorcycleforum.com") {
body, td, th, tr {
  background-color: black;
  color: white;
}

/* Ads */
.adsbygoogle {
  display: none !important;
}

/* Forum listings */

#ib1 {
  background-color: black;
}

ul.linktree li a
{
  color: inherit;
}
ul.linktree li a:hover
{
  color: inherit;
}

#mainframe {
  background-color: black;
  color: white;
}

#greeting_section
{
    display: none;
}

#messageindex td.icon1, #messageindex td.icon2
{
	padding: 0;
	width: 1%;
}
#messageindex td.subject
{
	padding: 0.25em;
}
#messageindex td.starter
{
	padding: 0.25em;
}
#messageindex td.replies
{
	padding: 0.25em;
	width: 2%;
}
#messageindex td.views
{
	padding: 0.25em;
	width: 2%;
}
#messageindex td.lastpost
{
	padding: 0.25em;
	width: 22%;
}
#messageindex td.moderation
{
	padding: 0.25em;
	width: 2%;
}

#ib1 table img[src$="myslogan.jpg"] {
  display: none;
}
.tborder {
  padding: 1px;
  border: none;
}

#messageindex * {
  border-collapse: collapse;
}

#messageindex tr td:only-child {
  background-color: grey;
}

#messageindex tr:nth-child(2n) {
  background-color: #eee;
}

#messageindex tr:nth-child(2n+1) {
  background-color: black;
}

a:visited {
  color: lightblue;
  text-decoration: none;
}
a:hover {
  color: white;
  text-decoration: underline;
}

ul.linktree {
  font-size: large;
  padding: 0.5em;
}
.windowbg, #preview_body, .content, .roundframe {
  color: white;
  background-color: black;
}
.windowbg2 {
  color: white;
  background-color: black;
}
.windowbg3 {
  color: white;
  background-color: black;
}

#linktree_lower {
  display: none;
}

#topic_icons {
  display: none;
}

#footerarea {
  display: none;
}

/* Thread pane */

#forumposts {
  background-color: black;
}

.bordercolor {
  background-color: inherit;
}

/* Profile */

#profileview {
  background-color: inherit;
}

.adm_section li {
    background-color: black;
}
}
