css:
@-moz-document url("http://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com/"), domain("www.tumblr.com") {
.messaging-conversation .has-image.conversation-header .conversation-header-image,
.messaging-conversation .conversation-main,
.messaging-conversation .conversation-compose,
.messaging-conversation .hide-blog-selector.conversation-compose .text-input, .messaging-conversation--open .conversation-header:not(.has-image) {
    background: url('http://i.imgur.com/S7c0s0P.png') top left #C5C3E1 repeat fixed !important;
	}

.messaging-inbox, .popover--activity-popover .activity-popover-header, .popover--messaging-share-post.popover.popover_menu.popover_gradient.popover--base.bottom, .popover-inner.popover_inner, .text-input-container,   .messaging-share-post, .show-submission.messaging-share-post-submission, .messaging-share-post .messaging-share-post-message, .messaging-share-post .messaging-share-post-message .text-input-container, .messaging-inbox .inbox-recipients .inbox-recipient {
    background: url('http://i.imgur.com/S7c0s0P.png') top left #C5C3E1 repeat fixed !important;
	}



.post_notes .notes_outer_container.popover .popover_scroll, .popover--account-popover .popover .popover_subsection .popover_menu_list {
    background:#fff!important;
    margin:0;
	}

.selected .tab_notice, .messaging-share-post .messaging-share-post-search .selected-blog, .tab_notice, .blog-selector-container .selected-blog .caret.has-badge, .messaging-inbox .inbox-conversations .inbox-conversation:hover {
    background:#9f9bcd!important;
    color:#fff!important;
	}

.messaging-inbox .inbox-conversations .inbox-conversation .name-container .name, span.text, .messaging-inbox .inbox-conversations .inbox-conversation .additional-blog-name:not(:empty), span.name, .post-activity-header .primary-message, .messaging-share-post .messaging-share-post-result .name, .messaging-share-post .messaging-share-post-result .title,.messaging-share-post .messaging-share-post-search .search-input-wrapper:before, .messaging-share-post .messaging-share-post-message .submit-button .submit {
    color:#fff!important;
	}

.messaging-conversation .conversation-header .conversation-header-image {
    -webkit-filter: initial;
    filter: initial;
    background-position: top left;
	}

.messaging-conversation .conversation-main,
.messaging-conversation .conversation-compose,
textarea {
    border-top: 1px solid rgba(0, 0, 0, .0);
	}

.messaging-share-post .messaging-share-post-search, .post-activity>*, .post-activity .header-container-inner>* {
    border-bottom: 1px solid rgba(0, 0, 0, .0)!important;
	}

.messaging-conversation .conversation-header::after {
    background: rgba(0, 0, 0, .0)!important;
	}

.messaging-conversation .has-image.conversation-header .conversation-header-main {
    background-color: rgba(0, 0, 0, .0);
	}


.messaging-conversation.theme-background-white .conversation-message-text .message-bubble,
.messaging-conversation .conversation-message-text .message-bubble { 
    background: rgba(255, 255, 255, .8); 
	}

#x1cpostage_caption {
    background: white!important;
	}

.tx-scroll .tx-scrollbar-thumb {
    background: rgba(252, 244, 255, .5)!important;
	}

.tx-scroll .tx-scrollbar-thumb:hover { 
    background: rgba(253, 248, 255, .9)!important; 
	}

textarea.text-input  {
    color:#fff!important;
    background:none!important; 
	}

.messaging-conversation--open .conversation-message .message-bubble {
    background:rgba(197,195,225, .75)!important;
	}

.link-bubbles .options>div, .post_notes .notes_outer_container.popover .note a.follow, .link-bubbles .url, .bubbles>div {
    background:rgba(197,195,225, .75);
    border-color:rgba(197,195,225, .75);
	}

.post-activity-note, .post-activity-notes, .messaging-inbox .inbox-recipients .selected, .post_notes .notes_outer_container.popover .note a.follow:hover {
    background:rgb(197,195,225);
    border-color:rgb(197,195,225);
	}

.bubbles>div.active {
    background: rgba(220, 198, 223, .7);
	}

.messaging-conversation--open .conversation-message .inline-activity {
    background: #fff;
    width: 175px;
    margin-left: 38px;
    border-radius: 6px;
	}

.post-activity-header .return::before, .messaging-inbox .inbox-row h4, .messaging-inbox .inbox-row h3, .messaging-inbox .inbox-compose .inbox-compose-input .label-text-container, .messaging-inbox .inbox-compose-toggle .compose-cancel, .blog-selector-container .selected-blog .caret, .messaging-inbox .inbox-compose-toggle .compose-start, .messaging-conversation--open .conversation-header .title, .messaging-conversation--open .conversation-header a, .rollup-notes-summary, .post-activity-rollup-notes .rollup-notes-summary-likes, .post-activity-rollup-notes .rollup-notes-summary-reblogs, .messaging-share-post .messaging-share-post-search .search-input { 
    color: #fff!important; 
	}

.tx-scroll .tx-scrollbar-track { 
    background: rgb(220, 198, 223)!important; opacity: 0.8!important; 
	}

.tx-scroll:hover .tx-scrollbar-track { 
    opacity: 0.8!important; 
	}
}
