@-moz-document domain("gnu.org") {
  html, body, #content, #toplinks a, .content, h1, h2, h3, h4, h5, *[bgcolor="white"], #footer {
    background-color: #302F2F !important;
    color: #ccc !important;
    font-family: Loma,Verdana,Geneva,Tahoma,sans-serif !important;
  } 

  a:link { color: #66a3ff !important; }
  a:visited { color: #4d4dff !important; }

  .node { border-radius: .3em !important; }
  .node p {
    color: #ccc !important;
    padding: .5em !important;
  }

  /*
