css:
@-moz-document url-prefix("https://nosue.me"), url-prefix("http://nosue.me"), url-prefix("https://nosueip.azurewebsites.net/") {
a {
    color: #267BC5;
}

a:hover {
    text-decoration: none;
    color: #B73375;
}
@font-face {
	font-family: Exo;
    src: url('http://fonts.googleapis.com/css?family=Exo+2');
}
body {
    font-family: Exo,Tahoma,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    background-image: url("https://new.ppy.sh/images/backgrounds/page-ddd.png")!important;
    padding: 60px
}
img {
    border: 0;
    border-radius: 5px;
}
.navbar-custom {
    background-image: url('https://new.ppy.sh/images/headers/forum-category.png');
    background-color: #777777;
    border-color: #4C4C4C;    
}
.navbar-custom .navbar-nav > .active > a, .navbar-custom .navbar-nav > .active > a:hover, .navbar-custom .navbar-nav > .active > a:focus {
    color: #fff;
    background-color: transparent !Important;
}
.navbar-custom .navbar-nav > .open > a, .navbar-custom .navbar-nav > .open > a:hover, .navbar-custom .navbar-nav > .open > a:focus {
    background-color: rgba(0, 0, 0, 0.44);
    color: #ffffff;
    border-radius:10px;
}
.navbar-custom .navbar-nav > li > a {
    color: #D8D8D8;
    
}
.navbar-custom .navbar-brand {
    color: #C8C8C8;
}
.navbar-nav>li>.dropdown-menu {
    margin-top: 0;
    border-radius: 10px;
    background-color: rgb(112, 113, 113); 
    background-image: url('https://new.ppy.sh/images/headers/forum-category.png')                                           
}
.form-signin input {
    border-radius: 20px;
}
.dropdown-menu * {
    border-radius: 3px;    
    text-align: center;
}
.dropdown-menu > li > a {
    color: #FFF;
    background-color: transparent;
    background: none;
}
.container-fluid>.navbar-collapse, .container-fluid>.navbar-header, .container>.navbar-collapse, .container>.navbar-header {
    margin-right: 50px;
    margin-left: 50px;
}
.navbar-brand, .navbar-nav>li>a {
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
}
.jumbotron {    
/*  background-image: url('https://new.ppy.sh/images/headers/forum-category.png'); */
    background-image: url('http://puu.sh/mZO68/55ad4fbc6c.png');
    padding-top: 30px;
    padding-bottom: 16px;
    margin-bottom: 30px;
    box-shadow: 0 1px 3px rgba(0,0,0,.25);
    border-radius: 10px;
}
.jumbotron p {
    margin-bottom: 15px;
    font-size: 21px;
    font-weight: 200;
    text-shadow: 0 1px 3px rgba(0,0,0,.25);
    text-align: center;
}
.jumbotron .container {
    max-width: 31%;
}
.alert {
    background-image: url('https://new.ppy.sh/images/headers/forum-category.png') !Important;
    box-shadow: 0 1px 3px rgba(0,0,0,.25);
    text-shadow: 0 1px 3px rgba(0,0,0,.25);
    font-size: 15px;
}
.jumbotron .h1, .jumbotron h1 {
    color: white !important;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
    text-align: center;
}
.h2, h2 {
    font-size: 30px;
    text-align: center;
    background-image: url('https://new.ppy.sh/images/headers/forum-category.png');
    background-color: rgba(2, 2, 2, 0);
    color: white !Important;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
    box-shadow: 0 1px 3px rgba(0,0,0,.25);
    border: 1px solid;
    border-color: rgba(39, 39, 39, 0.2);
    border-radius: 5px;    
}
.h3, h3 {
    font-size: 24px;
    text-shadow: 0 1px 3px rgba(0,0,0,.75);
    color: #fff !Important;
}
/* nosue.me/u/ */
.h1, h1 {
    font-size: 36px;
}
/* Edit on nosue/me/leaderboard / clans * | Ranking Box */
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid #ABABAB;
    background-image: url("https://new.ppy.sh/images/headers/forum-category.png");
    background-color: #fff;
    box-shadow: 0 1px 3px rgba(0,0,0,.75);
}
.panel-heading {
    background-color: rgb(245, 245, 245) !important;
    background-image: none !important;
    /* -webkit-box-shadow: none !important; */
    box-shadow: 0 1px 3px rgba(0,0,0,.25);
}
.panel-body {
    padding: 0;
}
}

@-moz-document url-prefix("http://nosue.me/forum/"), url-prefix("http://nosue.me/forum/category/"), url-prefix("http://nosue.me/forum/topic/") {
.forumItem {
    background-image: url('https://new.ppy.sh/images/headers/forum-category.png');
    box-shadow: 0 1px 3px rgba(0,0,0,.75);
}
.h3, h3 {
    font-size: 24px;
    text-shadow: 0 1px 3px rgba(0,0,0,.35);
}
.col-xs-10 {
    width: 83.33333333%;
    background-color: rgba(255, 255, 255, 0.40);
}
.h2, h2 {
    font-size: 30px;
    text-align: center;
    background-image: url('https://new.ppy.sh/images/headers/forum-category.png');
    background-color: rgba(2, 2, 2, 0);
    color: white !Important;
    text-shadow: 0 1px 3px rgba(0,0,0,.35);
    box-shadow: 0 1px 3px rgba(0,0,0,.25);
    border: 1px solid;
    border-color: rgba(39, 39, 39, 0.2);
    border-radius: 5px;
    padding: 5px;
}
}

@-moz-document url-prefix("http://nosue.me/rules"), url-prefix("http://nosue.me/supporter"), url-prefix("http://nosue.me/changelog"), url-prefix("http://nosue.me/register"), url-prefix("http://nosue.me/manage") {
.panel-body {
    padding: 10px;
}
.h2, h2 {    
    background-color: rgb(253, 101, 106);
    padding: 5px;
}
}
