css:
@-moz-document regexp("(https|http)://animesub.info/forum/.*") {
@font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 400;
        src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v11/UyBMtLsHKBKXelqf4x7VRQ.woff2) format('woff2');
        unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
    }
    
    /* latin */
    @font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 400;
        src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v11/1YwB1sO8YE1Lyjf12WNiUA.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
    }
    
    /* latin-ext */
    @font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 700;
        src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v11/ObQr5XYcoH0WBoUxiaYK3_Y6323mHUZFJMgTvxaG2iE.woff2) format('woff2');
        unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
    }
    
    /* latin */
    @font-face {
        font-family: 'Lato';
        font-style: normal;
        font-weight: 700;
        src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v11/H2DMvhDLycM56KNuAtbJYA.woff2) format('woff2');
        unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
    }
    
    html {
        font-size: 62.5%;
    }

    body {
        background: url("http://i.imgur.com/jkURZLb.png") #fcfcfc;
        font-size: 14px;
        font-size: 1.4rem;
        height: 100%;
    }

    body > .pun {
        min-width: 960px;
        max-width: 1200px;
        margin: 0 auto;
    }

    .pun {
        font-family: Lato, Verdana, Helvetica, Arial, sans-serif;
        font-size: inherit;
    }

    .pun h2 {
        font-size: 1.3rem;
        padding: 0 0.2rem;
    }

    #rules > div.hd h2 {
        font-size: 1.6rem;
        line-height: 3.2rem;
    }

    .pun h2 a:hover, .pun h2 a:active, #brdmenu a:hover, #brdmenu a:active {
        color: #e15d2e;
        text-decoration: none;
    }

    .pun table, .pun td, .pun th, .pun input, .pun select, .pun optgroup, .pun textarea, .pun small, .pun samp, .pun legend {
        font-size: 1.3rem;
        font-family: Lato, Verdana, helvetica, arial, sans-serif;
    }

    .pun p, .pun ul, .pun ol {
        font-size: 1.3rem;
        padding: 0;
    }

    .pun dl {
        font-size: 1.25rem;
    }

    .pun .box {
        background-color: transparent;
        border: 0;
        border-bottom: 1px solid;
        border-color: rgba(0, 0, 0, 0.1);
    }

    .pun .block .inbox, .pun .blockmenu .inbox {
        padding: 0 0.2rem;
    }

    .pun .blockmenu li.isactive a, #posterror li strong {
        color: #4ca6c0;
        text-decoration: underline;
    }

    .pun .blockmenu a:hover, .pun .blockmenu a:active {
        text-decoration: none;
    }

    #brdheader {
        margin-bottom: 5px;
    }

    #brdtitle h1 {
        font-size: 1.45rem;
        font-weight: bold;
        line-height: 1.9rem;
    }

    #brddesc {
        font-size: 1.3rem;
        line-height: 1.5em;
        padding: 0;
    }

    .pun #brdmenu {
        background-color: #33302e;
        color: #FFF;
        padding: 0;
    }

    .pun #brdmenu li {
        display: inline;
        margin: 0;
    }

    #brdmenu a {
        color: #FFF;
        line-height: 2.4rem;
        padding-left: 1rem;
    }

    #brdwelcome, #brdfooter dl a, div.blockmenu li, div.rbox input {
        line-height: 2.4rem;
    }

    .pun .tcl h3 {
        font-weight: bold;
        margin: 0;
    }

    .pun .tclcon a {
        font-size: 1.1em;
    }

    .pun a, .pun .tcl h3 a {
        color: #4ca6c0;
        text-decoration: none;
    }

    .pun a:hover, .pun .tcl h3 a:hover {
        color: #e15d2e;
    }

    .pun .blocktable h2 {
        background-color: transparent;
        border-bottom: 1px solid rgba(0, 0, 0, 0.1);
        color: #4c4541;
        font-size: 1.5rem;
        font-weight: bold;
    }

    .pun .blocktable .tclcon div {
        color: #555;
        font-size: 1.25rem;
        overflow: hidden;
        width: 100%;
    }

    .pun thead tr {
        border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    }

    .pun th {
        background-color: transparent;
        border: 0;
    }

    .pun td, .pun th {
        border: 0;
        font-weight: normal;
        line-height: 2.1rem;
        padding: 0;
        text-align: left;
    }

    .pun td.tc2, .pun td.tc3, .pun td.tcmod, #postpreview, #viewprofile dd, .pun .forminfo, #adminconsole fieldset td, .pun .blockmenu .box, #adstats dd, #brdfooter #modcontrols {
        background-color: transparent;
    }

    .pun .usercontent p, .pun .postmsg p {
        font-size: 1.35rem;
        line-height: 2em;
        padding: 0;
    }

    .pun .postmsg {
        font-family: Lato, Verdana, Arial, Sans-serif;
        font-size: 1.3rem;
        font-weight: normal;
        width: auto;
    }

    .pun .postmsg a {
        color: #4ca6c0;
    }

    .pun .blockpost h2 {
        background-color: transparent;
    }

    .pun .postheadline {
        border-bottom: 1px solid rgba(0, 0, 0, 0.1);
        font-size: 1.25rem;
        line-height: 2.4rem;
        padding: 0 0.2rem;
    }

    .pun .postheadline a {
        color: #4ca6c0;
    }

    .pun .blockpost h2 a:hover, .pun .blockpost h2 a:active {
        color: #e15d2e;
    }

    .pun .blockpost .box, .pun .postright, .pun .postfootright, .pun .deletemsg {
        background-color: transparent;
    }

    .pun .postright {
        padding: 0 0.7rem;
    }

    .pun .postright, .pun .postfootright {
        border-left-color: transparent;
        border-left-width: 18rem;
    }

    .pun .postleft, .pun .postfootleft {
        float: left;
        overflow: hidden;
        position: relative;
        width: 18rem;
    }

    .pun .postleft, .pun .postfootleft, .pun .blockpost label, .pun .codebox, .pun .quotebox {
        background-color: transparent;
        border-right: 1px solid rgba(0, 0, 0, 0.1);
    }

    .pun .postleft a {
        color: #4ca6c0;
    }

    .pun .postleft dl {
        padding: 0 0.2rem;
    }

    .pun .poll {
        background-color: rgba(0, 0, 0, 0.02);
        border: 1px dashed #857b63;
        margin-top: 2em;
        padding: 0.5em 1em;
    }

    .pun .box fieldset {
        background-color: rgba(0, 0, 0, 0.02);
    }

    .pun input[type=submit] {
        background-color: #4ca6c0;
        border: 0;
        cursor: pointer;
        color: #FFF;
        font-size: 1.3rem;
        font-weight: bold;
        outline: 0;
    }

    .pun input[type=submit]:hover {
        background-color: #e15d2e;
        color: #FFF;
    }

    .pun .blockform form, .pun .fakeform {
        padding: 0;
    }

    .pun legend {
        color: #333;
        padding: 0;
        text-indent: 1rem;
    }

    .pun fieldset {
        border-color: rgba(0, 0, 0, 0.1);
        border-style: solid;
        border-width: 1px;
        padding: 0.5rem 1.5rem 1rem 1rem;
    }

    .pun .infldset {
        padding: 0;
    }

    .pun label {
        display: block;
        padding: 0;
    }

    .pun fieldset label {
        padding: 0.3rem 0;
    }

    .pun .linkst, .pun .linksb {
        margin: 0;
    }

    .pun .linkst {
        padding: 0 0.2rem;
    }

    .pun .postfootright ul, .pun .postfootright div, .pun .postfootright p, .pun .postfootleft p {
        line-height: 2.4rem;
        padding: 0 0.2rem;
    }

    .pun .blockform .buttons {
        padding: 0;
    }

    .pun .checklist .checklist-item {
        margin: 0.2rem 0;
        position: relative;
    }

    .pun .checklist .checklist-item label {
        padding: 0 2.4rem;
    }

    select {
        background: none;
        outline: 0;
    }

    .pun .postsignature p {
        line-height: 1.7rem;
    }

    .pun pre, .pun code {
        font-family: consolas, monaco, "bitstream vera sans mono", "courier new", courier, monospace;
        font-size: 1.4rem;
    }

    .pun pre code {
        font-size: inherit;
    }

    .pun .codebox, .pun .quotebox {
        border: 1px solid rgba(0, 0, 0, 0.1);
    }

    .pun .codebox pre code {
        display: block;
        padding: 0.75rem;
    }

    .pun .postlink a {
        background-color: #4ca6c0;
        background-image: none;
        border: 0;
        color: #FFF;
        display: inline-block;
        line-height: 2rem;
        padding: 0 0.6rem;
        text-decoration: none;
    }

    .pun .postlink a:hover {
        background-color: #e15d2e;
        background-image: none;
        color: #FFF;
    }

    #brdmain div.pagepost {
        line-height: 2.4rem;
    }

    .pun .crumbs {
        clear: both;
        line-height: 2.4rem;
        overflow: hidden;
        width: 100%;
    }

    .spoiler_wrap {
        border: 1px solid rgba(0, 0, 0, 0.1) !important;
        background-color: rgba(0, 0, 0, 0.05);
        padding: 0px 5px !important;
    }

    .spoiler_head {
        cursor: pointer;
        color: #4ca6c0;
        font-weight: bold;
    }

    .pun .icon {
        border-width: 3px;
        box-sizing: border-box;
        height: 14px;
        width: 14px;
    }

    .pun .inew .icon {
        border-color: #73C1D7 #2E8DA8 #0F7A98 #A8DDEC;
    }

    .pun .quotebox cite {
        display: block;
        line-height: 3rem;
        padding: 0;
    }

    .pun .postmsg img {
        vertical-align: middle;
    }
}
