css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("smwcentral.net") {
@font-face {
  src: url('https://dl.dropboxusercontent.com/u/221911440/ArchivoNarrow-Regular.woff') format('woff'), url('https://dl.dropboxusercontent.com/u/221911440/ArchivoNarrow-Regular.otf') format('opentype');
  font-family: archivo;
}

@font-face {
  src: url('https://dl.dropboxusercontent.com/u/221911440/BEBAS___.woff') format('woff'), url('https://dl.dropboxusercontent.com/u/221911440/BEBAS___.ttf') format('truetype');
  font-family: bebas;
}
  
body {
  background: radial-gradient(rgba(0,0,0,0), rgba(20,0,0,0.8)), linear-gradient(rgba(10,0,30,0.55), rgba(0,0,0,0.8)), url('https://dl.dropboxusercontent.com/u/221911440/smwccastlefloor.png') bottom left repeat-x, url('https://dl.dropboxusercontent.com/u/221911440/smwcpillarbg1.png') top left repeat-x, url('https://dl.dropboxusercontent.com/u/221911440/smwcpillarbg2.png') top left, url('https://dl.dropboxusercontent.com/u/221911440/smwccurtain.png') top -35px left repeat-x, url('https://dl.dropboxusercontent.com/u/221911440/hills.png') bottom left repeat-x, linear-gradient(rgb(210,110,115), rgb(100,0,100));
  font-family: sans-serif;
}

td.menu {
  background: rgb(92,52,52);
  text-shadow: 0px 1px black, 0px 1px 3px black !important;
  color: rgb(210,210,210);
  font-family: Trebuchet MS, tahoma, droid sans, verdana, sans-serif;
  padding-bottom: 7px !important;
}

td.menu a {
  color: white;
  font-style: normal;
}

td.menu a:hover {
  color: rgb(225,225,225);
}
  
td.menu::before {
  content: "Menu";
  display: block;
  width: 100%;
  margin-bottom: 7px;
  line-height: 26px;
}
  
td.menu div.ex {
  background: rgb(45,45,45);
/*   padding: 3px 7px;
  border-top: 1px solid #405060;
  border-bottom: 1px solid #405060;
  margin-top: 2px;
  margin-bottom: 2px; */
  box-shadow: 0px 1px 0px rgba(255,255,255,0.1), inset 0px 1px 5px black;
}
  
td.menu div.ex::before {
  content: "â€¢ "
}
  
td.title, td.cat, td.menu::before {
  box-shadow: inset 0px 1px 1px white, inset 0px -1px  1px -1px rgba(0,0,0,0.5);
  font-weight: normal; 
  text-align: center;
  text-shadow: 1px 0px black, -1px 0px black, 0px 1px black, 0px -1px black, 0px 2px 3px rgba(0,0,0,0.5)  !important;
/*   font-family: archivo; */
  font-weight: bold;
}
  
td.title, td.menu::before {
  background: linear-gradient(135deg, transparent 32px, rgb(58,101,165) 32px) top 16px left 3px, linear-gradient(315deg, transparent 32px, rgb(58,101,165) 32px) top 16px left 16px, linear-gradient(135deg, transparent 32px, rgb(58,101,165) 32px) top 3px left 16px, linear-gradient(315deg, rgb(90,154,209) 32px, rgb(58,101,165) 32px) top left !important;
  background-size: 32px 32px !important;
  color: white !important;
  height: 26px;
  }

td.cat {
  background: linear-gradient(135deg, transparent 32px, rgb(19,129,19) 32px) top 16px left 3px, linear-gradient(315deg, transparent 32px, rgb(19,129,19) 32px) top 16px left 16px, linear-gradient(135deg, transparent 32px, rgb(19,129,19) 32px) top 3px left 16px, linear-gradient(315deg, rgb(59,172,59) 32px, rgb(19,129,19) 32px) top left !important;
  background-size: 32px 32px !important;
  height: 20px;
  color: white;
}

td.cat a {
  color: rgb(240,240,240);
  text-decoration: none;
}
  
td.head, td.header {
  background: rgb(92,52,52) !important;
  color: white !important;
  box-shadow: inset 0px 1px 1px -1px rgba(255,255,255,0.5) !important;
  font-style: italic;
  text-shadow: 0px -1px black, 0px 1px rgba(255,255,255,0.1) !important;
}

td.normal, td.rope, .cell1, .cell2, .cell3, .col1 {
  background: rgb(55,55,55);
  padding: 3px;
  color: white;
  text-shadow: none;
}
  
td.normal, td.rope {
  border: 1px solid #555;
  border-collapse: collapse;
}

/* td.rope {
  text-align: center;
}
   */
tr > td.cell1:nth-child(1), .cell3 {
  background: rgb(45,45,45);;
}
  
/* td.rope.text.right.nowrap {
  display: none;
}
  
td.normal.text.nowrap, td.normal.text.right.nowrap, td.normal.text.center {
  border-top: 1px solid rgb(80,80,80);
  background: rgb(92,52,52);
  color: white;
  text-shadow: 0px -1px black, 0px 1px rgba(255,255,255,0.1) !important;
  box-shadow: none
}

td.normal.text.center {
  width: 100%;
}
   */
td.space {
  height: 15px;
}

span.stats {
  color: white;
}
  
td.border.line {
  background: rgb(92,52,52);
}

td.news {
  background: #435 !important;
/*   background: rgb(245,245,245) !important;
  color: rgb(50,50,50);
  font-family: Georgia, times new roman, times roman, sans-serif;
  */
  padding: 8px 8px 16px;
}
  
/*post stuff*/
td.postfield {
  background: rgb(55,55,55); 
}
  
td.postsidebar {
  background: rgb(45,45,45);
  text-shadow: 0px 1px black;
  position: relative;
  padding-top: 7px;
}
  
td.postinfo {
  background: rgb(92,52,52);
  color: white;
  box-shadow: none;
  text-align: right;
}
  
td.postsidebar .un {
  background: #000 !important;
  display: block;
  border-radius: 0px !important;
  height: 20px;
  position: absolute;
  top: -2px;
  left: -1px;
  min-width: 150px;
  border-bottom: 1px solid #405060;
  border-top: 1px solid #405060;
  line-height: 20px;
  white-space: nowrap;
}

td.postinfo > .brdr > tbody > tr > td.nowrap:nth-child(2) {
  background: black;
  width: 1px;
  padding: 0px 5px;
  border-radius: 4px;
  color: grey;
}

td.postinfo > .brdr > tbody > tr > td.nowrap:nth-child(1) {
  padding-right: 6px;
}
  
/*remove default borders*/
img[src="/images/tree_bottom_right.gif"], img[src="/images/tree_bottom_left.gif"], img[src="/images/tree_top_right.gif"], img[src="/images/tree_top_left.gif"], img[src="/images/pipe_top_left.gif"], img[src="/images/pipe_bottom_left.gif"],img[src="/images/pipe_top_right.gif"], img[src="/images/pipe_bottom_right.gif"], img[src="/images/menu.gif"], img[src="/images/rope_corner_1.gif"], img[src="/images/rope_corner_2.gif"], img[src="/images/rope_corner_3.gif"], /*img[src="/images/banner.png"],*/ img[src="/images/coin.gif"] {width: 0px; height: 0px;}

  
body > table > tbody > tr > td > table > tbody > tr > td > table > tbody > tr > td, body > table > tbody > tr:nth-child(3) > td:nth-child(1) > table > tbody > tr:nth-child(3) > td {
  background-image: none !important;
  height: 0px;
/*   width: 0px; */
}

body > table > tbody > tr:nth-child(3) > td:nth-child(1) > table > tbody > tr > td {
  background-image: none !important;
  width: 0px;
}

body > table > tbody > tr:nth-child(3) > td:nth-child(1) > table > tbody > tr:nth-child(2) > td, body > table > tbody > tr:nth-child(3) > td:nth-child(1) > table > tbody > tr:nth-child(4) > td {
  width: 2px;
}

body > table > tbody > tr:nth-child(7) > td > table > tbody > tr > td:nth-child(3) > table > tbody > tr:nth-child(2) > td:nth-child(3) {
  width: 3px !important
}
  
td.pad {
  padding: 0px 5px;
}
  
td.pad.menu.text {
  padding: 1px;
}

/*fix margin thing*/
body > table > tbody > tr:nth-child(7) > td > table > tbody > tr > td:nth-child(2) {
  Display: none;
}
  
/*links*/
  
a, a.user {
  color: #DB9;
}

a:hover, a.user:hover {
  color: #FCA;
  text-decoration: none;
}
  
.cell2 a {
  font-weight: bold;
}

td.head.normal.border.center > a {
  color: white;
}
  
/*usernames*/
body > table > tbody > tr:nth-child(7) span.un {
  background: rgb(40,40,40);
  padding: 0px 4px;
  border-radius: 3px;
}
  
/*new*/
.cell1 span.new, .cell1 span.warning {
  font-family: Bebas;
  text-shadow: 1px 1px white, -1px -1px white, 1px -1px white, -1px 1px white, 2px 2px black;
  padding-right: 2px;
}

span.new {
  color: goldenrod;
  font-weight: normal;
}
  
span.warning {
  color: rgb(194,42,42);
}
  
/*select*/
input, select /*, textarea*/ {
  background: white;
  color: black;
  border-radius: 3px;
}
  
select > option {
  background: white;
}
}
