css:
@-moz-document domain("www.umart.com.au") {
/*reset*/
.browse ul li ol li,
.browse ul li ol li h6,
.browse #menu_nav ul#catalog li.on h5 a,
.browse ul li ol li h5,
.browse ul li ol li h6,
.browse ul li ol li h7 {
	width: 201px;
	height: auto;
	text-align: left;
	border: none;
	padding: 0;
	margin: 0;
	line-height: 1;
	float: none !important;
	display: block;
}

#left {
	margin-left: -10px;
	width: 201px;
}

.browse ul#catalog {
	border: none !important;
}

.browse h2 {
	padding: 0;
	text-align: center;
	background: linear-gradient(to top, hsl(26, 98%, 47%), hsl(28, 92%, 54%)) !important
}

.browse ul > li > ol > li > h6 a {
	text-align: center;
	display: block;
	padding: 10px;
	width: auto;
	background: hsl(200,100%,93%) !important;
}

.browse > #menu_nav {
	width: 201px !important;
}

.browse #menu_nav ul#catalog li.on,
.browse ul#catalog li.on ol {
	border: none;
	width: 201px;
}

.browse {
	cursor: default;
}

.browse #menu_nav ul#catalog li.on h5 a {
	display: block;
	text-align: left;
	line-height: 30px;
	width: auto;
	padding: 0 3px 0 6px;
	border-bottom: 1px solid hsl(0,0%,88%);
}

.browse #menu_nav ul#catalog li.on a {
	box-sizing: border-box;
	width: 201px;
}

.li_menu_list_products:hover {
	background: hsl(0,0%,93%);
}

.browse #menu_nav ul#catalog li.on a:hover {
	font-weight: normal;
	color: black;
	text-decoration: none;
}

.browse #menu_nav ul#catalog li.on a .cat-arrow {
	background: none;
	width: 0;
	height: 0;
	position: absolute;
	right: 10px;
	margin-top: 10px;
}

.browse #menu_nav ul#catalog li.on a .cat-arrow:after {
	content: "";
	display: block;
	width: 0px;
	height: 0px;
	border: 5px solid transparent;
	border-left-color: hsla(0,0%,0%,.5);
}

/* pop-out */
.browse ul li ol li h5:hover .liright {
	width: 201px;
}

.browse ul li ol li .liright li, .browse ul li ol li .liright2 li, .browse ul li ol li .liright3 li {
	padding: 0;
	width: auto;
}

.browse ul li ol li h5, .browse ul li ol li h6, .browse ul li ol li h7 {
	width: auto;
}

.browse ul li ol li h5:hover .liright, .browse ul li ol li h6:hover .liright2, .browse ul li ol li h7:hover .liright3 {
	border: 1px solid hsla(0,0%,0%,.12);
	box-shadow: 0 3px 3px 0 hsla(0,0%,0%,.12), 0 6px 6px 0 hsla(0,0%,0%,.12);
}

.selected_menu_list_products > h5 > a,
.selected_menu_list_products > h6 > a,
.selected_menu_list_products > h7 > a {
	color: white !important;
	background: #ED6704  !important;
}
}
