@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.duolingo.com") {

    .page-main {
        box-shadow: 0px 0px 25px rgba(255, 255, 255, 0.04) !important;
    }

    /* page-main and those that follows page-main background */ 
    .page-main,
    .text-to-translate,
    .badge-correct-big,
    .badge-wrong-big,
    #controls.player-footer #grade h2, /* non-Duo selector */
    .box-colored.white,
    .player-container,
    .speech-bubble .inner,
    .speech-bubble .arrow, 
    .carousel-dot,
    .btn-standard.btn-store,
    .translation-sidebar .legend-circle.not-yet-translated,
    .translation-sidebar .legend-circle.needs-checking,
    .translation-sidebar .legend-circle.checked-by-community,
    .translation-sidebar .legend-circle.checked-by-user,
    .white-label,
    .rupee-store-items .wrapper,
    .report-options,
    .tier-badge-grid .tier-wrapper,
    .lingot-bill, 
    .bg-white {
        background-color: rgba(0,0,0,0.9) /* GRP: grpbg2 REP: black */ !important;
    }
    
    .dropdown-menu,
    .hint-popover .inner,
    .hint-popover .inner:before, .hint-popover .inner:after,
    .popover .arrow, 
    .popover,
    .modal {background-color: rgb(0,0,0) !important; 
    }  

    /* front page */
    ul#site-language-modal.double li a:hover {
        background-color: rgb(38, 38, 38); /* rgb(227, 227, 227) */
    }
    .splash-2014 .duo-info, .splash-2014 .mobile {
        background-color: rgb(13, 13, 13) !important; /* #f1f1f1 */
    }
    .splash-2014 .flag-carousel .featured-languages,
    .splash-2014 .website {
        background-color: rgb(0, 0, 0) !important; /* #ffffff */
    }
    #app.wiki-translation #document-omnibox .last-edit .reputation-tier {
        color: white !important;
    }
    .splash-2014 h1.splash {
        color: white !important;
    }
    .splash-2014 .btn-green-light {
        color: white !important;
    }
    .splash-2014 .sign-in-btn {
        color: rgb(28, 175, 246) !important;
        background-color: white !important;
        border-color: white !important;
    }
    .splash-2014 .footer .section-inner h2,
    .splash-2014 .footer .footer-main .nav-footer a,
    .splash-2014 .section-inner .btn.sign-up.bottom-signup-btn {
        color: white !important;
    }
    
    /* text colors */
    body {
        color: #FFFFFF /* GRP: grt1 REP: rgb(204, 204, 204) */ !important; /* rgb(51, 51, 51) */
    }
    h1, h2, h4,
    .gray-dark,
    .user-flair,
    .list-notifications,
    .nav-sidebar a:hover,
    .dropdown-menu > li > a,
    .list-leaderboard-item.you .points,
    .skill-badge-small,
    .sidebar-stats strong,
    .stream-item-comment-list > li,
    .nav-sidebar > li.active a,
    .report-options,
    .skill-progress-headline,
    .skill-progress-headline span,
    #app.wiki-translation .sentence.highlighted,
    #app.wiki-translation .sentence.translation-placeholder[contenteditable="true"],
    #word-sidebar .word-type a,
    #word-sidebar .word-strength a,
    #word-sidebar .top-hint a,
    #word-sidebar .word-skill a,
    div#word-modal #example-sentences tr > td:nth-child(1),
    .wiki-document-body .sentence.translation-placeholder[contenteditable="true"] {
        color: #A6A6A6 /* GRP: grt2 REP: rgb(195, 195, 195) */ !important; /* rgb(60, 60, 60) */
    }

    a,
    h3.gray,
    .gray,
    .discussion-comments-list-item .footer a,
    .discussion-main-detail,
    .list-document-item-category,
    .list-document-item-poster,
    .immersion-notification .title,
    .modal-body .modal-footnote,
    .list-checkable > li,
    .list-checkable .num-documents,
    .list-discussion-item-footer,
    .email-invite-box p,
    .list-leaderboard-item .points,
    .skill-badge-name .lessons-left,
    .sidebar-stats,
    #store-help .intro,
    .btn-outline.btn-standard,
    .rupee-store-items .item-description,
    .profile-header-subline .location,
    .profile-header-bio,
    .list-lesson-item p,
    .tips-notes-panel p,
    .player-quit, 
    .tips-and-notes,
    .speech-bubble .inner,
    .stream-item-content .blurb,
    #avatar-upload,
    #app.wiki-translation .instructions .strengthened-words > li i,
    .tab-switch > li > a,
    .list-lesson-item.locked h3,
    #default-sidebar p,
    div#word-modal #example-sentences td,
    .tips-notes-panel p,
    .tips-notes-panel li,
    .upload-panel select option:first-child {
        color: #666666 /* GRP: grt3 REP: rgb(102, 102, 102) */ !important; /* rgb(153, 153, 153) */
    }
    
    .translation-footer,
    .upload-panel select option {
        color: rgb(191, 191, 191) !important; /* rgb(64, 64, 64) */
    }

    .list-document-item-right {
        color: rgb(70, 70, 70) !important; /* rgb(185, 185, 185) */
    }
    
    .stream-item-time {
        color: rgb(57, 57, 57) !important; /* rgb(198, 198, 198) */
    }
    
    /* translations progress bar */
    .document-progress-bar-big {
        background-color: rgba(255, 255, 255, 0.14) /* GRP: grphigh-4 REP: rgb(36, 36, 36) */ !important; /* rgb(219, 219, 219) */
    }
    .document-progress-bar:after {
        border-color: rgb(24, 24, 24) !important; /* rgb(231, 231, 231) */
        background: linear-gradient(135deg, rgba(0, 0, 0, 0.4) 25%, 
                                    transparent 25%, 
                                    transparent 50%, 
                                    rgba(0, 0, 0, 0.4) 50%, 
                                    rgba(0, 0, 0, 0.4) 75%, 
                                    transparent 75%, 
                                    transparent) 
                    repeat scroll 0% 0% / 30px 30px rgb(2, 16, 45) !important;
    }
    .document-progress-bar .bar-started {
        /* FIXME this does not show up */
        background-color: rgb(41, 41, 41) !important; /* rgb(214, 214, 214) */
        z-index: 5 !important;
    }
    .document-progress-bar .bar {
        background-color: rgb(149, 149, 149) !important; /* rgb(106, 106, 106) */
        z-index: 10 !important;
    }
     
    /* vote count */
    .vote-module-small .vote-count {
        background-color: rgba(255, 255, 255, 0.06) /* GRP: grpvote REP: rgb(15, 15, 15) */ !important; /* rgb(240, 240, 240) */
    }
    .vote-module-small .vote-count.active-upvoted {
        background-color: rgb(132, 215, 59) !important; /* rgb(123, 196, 40) */
        color: black !important;
    }
    .vote-module-small .vote-count.active-downvoted {
        background-color: rgb(251, 31, 15) !important; /* rgb(224, 40, 4) */
        color: black !important;
    }
    
    /* discussion page */
    .discussion-main-body code,
    .discussion-comments-list-item .body code { 
        background-color: rgba(255, 234, 93, 0.2) !important;
    }

    /* restore blue links colors */
    .discussion-comments-list-item header .username,
    .list-notifications a,
    .list-document-item-header a,
    .translator-stats .document-info .show-votes a,
    .list-discussion-item-header a,
    .username,
    .stream-item-content a,
    .nav-tabs > li.active a, 
    .nav-tabs > li:hover a,
    .nav-footer > li a:hover,
    .blue,
    .blue-link,
    .discussion-main-body a,
    #app.wiki-translation .wiki-translation-popover .last-edit a,
    .translation-sidebar .footer > li a,
    .course-page-en .course-contributors a,
    .splash-2014 .duo-info .section-inner .text .mobile-link a,
    .splash-2014 .mobile .section-inner .text .mobile-link a,
    .splash-2014 .duo-info .section-inner .text a,
    .splash-2014 .flag-carousel .featured-languages li a,
    #app.wiki-translation #document-omnibox .last-edit a,
    .suggest-store a {
        color: rgb(28, 175, 246) !important;
    }
    
    /* navigation bar */
    .nav-tabs {
        border-bottom-color: rgba(255, 255, 255, 0.14) /* GRP: grpsepar-3 REP: rgb(34, 34, 34) */ !important; /* rgb(221, 221, 221) */
    }
    .dropdown-menu.arrow-top:after {
        border-bottom-color: #000000 /* GRP: grpbg2 REP: black */ !important;
    }
    .dropdown-menu > .active > a {
        background-color: rgba(255, 255, 255, 0.13) /* GRP: grphigh-7 REP: rgb(32, 32, 32) */ !important; /* rgb(223, 223, 223) */
    }
    
    /* top area notification */
    .persistent-notification,
    .immersion-notification {
        background-color: rgb(12, 12, 12) !important; /* rgb(243, 243, 243) */
        border-color: rgb(24, 24, 24) !important; /* rgb(231, 231, 231) */
    }
    
    /* separator in immersion page, new discussion page, lesson header */
    .list-document-item,
    .discussion-header, 
    .player-header {
        border-bottom-color: rgba(255, 255, 255, 0.05) !important; /* rgb(243, 243, 243) */
    }

    /* popover boxes */
    .popover {
        box-shadow: 0px 2px 15px rgba(255, 255, 255, 0.2) !important;
    }

    #app.wiki-translation .wiki-translation-popover .head {
        background-color: rgba(255, 255, 255, 0.04) /* GRP: grphigh-3 REP: rgb(10, 10, 10) */ !important; /* rgb(245, 245, 245) */
    }
    
    /* line separator in skill tree sidebar, discussion area, 
        notification bar, dropdown menus, store page, hint table, 
        lesson hints, immersion sidebar */
    .sidebar-progress,
    .strenghten-skills-container,
    .discussion-comments-list-item,
    .list-discussion-item,
    .list-notifications > li,
    .dropdown-menu .divider,
    #store-help,
    .hint-table table tr td,
    .more-hints,
    .translation-footer,
    .course-contributors.smaller .contributor .wrapper,
    .discussion-search-results > li {
        border-color: rgba(255, 255, 255, 0.05) /* GRP: grpsepar-1 REP: rgba(12, 12, 12) */ !important; /* rgb(243, 243, 243) */
    }
    .stream-item-comment-list > li {
        border-color: rgb(26, 26, 26) !important; /* rgb(229, 229, 229) */
    }
    .nav-sidebar > li.divider {
        border-color: rgba(255, 255, 255, 0.14) /* GRP: grpsepar-4 REP: rgb(37, 37, 37) */ !important; /* rgb(218, 218, 218) */
    }
    hr,
    #app.wiki-translation .wiki-translation-popover .head,
    #app.wiki-translation .wiki-translation-popover h4.translation,
    .simple-revisions,
    .simple-revisions > li,
    .hint-footer {
        border-color: rgba(255, 255, 255, 0.07) /* GRP: grpsepar-2 REP: rgb(18, 18, 18) */ !important; /* rgb(237, 237, 237) */
    }
    .progress-xp .col-left, 
    .translator-stats .document-info,
    .lingot-bill-items > li,
    .immersion-card .footer,
    #weekly-progress,
    .strengthen-skills-container {
        border-color: rgba(255, 255, 255, 0.13) /* GRP: grptrborder-4 REP: rgb(16, 16, 16) */ !important; /* rgb(239, 239, 239) */
    }
    .translation-sidebar .footer {
        border-color: rgba(255, 255, 255, 0.02) /* GRP: grptrborder-5 REP: rgb(24, 24, 24) */ !important; /* rgb(231, 231, 231) */
    }
    /* restore navigation bar styles */
    .topbar-nav-main > li a,
    .topbar-stats > li > a {
        color: white !important;
    }
    
    /* restore dropdown hover color */
    .dropdown-menu > li > a:hover, 
    .dropdown-menu > li > a:focus, 
    .dropdown-menu > li > a:hover span {
        color: white !important;
    }
    
    /* tooltip */
    .tooltip .tooltip-inner {
        background: rgba(255, 255, 255, 0.8) !important;
        color: black !important;
    }
    
    /* speech bubble in immersion page */
    .popover .arrow {
        background-color: black !important;
        box-shadow: 0px 0px 15px rgba(255, 255, 255, 0.2);
    }
    
    /* translation tier sidebar in immersion page */
    .box-colored {
        box-shadow: 0px 0px 25px rgba(255, 255, 255, 0.04) !important;
    }
    
    /* sidebars in immersion page and discussion page */
    .box-gray {
        background-color: rgba(0,0,0,0.9) /* GRP: grpbg1 REP: #161616 */ !important; /* #e9e9e9 */
        border-color: rgba(255, 255, 255, 0.06) /* GRP: grptrborder-1 (*) REP: rgb(37, 37, 37) */ !important; /* rgb(218, 218, 218) */
    }
    .nav-sidebar > li:hover,
    .nav-sidebar > li.active {
        background-color: rgba(255, 255, 255, 0.14) /* GRP: grphover REP: rgb(37, 37, 37) */ !important; /* rgb(218, 218, 218) */
    }
    
    /* modal dialog help box */
    .modal {
        box-shadow: 0px 2px 15px rgba(255, 255, 255, 0.2) !important;
    }
    
    /* table styles */
    .table th, .table td, .tips-notes-panel table td {
        border-color: rgba(255, 255, 255, 0.10) /* GRP: grphigh-5 REP: rgb(25, 25, 25) */ !important; /* rgb(230, 230, 230) */
    }
    
    .table-striped > tbody > tr:nth-child(2n+1) > td, 
    .table-striped > tbody > tr:nth-child(2n+1) > th {
        background-color: rgba(255, 255, 255, 0.04) /* GRP: grphigh-3 REP: rgb(10, 10, 10) */ !important; /* rgb(245, 245, 245) */
    }
    
    .table-bordered tr td:first-child,
    .table-bordered td {
        border-color: rgb(25, 25, 25) !important; /* rgb(230, 230, 230) */
    }
    /* drop down menu in activity page */
    .dropdown-menu {
        box-shadow: 0px 2px 15px rgba(255, 255, 255, 0.2) !important;
    }

    /* buttons */
    .btn.disabled,
    .btn[disabled],
    fieldset[disabled] .btn {
        background-color: rgba(255, 255, 255, 0.16) /* GRP: grphigh-6 REP: rgb(41, 41, 41) */ !important; /* rgb(214, 214, 214) */
        border-color: transparent /* REP: rgb(41, 41, 41) */ !important; /* rgb(214, 214, 214) */
        color: black !important;
    }
    .btn-primary, 
    .btn-blue, 
    .btn-green, 
    .btn-purple,
    .btn-red,
    .success {
        color: white !important;
    }
    .btn-standard {
        background-color: rgba(255, 255, 255, 0.29) /* GRP: grpbtnbg-2 REP: rgb(73, 73, 73) */ !important; /* rgb(182, 182, 182) */
        border-color: transparent /* REP: rgb(73, 73, 73) */ !important; /* rgb(182, 182, 182) */
        color: black !important;
    }
    .btn-white {
        background-color: #000000 /* GRP: grpbg2 REP: black */ !important;
        border-color: #000000 /* GRP: grpbg2 REP: black */ !important;
        color: rgb(195, 195, 195) !important; /* rgb(60, 60, 60) */
    }
    .btn-outline.btn-standard {
        border-color: rgba(255, 255, 255, 0.14) /* GRP: grpbtnoutline REP: rgb(35, 35, 35) */ !important; /* rgb(220, 220, 220) */
    }
    .btn-outline.btn-standard:hover {
        background-color: rgba(255, 255, 255, 0.05) !important; /* rgba(0, 0, 0, 0.05) */
    }
    .btn-store {
        border-color: rgba(255, 255, 255, 0.11) /* GRP: grptrborder-2 (*) REP: rgb(29, 29, 29) */ !important; /* rgb(226, 226, 226) */
    }
    .player-footer .btn[disabled] {
        color: rgb(27, 27, 27) !important; /* rgb(228, 228, 228) */
        background-color: rgba(255, 255, 255, 0.25) /* GRP: grpbtnbg-1 REP: rgb(63, 63, 63) */ !important; /* rgb(192, 192, 192) */
        border-color: rgba(255, 255, 255, 0) /* GRP: grpbtnbg-1 REP: rgb(63, 63, 63) */ !important; /* rgb(192, 192, 192) */
    }
    .btn-faded.btn-green.btn-outline {
        border-color: rgba(50, 121, 24, 0.5) !important; /* rgb(205, 231, 134) */
        color: rgb(124, 165, 0) !important; /* rgb(124, 165, 0) */
    }
    .btn-faded.btn-red.btn-outline {
        border-color: rgba(64, 16, 8, 0.5) !important; /* rgb(252, 194, 184) */
        color: rgb(221, 56, 29) !important;
    }
    #vkeyboard_content .vkeyboard_key {
        background-color: rgba(255, 255, 255, 0.09) /* GRP: grpvkbbg REP: rgb(23, 23, 23) */ !important; /* rgb(232, 232, 232) */
        color: rgb(180, 180, 180) !important; /* rgb(75, 75, 75) */
    }

    .btn-locked:before {
        /* make button lock image visible */
        opacity: 1 !important;
    }
    .btn-outline.btn-locked {
        color: rgb(45, 45, 45) !important; /* rgb(210, 210, 210) */
        border-color: rgb(30, 30, 30) !important; /* rgb(225, 225, 225) */
    }

    
    /* skill tree / home page */
    .bonus-row {
        background-color: rgba(255, 255, 255, 0.02) /* GRP: grplb REP: rgb(6, 6, 6) */ !important; /* rgb(249, 249, 249) */
        border-color: rgba(255, 255, 255, 0.05) /* GRP: grplb-2 REP: rgb(13, 13, 13) */ !important; /* rgb(242, 242, 242) */
    }
    .skill-icon.skill-icon-placeholder {
        border-color: rgba(255, 255, 255, 0.22) /* GRP: grplb-3 REP: rgb(199, 199, 199) */ !important; /* rgb(199, 199, 199) */
    }

    /* store button in home page */
    .btn-store {
        color: rgb(102, 102, 102) !important; /* rgb(153, 153, 153) */
    }
    
    /* button in strengthen sidebar */
    .btn-outline {
        background: transparent !important;
    }
    .btn-outline.btn-white {
        color: white !important;
        border-color: white !important;
    }
    
    /* skip button */
    #skip_button.btn.btn-outline {
        border-color: rgba(255, 255, 255, 0.2) /* GRP: grpbtnout1 REP: rgb(51, 51, 51) */ !important; /* rgb(204, 204, 204) */
    }
    
    /* email invite box in activity page */
    .email-invite-box {
        background: black !important;
    }
    
    /* activity line of activity page */
    .stream-item,
    .stream-item-type {
        border-color: rgb(23, 23, 23) !important; /* rgb(232, 232, 232) */
    }
    .stream-item:first-child:before,
    .stream-item-type.default {
        background-color: rgb(23, 23, 23) !important; /* rgb(232, 232, 232) */
    }
    .stream-item-type.default {
        border-color: #000000 /* GRP: grpbg2 REP: black */ !important;
    }

    /* discussion tag */
    .sticky-discussion-message {
        background-color: rgba(255, 255, 255, 0.04) /* GRP: grphigh-3 REP: rgb(10, 10, 10) */ !important; /* rgb(245, 245, 245) */
        color: rgb(102, 102, 102) !important; /* rgb(153, 153, 153) */
    }
    
    /* personal stream post discussion area */
    .stream-item-comment-list {
        background-color: rgba(255, 255, 255, 0.07) /* GRP: grphigh-1 REP: rgb(18, 18, 18) */ !important; /* rgb(243, 243, 243) */
    }
    .stream-item-comment-list .stream-item-comment-list {
        background: none !important;
    }
    
    /* input area, textareas */
    input[type="text"],
    input[type="password"],
    .fake-input,
    .textarea-white,
    textarea, 
    .fake-textarea {
        background: #000000 /* GRP: grpbg2 REP: black */ !important;
        border-color: rgba(255, 255, 255, 0.08) /* GRP: grptrborder-3 REP: rgb(20, 20, 20) */ !important; /* rgb(235, 235, 235) */
        color: rgb(180, 180, 180) !important; /* rgb(75, 75, 75) */
        box-shadow: 0px 2px 5px rgba(255, 255, 255, 0.05) inset !important;
    }
    .textarea-translate,
    .input-listen[type="text"],
    .listen-input,
    .one-line-input[type="text"],
    .store-shelf > li {
        border-color: rgba(255, 255, 255, 0.16) /* GRP: grpinput REP: rgb(22, 22, 22) */ !important; /* rgb(233, 233, 233) */
    }
    
    
    /* store boxes */
    .rupee-store-items .wrapper {
        box-shadow: 0px 0px 10px rgba(255, 255, 255, 0.15) !important;
    }
    
    /* hover box in sentence discussion page */
    .hint-popover .inner {
        box-shadow: 0px 0px 15px rgba(255, 255, 255, 0.2) !important;
    }
    .hint-popover .inner:before {
        box-shadow: -2px -2px 15px rgba(255, 255, 255, 0.15) !important; 
    }
    
    /* lesson page */
    .list-lesson-item.locked h3,
    .list-lesson-item p {
        color: rgb(102, 102, 102) !important; /* rgb(153, 153, 153) */
    }
    .list-lesson-item.locked .inner {
       background-color: rgba(8, 8, 8,0.3) !important; /* rgb(247, 247, 247) */
    }
    .list-lesson-item.past .inner {
        box-shadow: 0px 0px 10px rgba(255, 255, 255, 0.15) !important;
    }
    
    .player-container {
        box-shadow: 0px 0px 30px rgba(255, 255, 255, 0.08);
    }
    .speech-bubble .inner {
        box-shadow: 0px 0px 15px rgba(255, 255, 255, 0.2) !important;
    }
    .popover-blue,
    .popover-blue .arrow {
        background-color: rgb(28, 175, 246) !important;
    }
    .speech-bubble .arrow:after, 
    .popover .arrow:after {
        box-shadow: 0px 0px 15px rgba(255, 255, 255, 0.2) !important;
    }
    .player-footer {
        background: rgba(255, 255, 255, 0.14) !important; /* rgb(219, 219, 219) */
    }
    .text-to-translate {
        box-shadow: 0px 0px 8px rgba(255, 255, 255, 0.12) !important;
    }
    .speaker-slow {
        border-color: #000000 /* GRP: grpbg2 REP: black */ !important;
    }
    .blame-popover {
        background: rgba(255, 255, 255, 0.8) !important;
        color: black !important;
    }
    
    .select-images > li {
        background-color: black !important; 
        border-color: black !important;
        box-shadow: 0px 2px 7px rgba(255, 255, 255, 0.15) !important;
    }
    .select-images > li.selected, 
    .select-images > li:hover {
        background-color: rgb(28, 175, 246) !important;
        border-color: rgb(28, 175, 246) !important;
    }
    
    #app.correct .player-footer h1, 
    #app.correct .player-footer h2 {
        color: rgb(118, 156, 0) !important; /* rgb(118, 156, 0) */
    }
    
    #app.incorrect .player-footer h1, 
    #app.incorrect .player-footer h2 {
        color: rgb(251, 31, 15) !important; /* rgb(224, 40, 4) */
    }
    
    #controls.player-footer #grade h2 { /* non-Duo selector */
        color: white !important;
    }
    #app.correct .player-footer {
        background-color: rgba(16, 45, 7, 0.5) !important; /* rgb(222, 240, 165) */
