@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("music.microsoft.com") {
  #leftColumn {
    background-color: #555 !important;
    color: #CCC !important;
  }
  
  .player {
    background-color: #444 !important;
    border-top: 1px solid #333 !important;
  }
  
  .layoutFixTopLeftSection {
    background-color: #555 !important;
    color: #CCC !important;
  }
  
  .searchBox, .searchBoxSecondary {
    border: 2px solid #2a2a2a !important;
    background-color: #333 !important;
    color: #111 !important;
  }
  
  .searchBox input[type="text"], .searchBoxSecondary input[type="text"] {
    background-color: #333 !important;
    color: #111 !important;
  }
  
  .placeholder {
    color: #CCC !important;;
  }
  
   #header {
    background-color: #555 !important;
    color: #CCC !important;
    border-bottom: 1px solid #333 !important;
  }
  
  .layoutFluidRightSection {
    border-left: 1px solid #333 !important;
  }
  
  .list {
    background-color: #444 !important;
  }
  
  #nowPlayingQueue {
    background-color: #444 !important;  
  }
  
  .mediaRow .primaryMetadata, .mediaRowBox .primaryMetadata {
    color: #EEE !important;
  }
  
  .mediaRow .secondaryMetadata, .mediaRowBox .secondaryMetadata {
    color: #CCC !important;
  }
  
  .mediaRow:first-child, .mediaRowBox:first-child {
    border-top: 1px solid #222 !important;
  }
  
  .mediaRow, .mediaRowBox {
    border-bottom: 1px solid #222 !important;
  }
  
  .mediaRow:hover, .mediaRowBox:hover {
    transition: background-color 0.05s ease-in 0s;
    background-color: #222 !important;
  } 
  
  .mediaRow.alternate, .alternate.mediaRowBox {
    background-color: #393939 !important;
  }

  .mediaRow.alternate:hover, .mediaRowBox.alternate:hover {
    transition: background-color 0.05s ease-in 0s;
    background-color: #222 !important;
  } 
}
