css:
@-moz-document domain("leho.howest.be") {
*{
	font-family: Gill Sans MT, Arial, sans-serif !important;
}

body{
	background: #373737;
}

#container #header #menu ul li a, .boxedContent h2{
	font-family: Gill Sans MT, Arial, sans-serif !important;
}
h1, h2, h3, h4, h3 strong, h3 a, .user_course_category_header span{
    font-weight: bold !important;
    font-family: Tw Cen MT Condensed, Arial, sans-serif !important;
}

#logoWrapper {
    padding-top: 20px !important;
}

#header #usermenu {
  position: absolute !important;
  width: 360px !important;
  right: 40px !important;
}
#container #header #menu {
	height: 78px !important;
}
#container #header #menu ul li a {
    padding: 30px 15px !important;
}
#header #breadcrumbs, #header #breadcrumbs span, #header #breadcrumbs a {
	background: #1d1d1d !important;

}
.actions img, img.icon.new_item{
margin: 0 10px 0 0 !important;
position: relative !important;
left: 5px !important;
top: 5px !important;
}
img.icon.new_item{
background: url('http://icons.iconarchive.com/icons/yusuke-kamiyamane/fugue/16/asterisk-yellow-icon.png');
background-position: 0 0;
width: 0;
height: 0;
padding: 8px;
    top: 3px !important;
}
.actions a{
	width: auto !important;
    max-width: 100% !important;
}

table.display tr.firstrow th{
	background: #34414a !important;
}
table.display tr.firstrow:hover th{
	background: #34414a !important;
}

table.display tr.firstrow a:hover{
	background: #34414a !important;
}
tbody tr td, #calendar .fc-today{
	background: #1d1d1d !important;
}
tbody tr.even:hover, tbody tr.odd:hover{
	background: #1d1d1d !important;
}
table.display a:hover{
	background: #34414a !important;
}

#firstcolumn{
 	display: inline;   
}

.dataTables_topbar{
	border-top: 1px solid #34414a !important;
}
.dataTables_bottombar{
	border-top: 1px solid #34414a !important;
}

.ui-widget-header{
 	border-bottom: 1px solid #34414a !important;
    border-top: 1px solid #34414a !important;
	background: #1d1d1d !important;
}

.ui-widget-content{
	background: #34414a  !important;
}
#announcementlist .announcement .announcement_title, #tabs li.ui-state-default{
 	border: 1px solid #34414a !important;
	background: #1d1d1d !important;
}
.ui-state-active{
 	border: 1px solid #34414a !important;
	background: #C2E0FF !important;
}
#tabs li.ui-state-default{
 	border-bottom: 0px !important;
}
.announcement{
	border: 0;
}
#announcementlist .announcement .announcement_content{
    border: 1px solid #34414a !important;
  margin-left: 0;
}
#announcementlist .announcement .announcement_title, #tabs li.ui-state-default{
	border-bottom: 0;
}
#announcementlist #sort_area, .ui-tabs-nav.ui-widget-header, #tabs .ui-widget-content{
    border-bottom: 1px solid #34414a !important;
}
#tabs .ui-widget-content{
    display: inline-block !important;
}
#tabs li.ui-state-active a:hover, #tabs li.ui-state-default a:hover{
	background: #34414a !important;
}
#tabs li.ui-tabs-selected a{
    color: #34414a;
    background: rgba(79, 143, 187, 0.5)
}
#corps {
	background: #373737 !important;
}
#corps #leftmenu li a, #calendar .ui-state-highlight .fc-day-number{
	background-color: #34414a !important;
}
#corps #leftmenu li.active{
	background: url("http://aljotica.eu/ext/subnav_cyan_active.png") no-repeat top right;
}
#corps #leftmenu li a:hover {
	color: #C2E0FF !important;
}

#corps #content h1, .user_course_category_header{
	background: #202020 !important;
}

.boxedContent a:hover, .boxedContent a:visited{
    background: rgba(73,143,187,0.2) !important;
}
#footer-wrap{
	height: 55px !important;
}
#footer a:hover{
    color: #FFF !important;
    text-decoration: underline;
}

#row #label{
    color: #FFF !important;
}

}
