css:
@-moz-document url-prefix("http://www.codeigniter.com/user_guide"), url-prefix("http://www.codeigniter.com/userguide3"), url-prefix("https://www.codeigniter.com/userguide3") {
.wy-menu-vertical li.current {
    background: #1d1d1d!important;
}
.wy-nav-content {
    background: #232323!important;
}
.wy-nav-content {
    color: #c0c0c0!important;
}
.toc-backref {
    color: #c3c3c3!important;
}
.highlight-ci, div[class^='highlight'] {
    background: #131212!important;
}
.admonition {
    background: #101010!important;
}
.wy-menu-vertical li.on a, .wy-menu-vertical li.current>a {
    background: #000!important;
}
.wy-table-odd td, .wy-table-striped tr:nth-child(2n-1) td, .rst-content table.docutils:not(.field-list) tr:nth-child(2n-1) td {
    background: #111!important;
}
.nt, .nx, .o {
    color: #fff!important;
}
.k {
    color: #868EFF!important;
}
div[class^='highlight'] pre {
    color: white !important;
}
.nc {
    color: #15CFFB!important;
}
.rst-content tt {
    color: #fff !important;
    background: #0e0101 !important;
    border: 1px solid black;
}
.rst-content dl:not(.docutils) dt {
    background: #353232 !important;
}
.rst-content dl:not(.docutils) dl dt {
    border-left: 3px #484141 solid;
    color: #DCD7D7 !important;
}
.wy-menu-vertical li.current a:hover {
    background: #0e0808 !important;
}
.admonition-title {
    background: #313B3E !important;
}
.wy-table thead, .rst-content table.docutils thead, .rst-content table.field-list thead {
    color: #fff !important;
}
.rst-content dl:not(.docutils) .optional {
    color: #36FB74 !important;
}
.btn-neutral {
    color: #F1F1F1 !important;
    background-color: #000000 !important;
}
.btn-neutral:hover {
    background-color: #111313 !important;
}
}
