css:
@-moz-document domain("tweetdeck.twitter.com") {
.spinner-small, .column-loading-placeholder .spinner-small {
    display: none;
}

*, a:active {
    outline: 0;
}

.is-wide-columns .app-columns {
    padding: 0;
}

.is-wide-columns .column, .column {
    border: 0;
    margin: 0;
    
    border-left: 1px solid #292939;
}

.avatar {
    border-radius: 0;
}

.social-proof-container, .prf-meta {
	background-color: #445;    
}

.prf-meta {
    border: 0;
}

.prf-stats li+li a {
    border: 0;
}

.prf-stats a, .prf-stats a strong,
.prf-stats a:hover, .prf-stats a:hover strong,
.non-selectable-item {
    color: #eee;
}

.prf .lst-profile a {
    border: 0;
}

.modal-content, .mdl-content {
    background: transparent;
}

.scroll-styled-v::-webkit-scrollbar-thumb, .scroll-styled-h::-webkit-scrollbar-thumb,
.is-inverted-dark .column-scroller::-webkit-scrollbar-thumb {
	border-radius: 0;
    background-color: #292939;
}

.scroll-styled-v::-webkit-scrollbar-track, .scroll-styled-h::-webkit-scrollbar-track,
.is-inverted-dark .column-scroller::-webkit-scrollbar-track {
    border: 0;
}


.stream-item {
    border-bottom: 0;
    transition: all 250ms ease;
}

.stream-item:hover {
    -webkit-box-shadow: 0 0 20px 0px #333342;
	box-shadow: 0 0 20px 0px #333342;
}

html {
    font-family: 'Helvetica Neue', sans-serif;
}

.column, .column-background-fill {
    background-color: #445;
}

.column-header, .column-header-temp, .column-temp .column-header {
    background-color: #333342;
    border-bottom: 0;
    -webkit-box-shadow: 0 0 20px 0px #333342;
	box-shadow: 0 0 20px 0px #333342;
    color: #eee;
}

.app-header, .column-nav-item, .app-navigator, .app-title {
    background-color: #333342;
}

.tweet-detail-wrapper {
    background: transparent;
}
.txt-mute, .txt-mute a:not(:hover):not(:focus),
.is-inverted-dark .txt-mute,
.is-inverted-dark .txt-mute a:not(:hover):not(:focus) {
    color: #eee;
}

.scroll-conversation {
	background: #334;    
}

.tweet-stats, .tweet-detail-actions, .detail-view-inline {
    border: 0;
}

.app-content, .app-columns-container {
	background-color: #252535;    
}

.quoted-tweet {
    background: #292939;
	border: 0;
    color: #eee;
}

.app-search-input, .app-search-fake {
	background-color: #292939;    
}

.column-options {
    background-color: transparent;
}

.facet-type, .facet-type-thumb-size, .accordion-divider-t,m .with-column-divider-bottom {
    border: 0;
}

.column-options .button-tray {
    background-color: #292939;
}

.is-options-open .column-settings-link {
	background-color: #445;    
}

.is-options-open .column-settings-link {
	border: 0;
}

.compose {
	background-color: #334;    
}

.compose-header {
    border-bottom: 0;
}

.compose textarea {
	background-color: #292939;
    border-radius: 0;
    color: #eee;
}

.btn-on-blue, .btn-on-blue:hover, .btn, .btn:hover,
.is-inverted-dark .btn-alt, is-inverted-dark .btn-alt:hover {
    background-color: #292939;
    color: #eee;
    border: 0;
}

.btn-positive, .btn-positive-alt, .btn-fav.s-favorited, .s-following .follow-btn {
	background-color: #292939;    
}

.caret {
    display: none;
}

.dropdown-menu {
	background: #292939;
    border: 0;
    border-radius: 0;
}

.dropdown-menu .typeahead-item, .dropdown-menu [data-action] {
	color: #eee;    
}

.dropdown-menu .is-selected {
	background: #252535;
}

.is-inverted-dark, .is-inverted-dark .account-link {
    color: #eee;
}

.mdl, .mdl-column-med {
    background-color: #252535;
    border-radius: 0;
    border: 0;
}

.mdl-header {
	color: #eee;    
}

.mdl-content {
	border: 0;    
}

.list-account {
    text-shadow: unset;
}

.list-account .fullname, .list-account .username {
    color: #eee;
}

.lst-group .selected {
    background: #445;
}

.lst-group .selected a:hover {
	background: #334;    
}

.list-account:hover:hover {
	background: #334;
}

.is-inverted-dark .link-normal-dark, .is-inverted-dark .link-normal-dark:hover, .is-inverted-dark .link-normal-dark:focus, .is-inverted-dark .link-normal-dark:active, .is-inverted-dark .list-link,
.frm, .list-filter {
	color: #eee;    
}

.is-inverted-dark .scroll-conversation {
    background: #445;
}

.is-inverted-dark .tweet-detail-wrapper {
    background: #445;
}

.tweet-detail-wrapper {
	border: 0;
}

.mdl-col-settings {
    background-color: #445;
}
}
