css:
@-moz-document domain("sales.vivifyhealth.com"), domain("dev.vivifyhealth.com") {
body {
   
    font-size: 14px;
    color: #727272;
    min-height: 100%;
    background-image: none;
    
     /* Gradient- Blue */
     background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
     background: #225fa1;
    background: -moz-radial-gradient(center, ellipse cover, #225fa1 0%, #041b37 100%);
    background: -webkit-radial-gradient(center, ellipse cover, #225fa1 0%, #041b37 100%);
    background: radial-gradient(ellipse at center, #225fa1 0%, #041b37 100%);
    /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#225fa1', endColorstr='#041b37', GradientType=1); */
    
     /* Gradient- Tan */
     background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
     background: #E2E0D6;
    background: -moz-radial-gradient(center, ellipse cover, #FFFFFF 0%, #E2E0D6 100%);
    background: -webkit-radial-gradient(center, ellipse cover, #FFFFFF 0%, #E2E0D6 100%);
    background: radial-gradient(ellipse at center, #FFFFFF 0%, #E2E0D6 100%);
    /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#FFFFFF', endColorstr='#E2E0D6', GradientType=1); */
    
    /* Gradient- Light Gray */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    background: #C1C0C4;
    background: -moz-radial-gradient(center, ellipse cover, #ECECEC 0%, #C1C0C4 100%);
    background: -webkit-radial-gradient(center, ellipse cover, #ECECEC 0%, #C1C0C4 100%);
    background: radial-gradient(ellipse at center, #ECECEC 0%, #C1C0C4 100%);
    /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#ECECEC', endColorstr='#C1C0C4', GradientType=1); */
    
     /* Gradient- Dark Gray */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    background: #C1C0C4;
    background: -moz-radial-gradient(center, ellipse cover, #999999 0%, #424242 100%);
    background: -webkit-radial-gradient(center, ellipse cover, #999999 0%, #424242 100%);
    background: radial-gradient(ellipse at center, #999999 0%, #424242 100%);
    /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#999999', endColorstr='#424242', GradientType=1); */
    
   
      /* Gradient- Blue Gray */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    background: #C1C0C4;
    background: -moz-radial-gradient(center, ellipse cover, #999999 0%, #5D6176 100%);
    background: -webkit-radial-gradient(center, ellipse cover, #999999 0%, #5D6176 100%);
    background: radial-gradient(ellipse at center, #999999 0%, #5D6176 100%);
    /* filter: progid: DXImageTransform.Microsoft.gradient( startColorstr='#999999', endColorstr='#5D6176', GradientType=1); */
    
     /* Texture- Light Gray */
    background-repeat: repeat;
    background-attachment: fixed;
    background-size: auto;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-gray.png') !important;
    
    /* Texture- Dark Gray */
    background-repeat: repeat;
    background-attachment: fixed;
    background-size: auto;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-darkgray.png') !important;
    
     /* Image- Sky */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-sky.jpg') !important;
    
     /* Image- Sky 2 */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-sky-2.jpg') !important;
    
     /* Image- Sky Old */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-sky-old.jpg') !important;
    
        /* Texture- Dark Gray Pattern */
    background-repeat: repeat !important;
    background-attachment: fixed !important;
    background-size: auto !important;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-graypattern.png') !important;
    
        /* Image- Diamonds */
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover !important;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/background-diamonds.jpg') !important;
    

    
}

.box1 {
    background-color: rgba(255,255,255,.7);
    margin-bottom: 3px;
     -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    border-radius: 8px;
    border: none;
}

.patientListMessage, .topcontainer {
    color: #727272 !important;
}

.selectedTab a {
    background-color: #333550 !important;
}

.liquid-bg {
    background-image: url(http://www.vivifyhealth.com/content/themes/caregiver/2016/images/top-white-bg.png);
}
.main {
    background-image: url(http://www.vivifyhealth.com/content/themes/caregiver/2016/images/shadow.png);
}
.main fieldset, .main #formcontainer {
    background-image: none !important;
    background-color: rgba(232,232,232,.6) !important;
   border-color: rgba(232,232,232,.6);
}
.main fieldset {
    /*margin-top: -30px; */
}



#formcontainer {
    -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    border-radius: 8px;
    margin-top:10px;
    margin-bottom:10px;
}
.footer fieldset {
 border: none;   
    font-size: .9em;
    opacity: .5;
    background-color: transparent !important;
}
.main fieldset legend {
 	color: #2A72C0   !important;
        padding-top: 40px !important;
}
.top {
    background-color: #FFF;
}
.container-top {
    margin-top: 5px !important;
}
.main {
    margin-bottom: 75px;
}
.footer {
    background-color: #2C2C2C !important;
}

.rounded-rectangle-2 {
 box-shadow: none !important;   
}

.patient-container, .container {
    -webkit-border-bottom-right-radius: 8px;
    -webkit-border-bottom-left-radius: 8px;
    -moz-border-radius-bottomright: 8px;
    -moz-border-radius-bottomleft: 8px;
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
}

.box-avatar img {
    -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    border-radius: 8px;
}

/* Patient Tabs */
.rtsLevel1 .rtsUL {
    margin-left: 8px !important;
}
.rtsOut, .rtsIn, .rtsLink, .rtsLI {
    margin: 0px !important;
}
.rtsOut, .rtsLink, .rtsLI {
    padding: 0px !important;
}
.rtsLI {
    margin-right: 10px !important;
}
.rtsLevel.rtsLevel2 {
    padding: 0px !important;
}
.rtsLevel.rtsLevel2 .rtsLI {
    margin: 8px !important;
}
.RadTabStrip .rtsLevel {
 width: inherit !important;   
}

/* Search Buttons */
#ctl00_ContentPlaceHolder1_btnSearch {

}

#ctl00_ContentPlaceHolder1_btnSearch:before, #ctl00_btnSearch:before {
    content: "";
   display: block;
	background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/search-dark.png');
     background-size: cover;
    height: 19px;
    width: 20px;
   
}

/* Bug */
body .bug {
    display: block;
    position: absolute;
    bottom: 70px;
    right: 15px;
    height: 255px;
    width: 255px;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: 255px 255px;
    background-image: url('http://www.vivifyhealth.com/content/themes/caregiver/2016/images/vivify-bug-lined.png') !important;
}


/* ANGULAR APP */
.profile-element {
    width: 180px;
    height: 108px;
    background-image: url("http://vivifyhealth.com/content/images/clients/vha/vha-header-logo-vertical.png");
    background-size: cover;
}
.profile-element img {
  display:none;
}
}
